import { Pipe, PipeTransform } from "@angular/core";

@Pipe({name: "ArraySort"})
export class ArraySortPipe implements PipeTransform {

    /**
     * Sorts a given input array either based on the element value, or an optional field
     *
     * @param {any[]} values - Array of input values to sort
     * @param {string} field - Optional field to use in sort function
     * @returns {any[]} - Sorted array
     */
    public transform(values: any[], field?: string): any[] {
        return values.sort((a, b) => this.sortFn(a, b, field));
    }


    private sortFn(a: any, b: any, field?: string): number {
        if (field) {

            if (a[field] < b[field]) {
                return -1;
            } else if (a[field] > b[field]) {
                return 1;
            }
            return 0;
        } else {
            if (a < b) {
                return -1;
            } else if (a > b) {
                return 1;
            }
            return 0;
        }
    }
}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({name: "mapValues"})
export class MapValuesPipe implements PipeTransform {
    public transform(value: Map<any, any>, ...args: any[]): any {
        console.debug("Piping:", value);
        const returnArray: any[] = [];
        value.forEach((entryVal: any, entryKey: any) => {
            console.debug("Pushing:", entryKey, entryVal);
            returnArray.push({
                key: entryKey,
                value: entryVal
            });
        });
        return returnArray;
    }
}

import {Pipe, PipeTransform} from "@angular/core";

@Pipe({name: "rounding"})
export class RoundingPipe implements PipeTransform {

  /**
   * Rounds a number to the specified number of decimal places.
   * Apparently the most accurate method
   * per {@link https://stackoverflow.com/a/43532829/773566 this} link
   * @param {number} value
   * @param {number} digits
   * @returns {number}
   */
  public transform(value: number, digits: number): number {
    let negative = false;
    if (digits === undefined) {
      digits = 0;
    }
    if (value < 0) {
      negative = true;
      value = value * -1;
    }
    const multiplier = Math.pow(10, digits);
    value = parseFloat((value * multiplier).toFixed(11));
    value = Number.parseFloat((Math.round(value) / multiplier).toFixed(2));
    if (negative) {
      value = Number.parseFloat((value * -1).toFixed(2));
    }
    return value;
  }
}

