   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<TrestleIndividual[]>}
   */
  public stIntersectIndividual(dataset: string,
                               wkt: wktValue,
                               validTime?: Moment,
                               dbTime?: Moment,
                               buffer: number = 0): Observable<TrestleIndividual[]> {
    const postBody: IIntersectionBody = {
      dataset,
      buffer,
      geojson: MapService.normalizeToGeoJSON(wkt)
    };

    if (validTime) {
      postBody.validAt = validTime.toISOString();
    }

    if (dbTime) {
      postBody.databaseAt = dbTime.toISOString();
    }

    console.debug('Intersecting individuals with', postBody);

    return this.http.post(this.baseURL + '/individual/intersect-individuals', postBody)
      .pipe(map(MapService.parseResponseToIndividuals));
  }

  /**
   * Perform a spatio-temporal comparison between the input object and the given set of comparison objects
   * @param {ICompareBody} request
   * @returns {Observable<IComparisonReport>}
   */
  public compareIndividuals(request: ICompareBody): Observable<IComparisonReport> {
    return this.http.post<IComparisonReport>(this.baseURL+ '/visualize/compare', request);
  }

  /**
   * Parses an input set of generic objects, by sending them to a web worker to do the interesting stuff
   * @param {object[]} objects
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  private parseToGeoJSONWorker = (objects: object[]): Observable<FeatureCollection<GeometryObject>> => {
    console.debug('Sending to worker');
    const id = new Date().getTime();
    const workerRequest: IMapWorkerRequest = {
      id,
      response: objects
    };

    //    Dispatch the event
    this.worker.postMessage(workerRequest);

    //    Subscribe to the event stream
    return new Observable((observer: Subscriber<FeatureCollection<GeometryObject>>) => {
      this.workerStream
        .pipe(filter(m => m.id === id))
        .subscribe((msg) => {
          console.debug('Has from worker:', msg);
          observer.next(msg.geom);
          observer.complete();
        });
    });
  };

  public static normalizeToGeoJSON(geom: wktValue): Polygon | MultiPolygon {
    if (MapService.isGeometryObject(geom)) {
      if (geom.type === 'Polygon') {
        return (geom as Polygon);
      } else if (geom.type === 'MultiPolygon') {
        return (geom as MultiPolygon);
      } else {
        console.error('Not correct geom', geom);
        throw new Error('Not correct geometry');
      }
    }
    return {
      type: 'Polygon',
      // need to return and array of bounds as an array of SW -> NW -> NE -> SE -> SW
      coordinates: [[geom.getSouthWest().toArray(),
        geom.getNorthWest().toArray(),
        geom.getNorthEast().toArray(),
        geom.getSouthEast().toArray(),
        geom.getSouthWest().toArray()]]
      // crs: {type: "name", properties: {name: "EPSG:4326"}}
    };
  }

  private static parseResponseToIndividuals(res: ITrestleIndividual[]): TrestleIndividual[] {
    console.debug('Intersected result from server:', res);
    return res
      .map((individual: ITrestleIndividual) => new TrestleIndividual(individual));
  }

  private static isGeometryObject(x: any): x is GeometryObject {
    return (x as GeometryObject).type !== undefined;
  }
}

export interface ID3Margin {
  top: number;
  right: number;
  bottom: number;
  left: number;
}

import {AfterViewInit, Component, ElementRef, Input, OnChanges, SimpleChange, ViewChild} from '@angular/core';
import {BaseType, select, Selection} from 'd3-selection';
import {scaleLinear, scaleOrdinal, scaleTime, ScaleTime} from 'd3-scale';
import {curveBasis, line} from 'd3-shape';
import {max, min} from 'd3-array';
import {axisBottom, axisLeft} from 'd3-axis';
import {schemeCategory10} from 'd3';
import {IMetricsData} from './metrics.service';

interface ID3Margin {
  top: number;
  right: number;
  bottom: number;
  left: number;
}

@Component({
  selector: 'metrics-graph',
  templateUrl: './metrics-graph.component.html',
  styleUrls: ['./metrics-graph.component.scss']
})

export class MetricsGraphComponent implements AfterViewInit, OnChanges {
  @ViewChild('container') private element: ElementRef;
  @Input() public data: IMetricsData;
  @Input() public minTime: Date;
  @Input() public maxTime: Date;
  private graphData: IMetricsData[] = [];
  private htmlElement: HTMLElement;
  private host: Selection<HTMLElement, IMetricsData, null, undefined>;
  private svg: Selection<BaseType, IMetricsData, null, undefined>;
  private width: number;
  private height: number;
  private margin: ID3Margin;
  private x: ScaleTime<number, number>;
  private visible: Map<string, boolean> = new Map();

  constructor() {
  }

  public ngAfterViewInit(): void {
    console.debug('Graph view-init');
    this.htmlElement = this.element.nativeElement;
    this.setupD3();

  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    const dataChange = changes['data'];
    if (dataChange != null
      && !dataChange.isFirstChange()
      && (dataChange.currentValue !== dataChange.previousValue)) {
      console.debug('Updated, plotting');
      const currentValue: IMetricsData = changes['data'].currentValue;
      this.graphData.push(currentValue);
      console.debug('Adding as visible:', currentValue.metric);
      this.visible.set(currentValue.metric, true);
      this.plotData();
    }
  }

  /**
   * Get all metrics that aren't currently disabled
   * @returns {string[]}
   */
  public getVisibleMetrics(): string[] {
    const metrics = Array<string>();
    this.visible.forEach((value, key) => {
      if (value) {
        metrics.push(key);
      }
    });
    return metrics;
  }

  private setupD3(): void {
    this.host = select<HTMLElement, IMetricsData>(this.htmlElement);
    this.margin = {top: 20, right: 200, bottom: 20, left: 70};
    this.width = this.htmlElement.offsetWidth - this.margin.left - this.margin.right;
    this.height = 500 - this.margin.top - this.margin.bottom;
    console.debug('Creating D3 graph with width/height', this.width + '/' + this.height);
    this.svg = this.host.html('')
      .append('svg')
      .attr('width', this.width + this.margin.left + this.margin.right)
      .attr('height', this.height + this.margin.top + this.margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + this.margin.left + ',' + this.margin.top + ')');

    // Setup the x axis
    this.x = scaleTime().range([0, this.width]);
    this.x.domain([this.minTime, this.maxTime]);
    this.svg
      .append('g')
      .attr('class', 'axis axis-x')
      .attr('transform', 'translate(0,' + this.height + ')')
      .call(axisBottom(this.x));
    // this.x.domain(extent(this.graphData[0].values, (d: IMetricsValue)=> d.timestamp));
    console.debug('D3 initialized');

  }

  private plotData(): void {

    const y = scaleLinear().range([this.height, 0]);
    const z = scaleOrdinal(schemeCategory10);

    const metricsLine = line()
      .curve(curveBasis)
      .x((d: any) => this.x(d.timestamp))
      .y((d: any) => y(d.value));

    // Build domain values
    y.domain([
      (min(this.graphData,
        (d) => min(d.values,
          (mv) => mv.value) || 0) || 0),
      (max(this.graphData,
        (d) => max(d.values,
          (mv) => mv.value) || 0) || 0)
    ]);

    z.domain(this.graphData.map((d) => d.metric));
    console.debug('Z-domain', z.domain());

    this.svg
      .append('g')
      .attr('class', 'axis axis-y')
      .call(axisLeft(y))
      .append('text')
      .attr('transform', 'rotate(-90)')
      .attr('y', 6)
      .attr('dy', '0.71em')
      .attr('fill', '#000')
      .text('Value');

    const metric = this.svg.selectAll('.metric')
      .data(this.graphData)
      .enter().append('g')
      .attr('class', 'metric');

    metric
      .append('path')
      .attr('class', 'line')
      .attr('id', (d) => d.metric.replace(/\./g, '-'))
      .attr('d', (d: any) => metricsLine(d.values))
      // .attr("data-legend", (d) => d.metric)
      .style('stroke', (d) => z(d.metric));

    //    Add the legend
    const legend = this.svg.selectAll('.legend')
      .data(z.domain())
      .enter()
      .append('g')
      .attr('class', 'legend')
      .attr('id', (d) => 'legend-' + d.replace(/\./g, '-'))
      .attr('transform',
        (d, i) => 'translate('
          + (this.width) + ','
          + (i * ((this.width / 100) * 2) + 15) + ')')
      .on('click', this.legendClickHandler);

    legend
      .append('circle')
      .attr('cx', 30)
      .attr('cy', 30)
      .attr('r', this.width / 150)
      .style('fill', z);

    legend
      .append('text')
      .attr('x', 40)
      .attr('y', 30)
      .attr('dy', '0.25em')
      .style('text-anchor', 'start')
      .text(d => d);

  }

  private legendClickHandler = (d: string): void => {
    console.debug('Clicked', d);
    console.debug('Visible', this.visible);
    const isVisible = this.visible.get(d);
    console.debug('Metric: ' + d + ' is visible?', isVisible);
    if (isVisible) {
      console.debug('Going out');
      this.svg.selectAll('#' + d.replace(/\./g, '-'))
        .transition()
        .duration(1000)
        .style('opacity', 0);

      // Fade the legend
      this.svg.select('#legend-' + d.replace(/\./g, '-'))
        .transition()
        .duration(1000)
        .style('opacity', .2);

      // Fade the y-axis
      this.svg.select('#y-' + d.replace(/\./g, '-'))
        .transition()
        .duration(1000)
        .style('opacity', .2);

      this.visible.set(d, false);
    } else {
      console.debug('Coming back');
      this.svg.selectAll('#' + d.replace(/\./g, '-'))
        // .style("display", "block")
        .transition()
        .duration(1000)
        .style('opacity', 1);

      // Fade the legend
      this.svg.select('#legend-' + d.replace(/\./g, '-'))
        .transition()
        .duration(1000)
        .style('opacity', 1);

      // Fade the y-axis
      this.svg.select('#y-' + d.replace(/\./g, '-'))
        .transition()
        .duration(1000)
        .style('opacity', 1);

      this.visible.set(d, true);
    }
  };
}

import {
  AfterViewInit,
  Component,
  ElementRef,
  Input,
  OnChanges,
  SimpleChange,
  ViewChild,
  ViewEncapsulation
} from "@angular/core";
import {BaseType, select, Selection} from "d3-selection";
import {ScaleOrdinal, scaleOrdinal} from "d3-scale";
import {schemeCategory10} from "d3";
import {
  forceCenter,
  forceLink,
  forceManyBody,
  forceSimulation,
  Simulation,
  SimulationLinkDatum,
  SimulationNodeDatum
} from "d3-force";
import * as moment from "moment";
import {TrestleIndividual} from "../../../shared/individual/TrestleIndividual/trestle-individual";
import {BehaviorSubject} from "rxjs";
import {MatSlideToggleChange} from "@angular/material/slide-toggle";

export interface IIndividualConfig {
  data: TrestleIndividual;
  validAt: moment.Moment;
  dbAt?: moment.Moment;
}

const enum NodeType {
  INDIVIDUAL,
  VTEMPORAL,
  FACT,
  RELATION
}

interface ID3Margin {
  top: number;
  right: number;
  bottom: number;
  left: number;
}

interface IGraphLayout {
  nodes: IFactNode[];
  links: Array<SimulationLinkDatum<IFactNode>>;
}

interface IFactNode extends SimulationNodeDatum {
  id: string;
  name: string;
  valid: boolean;
  group: number;
}

@Component({
  selector: 'individual-graph',
  templateUrl: './individual-graph.component.html',
  styleUrls: ['./individual-graph.component.scss'],
  encapsulation: ViewEncapsulation.None
})

export class IndividualGraphComponent implements AfterViewInit, OnChanges {

  @ViewChild('container') public element: ElementRef;
  @Input() public config: IIndividualConfig;

  public factToggleName = 'fact-toggle';
  public relationToggleName = 'relation-toggle';
  public graphFacts = true;
  public graphRelations = false;

  private htmlElement: HTMLElement;
  private host: Selection<HTMLElement, IFactNode, null, undefined>;
  private svg: Selection<BaseType, IFactNode, null, undefined>;
  private margin: ID3Margin;
  private height: number;
  private width: number;
  private color: ScaleOrdinal<string, string>;
  private layout: IGraphLayout;
  private links: Selection<BaseType, SimulationLinkDatum<IFactNode>, any, any>;
  private nodes: Selection<any, IFactNode, any, any>;
  private simulation: Simulation<IFactNode, any>;
  private nodeSize: number;
  private nodeSizeLarge: number;
  private dataChanges: BehaviorSubject<IIndividualConfig | undefined>;

  constructor() {
    this.dataChanges = new BehaviorSubject(undefined);
  }

  public ngAfterViewInit(): void {
    console.debug('graph view-init');
    this.htmlElement = this.element.nativeElement;
    this.setupD3();
    this.layout = {
      nodes: [],
      links: []
    };
    //    Subscribe
    this.dataChanges
      .subscribe((value) => {
        if (value !== undefined) {
          this.buildGraph(value);
          this.update({
            nodes: [],
            links: [],
          });
          this.update(this.layout);
        }
      });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    const configChange = changes['config'];
    console.debug('Changed', configChange);
    if (configChange.currentValue !== configChange.previousValue) {
      console.debug('Config changed', configChange);
      this.dataChanges.next(configChange.currentValue);
    }
  }

  private setupD3() {
    this.host = select<HTMLElement, IFactNode>(this.htmlElement);
    this.margin = {top: 10, right: 20, bottom: 10, left: 10};
    console.debug('offsetWidth', this.htmlElement.offsetWidth);
    this.width = this.htmlElement.offsetWidth - this.margin.left - this.margin.right;
    this.height = 500 - this.margin.top - this.margin.bottom;
    this.nodeSize = this.width / 75;
    this.nodeSizeLarge = this.width / 50;
    console.debug('Creating D3 graph with width/height', this.width + '/' + this.height);
    this.svg = this.host.html('')
      .append('svg')
      .attr('width', this.width + this.margin.left + this.margin.right)
      .attr('height', this.height + this.margin.top + this.margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + this.margin.left + ',' + this.margin.top + ')');

    this.color = scaleOrdinal(schemeCategory10);
    console.debug('D3 initialized');
  }

  private update(data: IGraphLayout): void {
    console.debug('Data in update function', data);
    const force = forceManyBody();
    force.strength(-1000);
    this.simulation = forceSimulation<IFactNode>()
      .force('link', forceLink().id((d: IFactNode) => d.id))
      .force('charge', force)
      .force('center', forceCenter(this.width / 2, this.height / 2));

    const linkData = this.svg.selectAll('.link')
      .data(data.links, (d: any) => d.source.id + '_' + d.target.id);

    this.links = linkData.enter()
      .append('line')
      .attr('class', 'link');

    const nodeData = this.svg.selectAll('.node')
      .data(data.nodes, (d: IFactNode) => d.id);

    this.nodes = nodeData
      .enter()
      .append('g')
      .attr('class', 'node')
      .on('click', this.nodeClick)
      .on('mouseover', this.nodeMouseOver)
      .on('mouseout', this.nodeMouseOut);

    this.nodes
      .append('circle')
      .attr('r', this.nodeSize)
      .style('fill', (d) => this.color(d.group.toString(10)))
      .style('opacity', (d) => d.valid ? 1.0 : 0.5);

    this.nodes
      .append('text')
      .attr('x', 16)
      .attr('dy', '.35em')
      .text((d) => d.name);

    //    Legend
    const legend = this.svg.selectAll('.legend')
      .data(this.color.domain())
      .enter()
      .append('g')
      .attr('class', 'legend')
      .attr('transform', (d, i) => 'translate(0,' + (i * ((this.nodeSize) * 2) + 20) + ')');

    legend.append('circle')
      .attr('cx', this.width - 18)
      .attr('r', this.nodeSize)
      .attr('cy', this.nodeSize)
      .style('fill', this.color);

    legend
      .append('text')
      .attr('x', this.width - (this.nodeSize) * 2 - 12)
      .attr('y', this.nodeSize)
      .attr('dy', '0.35em')
      .style('text-anchor', 'end')
      .text((d) => IndividualGraphComponent.parseColorGroup(d));
    // Force setup
    this.simulation
      .nodes(data.nodes)
      .on('tick', this.forceTick);

    // For some reason, the links() function doesn't exist on the simulation type, so we do a simple cast to get around it.
    // Seems to work, and the only other option is to lose all type checking for the simulation object
    (this.simulation.force('link') as any).links(data.links);

    linkData.exit().remove();
    nodeData.exit().remove();
  }

  private nodeClick = (d: IFactNode): void => {
    console.debug('Clicked', d);
  };

  private nodeMouseOver = (event: any): void => {
    select(event.currentTarget).select('circle')
      .transition()
      .duration(750)
      .attr('r', this.nodeSizeLarge);
  };

  private nodeMouseOut = (event: any): void => {
    select(event.currentTarget)
      .select('circle')
      .transition()
      .duration(750)
      .attr('r', this.nodeSize);
  };

  private forceTick = (): void => {
    this.nodes
      .attr('transform', (d) => 'translate(' + d.x + ',' + d.y + ')');

    this.links
      .attr('x1', (d: any) => d.source.x)
      .attr('y1', (d: any) => d.source.y)
      .attr('x2', (d: any) => d.target.x)
      .attr('y2', (d: any) => d.target.y);
  };

  private buildGraph(config: IIndividualConfig): void {
    const individual = config.data;
    this.layout = {
      nodes: [],
      links: []
    };

    //    Add the selection as node 0
    const individualNode = {
      id: individual.getID(),
      name: IndividualGraphComponent.parseIndividualID(individual.getID()),
      valid: true,
      group: NodeType.INDIVIDUAL
    };

    const individualTemporal = {
      id: individual.getTemporal().getID(),
      name: 'selection-temporal',
      valid: true,
      group: NodeType.VTEMPORAL
    };

    this.layout.nodes.push(individualNode, individualTemporal);

    this.layout.links.push({
      source: individualNode,
      target: individualTemporal
    });

    if (this.graphFacts) {
      individual.getFacts().forEach(fact => {
        const factNode = {
          id: fact.getID(),
          name: fact.getName(),
          valid: fact.isActive(config.validAt, config.dbAt),
          group: NodeType.FACT
        };
        this.layout.nodes.push(factNode);
        this.layout.links.push({
          source: individualNode,
          target: factNode
        });
      });
    }

    //    Relations
    if (this.graphRelations) {
      individual.getRelations().forEach(relation => {
        const relationNode = {
          id: relation.getObject(),
          name: relation.getType().toString() + ': ' + IndividualGraphComponent.parseIndividualID(relation.getObject()),
          valid: true,
          group: NodeType.RELATION
        };
        this.layout.nodes.push(relationNode);
        this.layout.links.push({
          source: individualNode,
          target: relationNode
        });
      });
    }
  }

  public changeGraphMembers(event: MatSlideToggleChange): void {
    if (event.source.id === this.factToggleName) {
      console.debug('Graph facts?', event.checked);
      this.graphFacts = event.checked;
      this.buildGraph(this.config);
      this.update({
        nodes: [],
        links: [],
      });
      this.update(this.layout);
    } else if (event.source.id === this.relationToggleName) {
      console.debug('Graph relations?', event.checked);
      this.graphRelations = event.checked;
      this.buildGraph(this.config);
      this.update({
        nodes: [],
        links: [],
      });
      this.update(this.layout);
    }
  }

  private static parseColorGroup(group: string): string {
    switch (parseInt(group, 10)) {
      case 0:
        return 'Individual';
      case 1:
        return 'Valid Temporal';
      case 2:
        return 'Fact';
      case 3:
        return 'Relation';
      default:
        return 'unknown';
    }
  }

  private static parseIndividualID(id: string): string {
    const matches = id.match(/(#)(.*)/g);
    if (matches) {
      return matches[0].replace('#', '');
    }
    return id;
  }
}

import {Component, Input} from '@angular/core';
import {ExporterService} from './exporter.service';
import {forkJoin} from 'rxjs';
import {finalize} from 'rxjs/operators';
import {saveAs} from "file-saver";
import {MatSelectChange} from '@angular/material/select';

export interface IDataExport {
  dataset: string;
  individuals: string[];
}

@Component({
  selector: 'data-exporter',
  templateUrl: './exporter.component.html',
  styleUrls: ['./exporter.component.scss']
})
export class ExporterComponent {

  @Input() public dataExport: IDataExport[];
  @Input() public label = true;
  public options: { value: string, viewValue: string }[];
  public selectedValue: string;
  public loading: boolean;

  public constructor(private es: ExporterService) {
    this.options = [
      {value: 'SHAPEFILE', viewValue: 'Shapefile'},
      {value: 'GEOJSON', viewValue: 'GeoJson'},
      {value: 'KML', viewValue: 'KML'},
      {value: 'KMZ', viewValue: 'KMZ'}
      // {value: "TOPOJSON", viewValue: "TopoJSON"}
    ];
    this.selectedValue = this.options[0].value;
    this.loading = false;
  }

  public handleChange(change: MatSelectChange): void {
    this.selectedValue = change.value;
  }

  /**
   * Click handler to export given dataset objects
   */
  public click(): void {
    console.debug('Clicked export', this.dataExport);
    // If the input is undefined, or there are not individuals, skip
    if ((this.dataExport !== undefined)) {
      this.loading = true;

      const exportArray = this.dataExport
        .filter((de) => de.individuals.length > 0)
        .map((de) => {
          return this.es.exportIndividuals({
            dataset: de.dataset,
            individuals: de.individuals,
            type: this.selectedValue
          });
        });
      forkJoin(exportArray)
        .pipe(finalize(() => this.loading = false))
        .subscribe((exports) => {
          exports.forEach((data) => {
            console.debug('exported data:', data);
            let fileName = '';
            switch (this.selectedValue) {
              case 'GEOJSON': {
                fileName = 'trestle.json';
                break;
              }
              case 'KML': {
                fileName = 'trestle.kml';
                break;
              }
              case 'KMZ': {
                fileName = 'trestle.kmz';
                break;
              }
              default: {
                fileName = 'trestle.zip';
                break;
              }
            }
            saveAs(data, fileName);
          });
        });
    }
  }
}

