})
export class ViewerComponent implements OnInit {
  public availableDatasets: IDatasetState[] = [];
  public DatasetState = DatasetState;
  public minTime = moment('1990-01-01');
  public maxTime = moment('2016-01-01');
  public sliderValue = 2013;
  public selectedIndividual: TrestleIndividual;
  public objectHistory: IIndividualHistory;
  public dataChanges: Subject<MapSource>;
  public exportIndividuals: IDataExport[];
  @ViewChild('map')
  public map: TrestleMapComponent;
  private mapBounds: LngLatBounds;

  constructor(private mapService: MapService,
              private is: IndividualService,
              private ds: DatasetService) {
    this.dataChanges = new Subject();
    this.exportIndividuals = [];
  }

  public ngOnInit(): void {
    this.objectHistory = {
      entities: []
    };
    this.ds.getAvailableDatasets()
      .subscribe((results: string[]) => {
        results.forEach((ds) => {
          this.availableDatasets.push({
            name: ds,
            state: DatasetState.UNLOADED
          });
        });
      });
  }

  /**
   * Load new data (or an entirely new dataset)
   * If one's already loaded, unload it, unless we mark the keep flag
   * @param {IDatasetState} dataset - dataset to load
   * @param {boolean} keepLoaded - true if we need to keep the dataset loaded, false to unload it
   */
  public loadDataset(dataset: IDatasetState, keepLoaded = false): void {
    if (dataset.state === DatasetState.LOADED && !keepLoaded) {
      console.debug('Unloading dataset:', dataset.name);
      this.map.removeIndividual('intersection-query');
      dataset.state = DatasetState.UNLOADED;
    } else {
      console.debug('Loading:', dataset.name);
      dataset.state = DatasetState.LOADING;
      this.mapService.stIntersect(dataset.name,
        this.mapBounds, moment()
          .year(this.sliderValue)
          .startOf('year'))
        .subscribe((data) => {
          dataset.state = DatasetState.LOADED;
          console.debug('Data:', data);
          // Get the list of individuals, for exporting
          this.exportIndividuals.push({
            dataset: this.availableDatasets[0].name,
            individuals: (data.features
              .filter((feature) => feature.id)
              // We can do this cast, because we filter to make sure the features have an id
              .map((feature) => feature.id) as string[])
          });
          this.dataChanges.next({
            id: 'intersection-query',
            idField: 'id',
            labelField: 'objectName',
            data
          });
        }, (error) => {
          console.error('Error loading dataset:', error);
          dataset.state = DatasetState.ERROR;
          dataset.error = error;
        });
    }
  }

  /**
   * Update map bounds, and fetch new data, if necessary
   * @param {mapboxgl.LngLatBounds} bounds
   */
  public updateBounds(bounds: LngLatBounds): void {
    console.debug('Moving, updating bounds', bounds);
    // If we haven't loaded any datasets, keep resetting the map bounds
    if (!this.availableDatasets.some((ds) => ds.state === DatasetState.LOADED)) {
      this.mapBounds = bounds;
    }

    // If we've moved outside of the current bounds, get new data
    if (this.needNewData(bounds)) {
      this.mapBounds = bounds;
      this.availableDatasets
        .filter((ds) => ds.state === DatasetState.LOADED)
        .forEach((ds) => this.loadDataset(ds, true));
    }
    // On the first time around, set the map bounds
    if (!this.mapBounds) {
      this.mapBounds = bounds;
    }
  }

  /**
   * Handler to update time slider, and fetch new data
   * @param {MatSliderChange} event
   */
  public sliderChanged = (event: MatSliderChange): void => {
    console.debug('Value changed to:', event);
    if (event.value) {
      this.sliderValue = event.value;
      //    Reload all the currently loaded datasets
      this.availableDatasets
        .filter((ds) => ds.state === DatasetState.LOADED)
        .forEach((ds) => this.loadDataset(ds, true));
    }
  };

  /**
   * Map click handler, which currently fetches the object as a {TrestleIndividual}
   * @param {string} event
   */
  public mapClicked = (event: string): void => {
    console.debug('Clicked:', event);
    this.is.getTrestleIndividual(event)
      .subscribe((data) => {
        console.debug('Has selection', data);
        this.selectedIndividual = data;
      });
  };

  /**
   * Get error from {IDatasetState}
   * @param {IDatasetState} ds
   * @returns {string}
   */
  public getError(ds: IDatasetState): string {
    return ds.error === undefined ? 'Error' : ds.error;
  }

  private needNewData(newBounds: mapboxgl.LngLatBounds) {
    console.debug('Need new data', newBounds, 'old Data', this.mapBounds);
    // This short circuits the checks to avoid loading data on the first go 'round.
    if (newBounds === null || this.mapBounds === undefined) {
      return false;
    }
    // Moved up/down
    if ((newBounds.getNorth() > this.mapBounds.getNorth())
      || (newBounds.getSouth() < this.mapBounds.getSouth())) {
      console.debug(newBounds.getNorth() + ', ' + this.mapBounds.getNorth());
      console.debug(newBounds.getSouth() + ', ' + this.mapBounds.getSouth());
      console.debug('Moved north/south, so true');
      return true;
      //    Moved east/west
    } else if ((newBounds.getEast() > this.mapBounds.getEast())
      || (newBounds.getWest() < this.mapBounds.getWest())) {
      console.debug(newBounds.getEast() + ', ' + this.mapBounds.getEast());
      console.debug(newBounds.getWest() + ', ' + this.mapBounds.getWest());
      console.debug('Moved east/west, so true');
    }
    return false;
  }
}

import {AfterViewInit, Component, ElementRef, Input, OnChanges, SimpleChanges, ViewChild} from "@angular/core";
import {BaseType, select, Selection} from "d3-selection";
import {scaleLinear, scaleOrdinal, scaleTime} from "d3-scale";
import {schemeCategory10} from "d3";
import {axisBottom} from "d3-axis";
import {ID3Margin} from "../common";

export interface IEventElement {
  id: string;
  entity: string;
  temporal: Date;
  bin: number;
  value: any;
  continuing?: boolean;
}

export interface IEventLink {
  source: IEventElement;
  target: IEventElement;
}

export interface IEventData {
  bins: number;
  nodes: IEventElement[];
  links: IEventLink[];
}

@Component({
  selector: 'event-graph',
  templateUrl: './event-graph.component.html',
  styleUrls: ['./event-graph.component.scss']
})
export class EventGraphComponent implements AfterViewInit, OnChanges {
  @ViewChild('graph') public element: ElementRef;
  @ViewChild('tooltip') private tooltipEl: ElementRef;
  @Input() public selectedIndividual: string;
  @Input() public data: IEventData;
  @Input() public graphHeight: number;
  @Input() public graphWidth: number;
  @Input() public minDate: Date;
  @Input() public maxDate: Date;
  @Input() public filterLabel?: (input: IEventElement) => string;
  private htmlElement: HTMLElement;
  private host: Selection<HTMLElement, IEventElement | IEventLink, null, undefined>;
  private svg: Selection<BaseType, IEventElement | IEventLink, null, undefined>;
  private width: number;
  private height: number;
  private margin: ID3Margin;
  private tooltip: any;


  public constructor() {

  }

  public ngAfterViewInit(): void {
    this.htmlElement = this.element.nativeElement;
    this.setupD3();
  }

  public ngOnChanges(changes: SimpleChanges): void {
    const dataChange = changes['data'];
    if (dataChange.currentValue !== undefined
      && (dataChange.currentValue !== dataChange.previousValue)) {
      console.debug('Changed, plotting data', this.data);
      this.plotData();
    }
  }

  private plotData(): void {
    // Nuke everything, because I can't figure out the update pattern
    this.svg.selectAll('*').remove();
    //    Setup the X/Y/Z values
    const entityNames = this.data.nodes.map((d) => d.entity);
    const x = scaleTime()
      .range([120, this.width])
      .domain([this.minDate, this.maxDate]);
    const y = scaleLinear()
      .range([this.height, 0])
      .domain([0, this.data.bins]);
    const z = scaleOrdinal(schemeCategory10)
      .domain(entityNames);

    // Filter down the events, to make sure they don't go off the screen
    this.data.nodes.map((node) => {
      if (node.temporal > this.maxDate) {
        node.continuing = true;
        node.temporal = this.maxDate;
      } else if (node.temporal < this.minDate) {
        node.continuing = true;
        node.temporal = this.minDate;
      }
    });

    // Let's draw a box around the selectHandler selection
    const focusedIndividual = this.svg.selectAll<SVGRectElement, BaseType>('.selected')
      .data(this.data.nodes
          .filter((node) => node.entity === this.selectedIndividual),
        (d: IEventElement) => d.entity);

    // Calculate bin height
    const binHeight = y(1) - y(2);

    focusedIndividual
      .enter()
      .append('rect')
      .attr('class', 'selected')
      .attr('x', 0)
      .attr('y', (d) => y(d.bin + 1) + (binHeight / 2))
      .attr('height', binHeight)
      .attr('width', () => x(this.maxDate) + 120)
      .attr('fill', '#FAFAD2')
      .attr('opacity', '0.7')
      .merge(focusedIndividual);

    focusedIndividual
      .exit()
      .remove();

    //    Add the lines
    const links = this.svg
      .selectAll<SVGLineElement, BaseType>('.link')
      .data(this.data.links, (link: IEventLink) => link.source.id + '_' + link.target.id);

    links
      .enter()
      .append('line')
      .attr('class', 'link')
      .attr('x1', (d) => x(d.source.temporal))
      .attr('y1', (d) => y(d.source.bin) || null)
      .attr('x2', (d) => x(d.target.temporal))
      .attr('y2', (d) => y(d.target.bin) || null)
      .merge(links);

    links
      .exit()
      .remove();

    //    Add the nodes to the graph
    const nodes = this.svg
      .selectAll<SVGCircleElement, BaseType>('.node')
      .data(this.data.nodes, (data: IEventElement) => data.id);

    nodes
      .enter()
      .append('circle')
      .attr('class', 'node')
      .attr('cx', (d) => x(d.temporal))
      .attr('cy', (d) => y(d.bin) || null)
      .attr('r', 9)
      .attr('name', (d) => d.bin)
      .attr('node-id', (d) => d.id)
      .attr('entity', (d) => d.entity)
      .style('fill', (d: IEventElement) => z(d.entity))
      .style('opacity', (d) => d.continuing ? 0.7 : 1.0)
      .merge(nodes);

    nodes
      .exit()
      .remove();

    // Add the labels
    const mainLabels = this.svg.selectAll<SVGTextElement, BaseType>('.entityName')
      .data(this.data.nodes, (d: IEventElement) => d.entity);

    mainLabels
      .enter()
      .append('text')
      .text((d) => this.applyFilter(d))
      .attr('class', 'entityName')
      .attr('x', 0)
      .attr('y', (d) => y(d.bin) || 0)
      .attr('text-anchor', 'start')
      .attr('dy', '.1ex')
      .attr('font-size', '12px')
      .merge(mainLabels);

    mainLabels
      .exit()
      .remove();

    //    Update the X-axis
    const xSelection = this.svg.selectAll('g.x-axis');
    if (xSelection.empty()) {
      this.svg
        .append('g')
        .attr('class', 'axis x-axis')
        .attr('transform', 'translate(0,' + this.height + ')')
        .call(axisBottom(x));
    } else {
      xSelection
        .call(axisBottom(x));
    }
  }

  private setupD3(): void {
    this.host = select<HTMLElement, IEventElement | IEventLink>(this.htmlElement);
    console.debug('Width for event graph:', this.htmlElement.offsetWidth);
    this.margin = {top: 20, right: 30, bottom: 20, left: 20};
    // this.width = this.htmlElement.offsetWidth - this.margin.left - this.margin.right;
    // Calculate width
    // If the element isn't actually on the screen, then the width will be 0 which is wrong
    // So we'll either take it from the input, or just make something up
    if (this.graphWidth === undefined) {
      if (this.htmlElement.offsetWidth == 0) {
        this.width = 800 - this.margin.left - this.margin.right;
      } else {
        this.width = (this.htmlElement.offsetWidth) - this.margin.left - this.margin.right;
      }
    } else {
      this.width = this.graphWidth - this.margin.left - this.margin.right;
    }

    this.height = this.graphHeight - this.margin.top - this.margin.bottom;

    // Add the tooltip
    this.tooltip = select('body')
      .append('div')
      // .style("position", "absolute")
      .style('z-index', '10')
      .style('visibility', 'hidden')
      .style('background', 'steelblue')
      .text('a simple tooltip');

    this.svg = this.host.html('')
      .append('svg')
      .attr('width', this.width + this.margin.left + this.margin.right)
      .attr('height', this.height + this.margin.top + this.margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + this.margin.left + ',' + this.margin.top + ')');

    console.debug('Event Graph Initialized');
  }

  /**
   * Apply the filter function, if it exists, otherwise, return the entity as the label
   * @param {IEventElement} input
   * @returns {string}
   */
  private applyFilter = (input: IEventElement): string => {
    if (this.filterLabel) {
      return this.filterLabel(input);
    } else {
      return input.entity;
    }
  };


}

