import {Injectable} from '@angular/core';
import {LngLatBounds} from 'mapbox-gl';
import {FeatureCollection, GeometryObject, MultiPolygon, Polygon} from 'geojson';
import {Moment} from 'moment';
import {fromEvent, Observable, Subscriber, throwError} from 'rxjs';
import {filter, flatMap, map} from 'rxjs/operators';
import {HttpClient} from '@angular/common/http';
import {environment} from '../../../environments/environment';
import {ITrestleIndividual, TrestleIndividual} from '../../shared/individual/TrestleIndividual/trestle-individual';

export type wktType = 'POINT' |
  'MULTIPOINT' |
  'LINESTRING' |
  'MULTILINESTRING' |
  'POLYGON' |
  'MULTIPOLYGON';

export type wktValue = LngLatBounds | GeometryObject;

export interface IContributionReport {
  object: any;
  area: number;
  contributingParts: IContributionPart[];
}

export interface IComparisonReport {
  union: IContributionReport | null;
  reports: ISpatialComparisonReport[];
}

export interface IContributionPart {
  object: any;
  contribution: number;
}

export interface ISpatialComparisonReport {
  objectAID: string;
  objectBID: string;
  relations: string[];
  equality?: number;
  spatialOverlap?: string;
  spatialOverlapPercentage?: number;
}

interface IIntersectionBody {
  dataset: string;
  geojson: Polygon | MultiPolygon;
  buffer: number;
  validAt?: string;
  databaseAt?: string;
}

interface ICompareBody {
  compare: string;
  compareAgainst: string[];
}

export interface IMapWorkerRequest {
  id: number;
  response: object[];
}

export interface IMapWorkerResponse {
  id: number;
  geom: FeatureCollection<GeometryObject>;
}


@Injectable()
export class MapService {
  private worker: Worker;
  private workerStream: Observable<IMapWorkerResponse>;
  private readonly baseURL;

  constructor(private http: HttpClient) {
    //    Create the worker and register a stream for the results
    this.worker = new Worker("/projector");
    this.workerStream = fromEvent(this.worker, 'message')
      .pipe(map((m: MessageEvent) => (m.data as IMapWorkerResponse)));
    this.baseURL = environment.baseUrl;
  }

  /**
   * Before a spatio-temporal interesction for the given WKT bounding box, returning a GeoJSON Feature Collection
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  public stIntersect(dataset: string,
                     wkt: wktValue,
                     validTime: Moment,
                     dbTime?: Moment,
                     buffer: number = 0): Observable<FeatureCollection<GeometryObject>> {
    console.debug('Intersecting at:', wkt, validTime.toISOString());

    if (wkt === null || wkt === undefined) {
      return throwError('Intersection boundary cannot be empty');
    }

    const postBody: IIntersectionBody = {
      dataset,
      validAt: validTime.toISOString(),
      databaseAt: new Date().toISOString(),
      geojson: MapService.normalizeToGeoJSON(wkt),
      buffer
    };
    console.debug('Post body', postBody);
    return this.http.post(this.baseURL + '/visualize/intersect', postBody)
      .pipe(flatMap(this.parseToGeoJSONWorker))
  }

  /**
   * Performa a spatio-temporal intersection for the given WKT boundary, returning the results as a list of {TrestleIndividual}
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<TrestleIndividual[]>}
   */
  public stIntersectIndividual(dataset: string,
                               wkt: wktValue,
                               validTime?: Moment,
                               dbTime?: Moment,
                               buffer: number = 0): Observable<TrestleIndividual[]> {
    const postBody: IIntersectionBody = {
      dataset,
      buffer,
      geojson: MapService.normalizeToGeoJSON(wkt)
    };

    if (validTime) {
      postBody.validAt = validTime.toISOString();
    }

    if (dbTime) {
      postBody.databaseAt = dbTime.toISOString();
    }

    console.debug('Intersecting individuals with', postBody);

    return this.http.post(this.baseURL + '/individual/intersect-individuals', postBody)
      .pipe(map(MapService.parseResponseToIndividuals));
  }

  /**
   * Perform a spatio-temporal comparison between the input object and the given set of comparison objects
   * @param {ICompareBody} request
   * @returns {Observable<IComparisonReport>}
   */
  public compareIndividuals(request: ICompareBody): Observable<IComparisonReport> {
    return this.http.post<IComparisonReport>(this.baseURL+ '/visualize/compare', request);
  }

  /**
   * Parses an input set of generic objects, by sending them to a web worker to do the interesting stuff
   * @param {object[]} objects
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  private parseToGeoJSONWorker = (objects: object[]): Observable<FeatureCollection<GeometryObject>> => {
    console.debug('Sending to worker');
    const id = new Date().getTime();
    const workerRequest: IMapWorkerRequest = {
      id,
      response: objects
    };

    //    Dispatch the event
    this.worker.postMessage(workerRequest);

    //    Subscribe to the event stream
    return new Observable((observer: Subscriber<FeatureCollection<GeometryObject>>) => {
      this.workerStream
        .pipe(filter(m => m.id === id))
        .subscribe((msg) => {
          console.debug('Has from worker:', msg);
          observer.next(msg.geom);
          observer.complete();
        });
    });
  };

  public static normalizeToGeoJSON(geom: wktValue): Polygon | MultiPolygon {
    if (MapService.isGeometryObject(geom)) {
      if (geom.type === 'Polygon') {
        return (geom as Polygon);
      } else if (geom.type === 'MultiPolygon') {
        return (geom as MultiPolygon);
      } else {
        console.error('Not correct geom', geom);
        throw new Error('Not correct geometry');
      }
    }
    return {
      type: 'Polygon',
      // need to return and array of bounds as an array of SW -> NW -> NE -> SE -> SW
      coordinates: [[geom.getSouthWest().toArray(),
        geom.getNorthWest().toArray(),
        geom.getNorthEast().toArray(),
        geom.getSouthEast().toArray(),
        geom.getSouthWest().toArray()]]
      // crs: {type: "name", properties: {name: "EPSG:4326"}}
    };
  }

  private static parseResponseToIndividuals(res: ITrestleIndividual[]): TrestleIndividual[] {
    console.debug('Intersected result from server:', res);
    return res
      .map((individual: ITrestleIndividual) => new TrestleIndividual(individual));
  }

  private static isGeometryObject(x: any): x is GeometryObject {
    return (x as GeometryObject).type !== undefined;
  }
}

import {AfterViewChecked, AfterViewInit, ChangeDetectorRef, Component, ElementRef, ViewChild} from "@angular/core";
import {ISpatialComparisonReport, MapService} from "../viewer/map.service";
import moment from "moment";
import {interpolateReds} from "d3-scale-chromatic";
import {IDataExport} from "../exporter/exporter.component";
import {parse} from "wellknown";
import {MultiPolygon} from "geojson";
import {ActivatedRoute} from "@angular/router";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import {BehaviorSubject, Subject} from "rxjs";
import {IMapAttributeChange, MapSource, TrestleMapComponent} from "../../ui/trestle-map/trestle-map.component";
import {IndividualService} from "../../shared/individual/individual.service";
import {LoadingSpinnerService} from "../../ui/loading-spinner/loading-spinner.service";
import {ColorService} from "../../shared/color/color.service";
import {TrestleTemporal} from "../../shared/individual/TrestleIndividual/trestle-temporal";
import {MapboxOptions} from "mapbox-gl";
import {MatSliderChange} from "@angular/material/slider";
import {MatSlideToggleChange} from "@angular/material/slide-toggle";

interface ICompareIndividual {
  individual: TrestleIndividual;
  color: string;
  visible: boolean;
  focused: boolean;
  height: number;
  base: number;
  sliderValue: number;
  report?: ISpatialComparisonReport;
}

interface ILoadingState {
  color: loadingColor;
  type: loadingState;
  visible: boolean;
}

export type loadingState = 'determinate' | 'indeterminate';
export type loadingColor = 'accent' | 'warn' | 'primary';

@Component({
  selector: 'compare',
  templateUrl: './compare.component.html',
  styleUrls: ['./compare.component.scss']
})
export class CompareComponent implements AfterViewInit, AfterViewChecked {

  public zoomMap = true;
  public mapConfig: MapboxOptions;
  public selectedIndividuals: Map<string, ICompareIndividual>;
  public baseIndividual: ICompareIndividual | null;
  public dataChanges: BehaviorSubject<MapSource | undefined>;
  public layerChanges: Subject<IMapAttributeChange>;
  public exportValues: IDataExport[];
  public loadedOverlap: ISpatialComparisonReport | null;
  public loading: ILoadingState;
  public currentSliderValue: number;

  private filterCompareResults: boolean;
  private layerDepth: number;
  private maxHeight: number;
  private layerNumber: number;
  @ViewChild(TrestleMapComponent)
  private mapComponent: TrestleMapComponent;
  @ViewChild('loadable')
  private mapRef: ElementRef;
  private disabledFocusIndividuals: string[];

  constructor(private is: IndividualService,
              private vs: MapService,
              private spinner: LoadingSpinnerService,
              private route: ActivatedRoute,
              private cdRef: ChangeDetectorRef,
              private cs: ColorService
  ) {

    this.mapConfig = {
      style: 'mapbox://styles/nrobison/cj3n7if3q000s2sutls5a1ny7',
      center: [32.3558991, -25.6854313],
      zoom: 8,
      pitch: 40,
      bearing: 20,
      container: "map"
    };
    this.layerDepth = 50;
    this.maxHeight = 2016;
    this.selectedIndividuals = new Map();
    // Setup layer coloring
    this.layerNumber = 0;
    this.currentSliderValue = 0;
    this.dataChanges = new BehaviorSubject(undefined);
    this.layerChanges = new Subject();
    this.filterCompareResults = true;
    this.exportValues = [{
      dataset: 'GAUL',
      individuals: []
    }];
    this.loadedOverlap = null;

    this.loading = {
      color: 'primary',
      type: 'indeterminate',
      visible: false
    };
    this.disabledFocusIndividuals = [];
  }

  public ngAfterViewInit(): void {
    // Subscribe to route observables
    this.route.queryParams
      .subscribe((queryParams) => {
        console.debug('params', queryParams);
        const individual = queryParams['id'];
        if (individual !== null) {
          this.addBaseIndividual(individual);
        }
      });

    console.debug('Child', this.mapComponent);
    this.spinner.setViewContainerRef(this.mapRef.nativeElement);
  }

  /**
   * Recheck the view.
   * I'm really not sure why I need this, but SO says so
   * https://stackoverflow.com/questions/43513421/ngif-expression-has-changed-after-it-was-checked
   */
  public ngAfterViewChecked(): void {
    this.cdRef.detectChanges();
  }

  /**
   * Compare the base individual against all the other currently visible objects
   */
  public compareIndividuals(): void {
    // Get all the individuals
    if (this.baseIndividual) {
      this.loading = {
        color: 'accent',
        type: 'indeterminate',
        visible: true
      };
      this.vs.compareIndividuals({
        compare: this.baseIndividual.individual.getID(),
        compareAgainst: Array.from(this.selectedIndividuals.values())
          // Filter out invisible members
          .filter((individual) => individual.visible === true)
          .map((individual) => individual.individual.getID())
      })
        .subscribe((data) => {
          console.debug('Has data from compare', data);
          // Add the comparison reports to each individual,
          // or set them equal to undefined
          data.reports.forEach((report) => {
            this.loading.visible = false;
            const selection = this.selectedIndividuals.get(report.objectBID);
            if (selection) {
              if (selection.visible) {
                selection.report = report;
                //    Change the color to something on the red scale
                if (report.spatialOverlapPercentage) {
                  const interpolated = interpolateReds(
                    report.spatialOverlapPercentage);
                  selection.color = interpolated;
                  this.layerChanges.next({
                    individual: selection.individual.getID(),
                    // Change the color and set the opacity a little higher
                    changes: [
                      {
                        attribute: 'fill-extrusion-color',
                        value: interpolated
                      },
                      {
                        attribute: 'fill-extrusion-opacity',
                        value: 0.85
                      }]
                  });
                } else {
                  // If we don't have any overlap, are we supposed to filter ou those individuals?
                  if (this.filterCompareResults) {
                    this.removeIndividual(selection);
                  }
                }
              } else {
                selection.report = undefined;
              }
            }
          });
        }, (error) => {
          console.debug(error);
          this.loading = {
            color: 'warn',
            type: 'determinate',
            visible: true
          };
        });
    }
  }

  /**
   * Handler function to load the selected individual
   * @param {string} individual
   */
  public selectedHandler(individual: string): void {
    console.debug('Selected:', individual);
    this.loadSelectedIndividual(individual);
  }

  /**
   * Add base individual to compare
   * @param {string} individual
   */
  public addBaseIndividual(individual: string): void {
    this.loadSelectedIndividual(individual, true);
  }

  /**
   * Reset comparison to the base state
   *
   * If a new individual is provided, add it
   * @param {string} individual
   */
  public reset(individual?: string): void {
    //    Clear the map
    //    Remove all the individuals from map
    this.mapComponent.clearMap();
    this.zoomMap = true;
    // this.selectedIndividuals = [];
    this.selectedIndividuals = new Map();
    //    Clear the base selection
    this.baseIndividual = null;
    this.layerNumber = 0;
    this.currentSliderValue = 0;
    this.cs.reset();

    //    Should we add the given individual to the compare?
    if (individual) {
      this.addBaseIndividual(individual);
    }
  }

  /**
   * Toggle the visibility of the given individual
   * @param {ICompareIndividual} individual
   */
  public toggleVisibility(individual: ICompareIndividual): void {
    individual.visible = !individual.visible;
    this.mapComponent
      .toggleIndividualVisibility(individual
          .individual.getID(),
        individual.visible);
  }

  /**
   * Toggle the focus of a given individual, this will hide all other currently visible individuals
   * @param {ICompareIndividual} individual
   */
  public toggleFocus(individual: ICompareIndividual): void {
    individual.focused = !individual.focused;

    // If we've selected the focus option, grab all the currently visible layers, disable them, and stash them for later
    if (individual.focused) {
      // If we have previously disabled individuals, reenable them, then do your thing
      if (!(this.disabledFocusIndividuals.length === 0)) {
        this.disabledFocusIndividuals
          .forEach((fIndividual) => {
            const mapValue = this.selectedIndividuals.get(fIndividual);
            if (mapValue) {
              this.toggleVisibility(mapValue);
            }
          });
        // Also, find any other focused layers, and unfocus them.
        this.selectedIndividuals
          .forEach((value) => {
            if (value !== individual && value.focused) {
              value.focused = false;
            }
          });
        this.disabledFocusIndividuals = [];
      }
      //    Get all the currently visible layers
      this.selectedIndividuals
        .forEach((value, key) => {
          // If the individual is visible, stash it's value so we can enable it later
          if (value !== individual && value.visible) {
            this.disabledFocusIndividuals.push(key);
            this.toggleVisibility(value);
          }
        });
      //    If the individual isn't supposed to be focused, grab all the stashed individuals and reenable them.
    } else {
      this.disabledFocusIndividuals
        .forEach((fIndividual) => {
          const mapValue = this.selectedIndividuals.get(fIndividual);
          if (mapValue) {
            this.toggleVisibility(mapValue);
          }
        });
      //    Reset the focused list
      this.disabledFocusIndividuals = [];
    }
  }

  /**
   * Remove the given individual from the comparison list
   * @param {ICompareIndividual} individual
   */
  public removeIndividual(individual: ICompareIndividual): void {
    console.debug('Remove:', individual);
    // Remove from the array first, then from the map
    this.selectedIndividuals.delete(individual.individual.getID());
    this.mapComponent
      .removeIndividual(individual.individual.getID());

    //    Remove from export
    const idx = this.exportValues[0].individuals.indexOf(individual.individual.getID());
    if (idx > -1) {
      this.exportValues[0].individuals.splice(idx);
    }

    //    Return the color to reuse
    this.cs.returnColor(individual.color);
  }

  /**
   * Update explode slider and change the values on the map
   * @param {MatSliderChange} event
   * @param {ICompareIndividual | null} selection
   */
  public sliderUpdate(event: MatSliderChange, selection = this.baseIndividual) {
    if ((event.value !== null) && (selection !== null)) {
      //     For now, let's just change the base individual,
      // we'll figure out the rest later
      const newOffset = (event.value - selection.sliderValue) * 50;
      this.mapComponent.change3DOffset(selection.height,
        newOffset,
        selection.individual.getID());
      selection.sliderValue = event.value;
      selection.height = selection.height + newOffset;
      selection.base = selection.base + newOffset;
    }
  }

  /**
   * Filter compare results to only return objects which overlap in some way
   * @param {MatSlideToggleChange} event
   */
  public filterChanged(event: MatSlideToggleChange): void {
    this.filterCompareResults = event.checked;
  }

  /**
   * Perform spatial intersection with base individual
   */
  public intersectBaseIndividual(): void {
    if (this.baseIndividual) {
      // this.spinner.reveal();
      this.loading = {
        color: 'accent',
        type: 'indeterminate',
        visible: true
      };
      this.vs
        .stIntersectIndividual('GAUL',
          this.baseIndividual.individual.getSpatialValue(),
          undefined,
          moment(),
          0)
        .subscribe((results) => {
          console.debug('IDs', results.map((indv) => indv.withoutHostname()));
          // If we have results, turn off the loading bar
          this.loading.visible = false;
          results
            .filter((result) => {
              // Filter out the base individual,
              // if it exists, in the grossest way possible
              if (this.baseIndividual !== null) {
                return !(result.getID() === this.baseIndividual.individual.getID());
              }
              return true;
            })
            .forEach((result) => this.addIndividualToCompare(result, false));
          // this.spinner.hide();
        }, (error) => {
          console.error(error);
          this.loading = {
            color: 'warn',
            type: 'determinate',
            visible: true
          };
        });
    }
  };

  /**
   * Get all currently selected individuals
   * @returns {ICompareIndividual[]}
   */
  public getSelectedIndividuals(): ICompareIndividual[] {
    return Array.from(this.selectedIndividuals.values());
  }

  /**
   * Pipe function to enable the view to check for changes
   * @returns {ICompareIndividual[]}
   */
  public get mapValues(): ICompareIndividual[] {
    return Array.from(this.selectedIndividuals.values());
  }

  /**
   * Toggle overlap between the base individual and the selected individual
   * @param {ISpatialComparisonReport} overlap
   */
  public toggleOverlap(overlap: ISpatialComparisonReport): void {
    const id = TrestleIndividual.filterID(overlap.objectAID)
      + '-' + TrestleIndividual.filterID(overlap.objectBID);
    // If we have an overlap, and we haven't loaded it yet
    if (overlap.spatialOverlap) {
      // If the loaded overlap is null, add the new one
      if (this.loadedOverlap === null) {
        // Build the change value
        const changes: MapSource = {
          id,
          data: {
            type: 'Feature',
            // TODO(nickrobison): Gross?
            geometry: (parse(overlap.spatialOverlap) as MultiPolygon),
            properties: null,
            id
          },
          extrude: {
            id: id + '-extrude',
            type: 'fill-extrusion',
            source: id,
            paint: {
              'fill-extrusion-color': 'blue',
              'fill-extrusion-height': 3050,
              'fill-extrusion-base': 3000,
              'fill-extrusion-opacity': 0.7
            }
          }
        };

        //    Turn off all layers except objects A and B that we need
        this.selectedIndividuals.forEach((value) => {
          if (CompareComponent.filterOverlapIndividuals(value, overlap)) {
            this.toggleVisibility(value);
          }
        });

        //    Now, add the new overlap
        this.dataChanges.next(changes);
        this.loadedOverlap = overlap;

        //    If we are the overlap, remove us and turn everything back on
      } else if (this.loadedOverlap === overlap) {
        this.selectedIndividuals.forEach((value) => {
          if (CompareComponent.filterOverlapIndividuals(value, overlap)) {
            this.toggleVisibility(value);
          }
        });
        this.mapComponent.removeIndividual(id);
        this.loadedOverlap = null;
        //    Otherwise, remove the current overlap, and cycle what needs to be toggled
      } else {
        //    Unload the current overlap
        const overlapID = TrestleIndividual.filterID(this.loadedOverlap.objectAID)
          + '-' + TrestleIndividual.filterID(this.loadedOverlap.objectBID);
        this.mapComponent.removeIndividual(overlapID);
        //    Build the change value
        const changes: MapSource = {
          id,
          data: {
            type: 'Feature',
            // TODO(nickrobison): Gross?
            geometry: (parse(overlap.spatialOverlap) as MultiPolygon),
            properties: null,
            id
          },
          extrude: {
            id: id + '-extrude',
            type: 'fill-extrusion',
            source: id,
            paint: {
              'fill-extrusion-color': 'blue',
              'fill-extrusion-height': 4000,
              'fill-extrusion-base': 3000,
              'fill-extrusion-opacity': 0.7
            }
          }
        };

        this.selectedIndividuals.forEach((value) => {
          //    If it's part of the new overlap, and is not visible, turn it on
          if (!CompareComponent.filterOverlapIndividuals(value, overlap)) {
            if (!value.visible) {
              this.toggleVisibility(value);
            }
            //    If it's visible and not part of the new overlap, turn it off
          } else if (value.visible) {
            this.toggleVisibility(value);
          }
        });

        //    Set the new overlap
        this.dataChanges.next(changes);
        this.loadedOverlap = overlap;
      }
    }
  }

  /**
   * Load the selected individual by fetching its value from the database
   * @param {string} individual
   * @param {boolean} baseIndividual
   */
  private loadSelectedIndividual(individual: string, baseIndividual = false): void {
    this.is.getTrestleIndividual(individual)
      .subscribe((result) => this.addIndividualToCompare(result, baseIndividual));
  }

  /**
   * Add individual to the comparison set and the map
   * @param {TrestleIndividual} individual
   * @param {boolean} baseIndividual
   */
  private addIndividualToCompare(individual: TrestleIndividual, baseIndividual = false): void {
    console.debug('Adding individual:', individual);
    // Before we add any individuals to the map, we need to see if we're loading the base individual or not
    // This is to deal with some racy behavior between drawing the individual on the map and moving on from the data load
    // It's gross, but what do you expect?
    if (baseIndividual) {
      console.debug('Setting zoom true');
      this.zoomMap = true;
    } else {
      console.debug('Setting zoom false');
      // If zoom is true, manually run the change detection.
      // Why? no idea
      if (this.zoomMap === true) {
        this.zoomMap = false;
        this.cdRef.detectChanges();
      } else {
        this.zoomMap = false;
      }
    }

    // This is one way to filter out the base individual
    console.debug('Adding %s to map', individual.getFilteredID());
    const color = this.cs.getColor(this.layerNumber);
    const height = this.getHeight(individual.getTemporal());
    const baseHeight = CompareComponent.getBase(individual.getTemporal());
    this.dataChanges.next({
      id: individual.getID(),
      data: {
        type: 'Feature',
        geometry: individual.getSpatialValue(),
        id: individual.getFilteredID(),
        properties: individual.getFactValues()
      },
      extrude: {
        id: individual.getID() + '-extrude',
        type: 'fill-extrusion',
        source: individual.getID(),
        paint: {
          'fill-extrusion-color': color,
          'fill-extrusion-height': height,
          'fill-extrusion-base': baseHeight,
          'fill-extrusion-opacity': 0.7
        }
      },
      labelField: 'adm2_name'
    });

    const compare = {
      individual,
      color,
      visible: true,
      focused: false,
      height,
      base: baseHeight,
      sliderValue: 50
    };

    // Are we loading the base selection, or not?
    if (baseIndividual) {
      // Reset the slider value to 0
      compare.sliderValue = 0;
      this.baseIndividual = compare;
    } else {
      //    Add the selection to the list
      this.selectedIndividuals.set(compare.individual.getID(),
        compare);
      // this.selectedIndividuals.push(compare);
    }
    this.layerNumber++;

    //    Add them to the export record
    this.exportValues[0].individuals.push(compare.individual.getID());
  }

  private getHeight(temporal: TrestleTemporal): number {
    const to = temporal.getTo();
    if (to === undefined) {
      return this.maxHeight;
    } else {
      return to.get('year');
    }
  }

  private static getBase(temporal: TrestleTemporal): number {
    return temporal.getFrom().get('year');
  }

  /**
   * Filter only individuals actually involved in the overlap
   * @param {ICompareIndividual} value
   * @param {ISpatialComparisonReport} overlap
   * @returns {boolean}
   */
  private static filterOverlapIndividuals(value: ICompareIndividual, overlap: ISpatialComparisonReport) {
    const id = value.individual.getID();
    return (id !== overlap.objectAID) &&
      (id !== overlap.objectBID);
  }
}

import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChange} from "@angular/core";
import {Feature, FeatureCollection, GeometryObject} from "geojson";
import {BehaviorSubject, Subject} from "rxjs";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import extent from "@mapbox/geojson-extent";
import {
  FillExtrusionLayer,
  FillLayer,
  FillPaint,
  GeoJSONSource,
  GeoJSONSourceRaw,
  ImageSource,
  LineLayer,
  LngLatBounds,
  Map as MapboxMap,
  MapboxOptions,
  MapMouseEvent,
  RasterSource,
  VectorSource,
  VideoSource
} from "mapbox-gl";

export interface IMapFillLayer extends FillLayer {
  type: 'fill';
}

export interface IMapLineLayer extends LineLayer {
  type: 'line';
}

export interface IMapHoverLayer extends FillLayer {
  type: 'fill';
  filter: ['==', 'name', ''];
}

export interface ITrestleMapLayers {
  fill?: IMapFillLayer;
  line?: IMapLineLayer;
  hover?: IMapHoverLayer;
}

export interface ITrestleMapSource {
  id: string;
  idField?: string;
  data: FeatureCollection<GeometryObject> | Feature<GeometryObject>;
  layers?: ITrestleMapLayers;
  labelField?: string;
  labelFunction?: (label: string) => string;
  labelValue?: string;
}

export interface I3DMapSource extends ITrestleMapSource {
  extrude: FillExtrusionLayer;
}

interface GeoJSONDataSource extends GeoJSONSource {
  _data: Feature<GeometryObject> | FeatureCollection<GeometryObject>;
}

export interface IMapAttributeChange {
  individual: string;
  changes: Array<{ attribute: string, value: any }>;
  // attribute: string;
  // value: any;
}

export interface IMapEventHandler {
  event: MapEvent;
  handler: (event: any) => void;
}

export type MapEvent = 'mousemove' | 'mouseleave' | 'click' | 'moveend';
export type MapSource = I3DMapSource | ITrestleMapSource;
export type MapBoxSource = GeoJSONSource | VectorSource | RasterSource | ImageSource | VideoSource | GeoJSONSourceRaw;

@Component({
  selector: 'trestle-map',
  templateUrl: './trestle-map.component.html',
  styleUrls: ['./trestle-map.component.scss']
})

export class TrestleMapComponent implements OnInit, OnChanges {

  @Input() public data: MapSource;
  @Input() public single: boolean;
  @Input() public multiSelect: boolean;
  @Input() public clickLayerSuffix: string;
  @Input() public zoomOnLoad?: boolean;
  @Input() public config?: MapboxOptions;
  @Input() public dataChanges: Subject<MapSource | undefined>;
  @Input() public attributeChanges: Subject<IMapAttributeChange>;
  @Input() public handlers: IMapEventHandler[];
  @Output() public mapBounds: EventEmitter<LngLatBounds> = new EventEmitter();
  @Output() public clicked: EventEmitter<string> = new EventEmitter();
  private centerMapOnLoad: BehaviorSubject<boolean>;
  private baseConfig: MapboxOptions;
  private map: MapboxMap;
  private mapSources: Map<string, string[]>;
  // This has to be integers, in order to match against the numeric IDs
  private filteredIDs: string[];
  private previousValue: MapSource;

  constructor() {

    this.mapSources = new Map();
    this.filteredIDs = [];

    //    Set defaults
    this.setupDefaults();
  }

  public ngOnInit(): void {
    if (this.zoomOnLoad === undefined) {
      this.centerMapOnLoad = new BehaviorSubject(true);
    } else {
      this.centerMapOnLoad = new BehaviorSubject(this.zoomOnLoad);
    }

    console.debug('Creating map, ' +
      'singleSelect?', this.single,
      'mulitSelect?', this.multiSelect,
      'zoom?', this.centerMapOnLoad.getValue());

    // Merge the map configs together
    const mergedConfig = Object.assign(this.baseConfig, this.config);
    this.map = new MapboxMap(mergedConfig);

    this.map.on('click', this.layerClick);
    this.map.on('mouseover', this.mouseOver);
    this.map.on('mouseleave', this.mouseOut);
    this.map.on('moveend', this.moveHandler);

    // Register any additional handlers
    if (this.handlers) {
      this.handlers.forEach((h) => {
        this.map.on(h.event, h.handler);
      });
    }

    // Once the map is loaded, setup the subscriptions
    this.map.on('style.load', () => {
      // If it's null, create a dummy one
      if (this.dataChanges === undefined) {
        console.debug('Creating dummy data changes subscription');
        this.dataChanges = new Subject();
      }
      console.debug('Subscribing to data changes observable');
      this.dataChanges.subscribe((data) => {
        console.debug('Map has new data to load', data);
        if (data !== undefined) {
          if (this.single && this.previousValue) {
            this.removeSource(this.previousValue);
          }
          this.addSource(data);
          this.previousValue = data;
        }
      });

      if (this.attributeChanges === undefined) {
        console.debug('Creating dummy attribute subscription');
        this.attributeChanges = new Subject();
      }
      console.debug('Subscribing to attribute changes observable');
      this.attributeChanges.subscribe((change) => {
        this.changeIndividualAttribute(change);
      });
      this.mapBounds.emit(this.map.getBounds());
    });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    // Individual changes
    const inputChanges = changes['data'];
    if (inputChanges != null
      && !inputChanges.isFirstChange()
      && (inputChanges.currentValue !== inputChanges.previousValue)) {
      console.debug('New change, updating', inputChanges);
      if (inputChanges.previousValue != null && this.single) {
        // mapChanges.previousValue= inputChanges.previousValue;
        this.removeSource(inputChanges.previousValue);
      }
      // this.dataChanges.next(mapChanges);
      this.addSource(inputChanges.currentValue);
    }

    //    Zoom On Load changes
    const zoomChanges = changes['zoomOnLoad'];
    if (zoomChanges != null
      && !zoomChanges.isFirstChange()) {
      console.debug('Changing zoom value');
      this.centerMapOnLoad.next(zoomChanges.currentValue);
    }

    // Event handlers
    // const handlerChanges = changes["handlers"];
    // if (handlerChanges != null
    //     && (handlerChanges.currentValue !== handlerChanges.previousValue)) {
    //     console.debug("Registering event handlers");
    //     (handlerChanges.currentValue as IMapEventHandler[]).forEach((e) => {
    //         this.map.on(e.event, e.handler);
    //     });
    // }
  }

  /**
   * Get the current map bounds
   * @returns {mapboxgl.LngLatBounds}
   */
  public getMapBounds(): LngLatBounds {
    return this.map.getBounds();
  }

  /**
   * Remove individual from the map, which clears the source and linked layers
   * @param {string} individual
   */
  public removeIndividual(individual: string): void {
    console.debug('Removing selection %s from the map', individual);

    // Is the data a source?
    if (this.mapSources.has(individual)) {
      this.removeSource(individual);
    } else {
      //    Otherwise find the matching layer and remove it
    }
  }

  /**
   * Modify the specified attribute for the given individual
   * @param {IMapAttributeChange} attributeChange
   */
  public changeIndividualAttribute(attributeChange: IMapAttributeChange): void {
    console.debug('Changing attribute:', attributeChange);

    //    Try to get the source first
    const layers = this.mapSources.get(attributeChange.individual);
    if (layers !== undefined) {
      console.debug('Changing layers:', layers);
      layers.filter((layer) => !layer.startsWith('label'))
        .forEach((layer) => {
          attributeChange.changes.forEach((change) => {
            this.map.setPaintProperty(layer, change.attribute, change.value);
          });

        });
    }
    //    I don't think we can do this with individuals yet, but maybe?
  }

  /**
   * Toggle the visibility of the layers for the given individual
   * This changes all registered layers for the given source
   * @param {string} individual
   * @param {boolean} setVisible
   */
  public toggleIndividualVisibility(individual: string, setVisible: boolean): void {
    console.debug('setting visible?', setVisible);
    //    See if the data is a source
    const layers = this.mapSources.get(individual);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          if (setVisible) {
            this.map.setLayoutProperty(layer, 'visibility', 'visible');
          } else {
            this.map.setLayoutProperty(layer, 'visibility', 'none');
          }
        });
      //    If not, figure out which layers have the data
    } else {
      console.debug('Looking for matching individual id:',
        TrestleMapComponent.buildFilterID(individual));
      for (const source of Array.from(this.mapSources.keys())) {
        const mapSource = this.map.getSource(source);
        if (TrestleMapComponent.isGeoJSON(mapSource)) {
          console.debug('Checking source:', mapSource);
          console.debug('Has data:', (mapSource as any)._data);
          const data = mapSource._data;
          // If it's a feature collection, dive into it
          if (TrestleMapComponent.isCollection(data)) {
            for (const feature of data.features) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              if ((feature.properties as any).id === TrestleMapComponent
                .buildFilterID(individual)) {
                console.debug('Source %s matches individual %s',
                  source, individual);
                this.toggleSourceVisibility(source, setVisible, individual);
                break;
              }
            }
          } else {
            // TODO(nickrobison): This will fail if the features don't have an ID property
            if ((data.properties as any).id === TrestleMapComponent
              .buildFilterID(individual)) {
              console.debug('Source feature %s matches individual %s',
                source, individual);
              this.toggleSourceVisibility(source, setVisible, individual);
              break;
            }
          }
        }
      }
    }
  }

  /**
   * Clear all the layers and sources from the map
   */
  public clearMap(): void {
    console.debug('Clearing map');
    this.mapSources.forEach((_, source) => {
      console.debug('removing:', source);
      this.removeSource(source);
    });
  }

  /**
   * Center the map on the given input set, computes the bounding box if one doesn't exist
   * @param {FeatureCollection<GeometryObject> | Feature<GeometryObject>} geom
   */
  public centerMap(geom: FeatureCollection<GeometryObject> | Feature<GeometryObject>): void {
    // We have to lock the map in order to avoid sending out a notice that the move happened.
    if (geom.bbox) {
      // FIXME(nrobison): This is garbage. Fix it.
      this.map.fitBounds(LngLatBounds.convert(geom.bbox as any));
    } else {
      const bbox = extent(geom);
      if (bbox) {
        // This works, but it seems to confuse the type system, so any for the win!
        this.map.fitBounds(LngLatBounds.convert(bbox as any));
      }
    }
  }

  /**
   * Change the map base layer
   * WARNING: This will cause all the sources and layers to be removed and added again, which is slow
   * @param {string} style - New base layer URL
   */
  public setMapStyle(style: string): void {
    this.clearMap();
    this.map.setStyle(style);
  }

  /**
   * Update the map view angle
   * @param {number} pitch
   * @param {number} bearing
   */
  public setPitchBearing(pitch?: number, bearing?: number): void {
    if (pitch) {
      this.map.setPitch(pitch);
    }

    if (bearing) {
      this.map.setBearing(bearing);
    }
  }

  /**
   * Change the 3D offset of the individual, or all sources that are currently at the input height
   * @param {number} height
   * @param {number} offset
   * @param {string} individual
   */
  public change3DOffset(height: number, offset: number, individual?: string): void {

    if (individual) {
      this.mapSources.forEach((layers, key) => {
        // If we have the individual's source, change its layers
        if (key === individual) {
          layers.forEach((layer) => {
            // You can't get properties that don't exist on layers,
            // that's an error not a null, because of course
            // But I'm sure it'll still return a null
            if (!layer.startsWith('label')) {
              const layerHeight = this.map.getPaintProperty(layer,
                'fill-extrusion-height');
              if (layerHeight) {
                this.map.setPaintProperty(layer,
                  'fill-extrusion-height',
                  layerHeight + offset);
                const layerBase = this.map.getPaintProperty(layer,
                  'fill-extrusion-base');
                if (layerBase) {
                  this.map.setPaintProperty(layer,
                    'fill-extrusion-base',
                    layerBase + offset);
                }
              }
            }
          });
        }
      });
    } else {
      //    Find all the individuals that have the same property
      //    For each layer, get its height
      this.mapSources.forEach((layers) => {
        layers.forEach((layer) => {
          const layerHeight = this.map.getPaintProperty(layer,
            'fill-extrusion-height');
          // If it matches the height of the layer, increase it
          if (layerHeight === height) {
            console.debug('Changing individuals');
            const layerBase = this.map.getPaintProperty(layer,
              'fill-extrusion-base');
            if (layerBase) {
              this.map.setPaintProperty(layer,
                'fill-extrusion-base',
                layerBase + offset);
            }
            this.map.setPaintProperty(layer,
              'fill-extrusion-height',
              layerHeight + offset);
          }
        });
      });
    }

  }

  private toggleSourceVisibility(source: string, setVisible: boolean, individual?: string): void {
    const layers = this.mapSources.get(source);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          // If we're filtering a layer and not a source,
          // set a filter to remove the individual
          if (individual) {
            // If we're setting the layer visible again,
            // remove it from the list and update the filter
            const filteredID = TrestleMapComponent.buildFilterID(individual);
            if (setVisible) {
              const idx = this.filteredIDs
                .indexOf(filteredID);
              if (idx > -1) {
                this.filteredIDs.splice(idx, 1);
              }
              //   If we're setting the layer invisible,
              // add the individual to the list of filtered IDs
            } else {
              console.debug('Removing individual %s from layer %s',
                individual, layer);
              this.filteredIDs.push(filteredID);
            }
            // If we have items to filter, add them,
            // otherwise remove the filter
            if (this.filteredIDs.length > 0) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              const filterValues = ['!in', 'id'].concat(this.filteredIDs);

              console.debug('Filtered Features:', this.map.querySourceFeatures(source,
                {
                  sourceLayer: layer,
                  filter: filterValues
                }));
              console.debug('Setting filter of %O on layer:', filterValues, layer);
              this.map.setFilter(layer, filterValues);
            } else {
              console.debug('Removing filter from layer:', layer);
              (this.map as any).setFilter(layer, null);
            }
          } else {
            if (setVisible) {
              this.map.setLayoutProperty(layer, 'visibility', 'visible');
            } else {
              this.map.setLayoutProperty(layer, 'visibility', 'none');
            }
          }
        });
    }

  }

  private removeSource(source: MapSource | string): void {
    let sourceID;
    if (typeof source === 'string') {
      sourceID = source;
    } else {
      sourceID = source.id;
    }

    if (this.mapSources.has(sourceID)) {
      console.debug('Removing source %s from map', sourceID);
      // Remove all the layers for each source
      const layers = this.mapSources.get(sourceID);
      if (layers !== undefined) {
        layers
          .forEach((layer) => {
            this.map.removeLayer(layer);
          });
      }

      this.map.removeSource(sourceID);
      this.mapSources.delete(sourceID);
    }
  }

  private addSource(inputLayer: MapSource): void {
    console.debug('Adding source data:', inputLayer.data);

    // Merge the new source with the default layers
    // But only if we don't already have that layer
    if (this.mapSources.has(inputLayer.id)) {
      console.debug('Map already has source:', inputLayer.id);
      return;
    }

    this.map.addSource(inputLayer.id, {
      type: 'geojson',
      data: inputLayer.data
    });

    const attributeLayers: string[] = [];

    // If it's a 3D layer, add the extrusion, otherwise add the normal layers
    if (TrestleMapComponent.is3D(inputLayer)) {
      console.debug('Adding 3D layer:', inputLayer.extrude);
      this.map.addLayer(inputLayer.extrude);
      attributeLayers.push(inputLayer.extrude.id);
    } else {
      // Add fill layer
      const fillID = inputLayer.id + '-fill';
      this.map.addLayer({
        id: fillID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 0.7,
        } as FillPaint)
      });
      // Add polygon line changes
      const lineId = inputLayer.id + '-line';
      this.map.addLayer({
        id: lineId,
        type: 'line',
        source: inputLayer.id,
        paint: {
          'line-color': 'white',
          'line-width': 2
        }
      });
      // Add hover layer
      const hoverID = inputLayer.id + '-hover';
      this.map.addLayer({
        id: hoverID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 1,
          // Repaint the lines so that they're still visible
        } as FillPaint),
        filter: ['==', 'name', '']
      });
      attributeLayers.push(fillID, lineId, hoverID);
    }

    // Labels
    const labelField = inputLayer.labelField;
    const labelValue = inputLayer.labelValue;
    if (labelField || labelValue) {
      // If it's a collection for each entity, add the label
      const iData = inputLayer.data;
      if (TrestleMapComponent.isCollection(iData)) {
        // Collections only support field labels, since we need to know how to filter each one
        if (labelField == null) {
          throw new Error('Geometry collections only support label Fields, not values');
        }
        iData.features.forEach((feature) => {
          const labelLayerID = 'label-' + feature.id;
          // This is terrible, but so is the web, so who blinks first?
          // If we have a field, use it, otherwise, use the provided value
          const labelText: string = labelField ? (feature.properties as any)[(labelField as any)] : labelValue;
          this.map.addLayer({
            id: labelLayerID,
            type: 'symbol',
            source: inputLayer.id,
            layout: {
              // For the text field, if the label function exists, call it, otherwise just place the label
              'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
              'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
              'text-size': 11,
              'text-transform': 'uppercase',
              'text-letter-spacing': 0.05,
              'text-offset': [0, 1.5],
              'text-allow-overlap': true
              // "text-ignore-placement": true
            },
            paint: {
              'text-color': '#202',
              'text-halo-color': '#fff',
              'text-halo-width': 2
            },
            filter: ['==', labelField, labelText]
          });
          attributeLayers.push(labelLayerID);
        });
      } else {
        const labelLayerID = 'label-' + iData.id;
        // This is terrible, but so is the web, so who blinks first?
        const labelText: string = labelField ? (iData.properties as any)[(labelField as any)] : labelValue;
        this.map.addLayer({
          id: labelLayerID,
          type: 'symbol',
          source: inputLayer.id,
          layout: {
            // For the text field, if the label function exists, call it, otherwise just place the label
            'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
            'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
            'text-size': 11,
            'text-transform': 'uppercase',
            'text-letter-spacing': 0.05,
            'text-offset': [0, 1.5]
          },
          paint: {
            'text-color': '#202',
            'text-halo-color': '#fff',
            'text-halo-width': 2
          }
        });
        attributeLayers.push(labelLayerID);
      }
    }
    // Add the map sources
    this.mapSources.set(inputLayer.id, attributeLayers);

    //    Center map
    if (this.centerMapOnLoad.getValue()) {
      this.centerMap(inputLayer.data);
    }
  }

  private layerClick = (e: MapMouseEvent): void => {
    console.debug('Clicked:', e);
    // FIXME(nrobison): Get rid of this type cast.
    // Get all the fill fillLayers
    let fillLayers: string[] = [];
    this.mapSources.forEach((values) => {
      fillLayers = fillLayers
        .concat((values
          // If we have a clickLayerSuffix, filter on that, otherwise, just find the fill Â¬layers
          .filter((val) => val.includes(this.clickLayerSuffix ? this.clickLayerSuffix : '-fill'))));
    });
    console.debug('Querying on fillLayers:', fillLayers);
    const features: any[] = this.map.queryRenderedFeatures(e.point, {
      layers: fillLayers
    });
    // Set the hover filter using either the provided id field, or a default property
    console.debug('Filtering with data:', this.data);
    // const idField = this.data.idField === undefined ? "id" : this.data.idField;
    const idField = 'id';
    console.debug('Accessing ID field:', idField);

    // If we don't filter on anything, deselect it all
    if (!this.multiSelect && !(features.length > 0)) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((layers) => {
        hoverLayers = hoverLayers
          .concat(layers
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting', hoverLayers);
      hoverLayers.forEach((layer) => {
        this.map.setFilter(layer, ['==', idField, '']);
      });
      return;
    }
    console.debug('Filtered features', features);

    const feature: any = features[0];
    let layerID = features[0].layer.id;
    // Emit the clicked layer
    const featureID = feature.properties[idField];
    this.clicked.emit(featureID);
    layerID = layerID.replace('-fill', '');
    console.debug('Filtering on layer:', layerID + '-hover');
    this.map.setFilter(layerID + '-hover', ['==', idField, featureID]);
    // If multi-select is not enabled, deselect everything else
    if (!this.multiSelect) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((values) => {
        hoverLayers = hoverLayers
          .concat(values
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting:', hoverLayers);
      // Add hover back to the layerID, otherwise nothing will match
      layerID = layerID + '-hover';
      hoverLayers
        .forEach((layer) => {
          if (layer !== layerID) {
            this.map.setFilter(layer, ['==', idField, '']);
          }
        });
    }
  };


  private mouseOver = (e: MapMouseEvent): void => {
    console.debug('Moused over:', e);
  };

  private mouseOut = (e: MapMouseEvent): void => {
    console.debug('Mouse out:', e);
  };

  private moveHandler = () => {
    this.mapBounds.emit(this.map.getBounds());
  };

  private setupDefaults(): void {
    this.baseConfig = {
      container: 'map',
      style: 'mapbox://styles/mapbox/light-v9',
      center: {lng: 32.3558991, lat: -25.6854313},
      zoom: 8,
      accessToken: 'pk.eyJ1IjoibnJvYmlzb24iLCJhIjoiY2ozdDd5dmd2MDA3bTMxcW1kdHZrZ3ppMCJ9.YcJMRphQAfmZ0H8X9HnoKA'
    };
  }

  private static is3D(x: any): x is I3DMapSource {
    return (x as I3DMapSource).extrude !== undefined;
  }

  private static isGeoJSON(x: any): x is GeoJSONDataSource {
    return (x as GeoJSONSource).type === 'geojson';
  }

  private static isCollection(x: any): x is FeatureCollection<GeometryObject> {
    return (x as FeatureCollection<GeometryObject>).type === 'FeatureCollection';
  }

  private static buildFilterID(individual: string): string {
    console.debug('Filtering:', individual);
    return TrestleIndividual.filterID(individual)
      .replace(/-/g, ' ')
      .replace(':', '-');
  }
}

import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChange} from "@angular/core";
import {Feature, FeatureCollection, GeometryObject} from "geojson";
import {BehaviorSubject, Subject} from "rxjs";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import extent from "@mapbox/geojson-extent";
import {
  FillExtrusionLayer,
  FillLayer,
  FillPaint,
  GeoJSONSource,
  GeoJSONSourceRaw,
  ImageSource,
  LineLayer,
  LngLatBounds,
  Map as MapboxMap,
  MapboxOptions,
  MapMouseEvent,
  RasterSource,
  VectorSource,
  VideoSource
} from "mapbox-gl";

export interface IMapFillLayer extends FillLayer {
  type: 'fill';
}

export interface IMapLineLayer extends LineLayer {
  type: 'line';
}

export interface IMapHoverLayer extends FillLayer {
  type: 'fill';
  filter: ['==', 'name', ''];
}

export interface ITrestleMapLayers {
  fill?: IMapFillLayer;
  line?: IMapLineLayer;
  hover?: IMapHoverLayer;
}

export interface ITrestleMapSource {
  id: string;
  idField?: string;
  data: FeatureCollection<GeometryObject> | Feature<GeometryObject>;
  layers?: ITrestleMapLayers;
  labelField?: string;
  labelFunction?: (label: string) => string;
  labelValue?: string;
}

export interface I3DMapSource extends ITrestleMapSource {
  extrude: FillExtrusionLayer;
}

interface GeoJSONDataSource extends GeoJSONSource {
  _data: Feature<GeometryObject> | FeatureCollection<GeometryObject>;
}

export interface IMapAttributeChange {
  individual: string;
  changes: Array<{ attribute: string, value: any }>;
  // attribute: string;
  // value: any;
}

export interface IMapEventHandler {
  event: MapEvent;
  handler: (event: any) => void;
}

export type MapEvent = 'mousemove' | 'mouseleave' | 'click' | 'moveend';
export type MapSource = I3DMapSource | ITrestleMapSource;
export type MapBoxSource = GeoJSONSource | VectorSource | RasterSource | ImageSource | VideoSource | GeoJSONSourceRaw;

@Component({
  selector: 'trestle-map',
  templateUrl: './trestle-map.component.html',
  styleUrls: ['./trestle-map.component.scss']
})

export class TrestleMapComponent implements OnInit, OnChanges {

  @Input() public data: MapSource;
  @Input() public single: boolean;
  @Input() public multiSelect: boolean;
  @Input() public clickLayerSuffix: string;
  @Input() public zoomOnLoad?: boolean;
  @Input() public config?: MapboxOptions;
  @Input() public dataChanges: Subject<MapSource | undefined>;
  @Input() public attributeChanges: Subject<IMapAttributeChange>;
  @Input() public handlers: IMapEventHandler[];
  @Output() public mapBounds: EventEmitter<LngLatBounds> = new EventEmitter();
  @Output() public clicked: EventEmitter<string> = new EventEmitter();
  private centerMapOnLoad: BehaviorSubject<boolean>;
  private baseConfig: MapboxOptions;
  private map: MapboxMap;
  private mapSources: Map<string, string[]>;
  // This has to be integers, in order to match against the numeric IDs
  private filteredIDs: string[];
  private previousValue: MapSource;

  constructor() {

    this.mapSources = new Map();
    this.filteredIDs = [];

    //    Set defaults
    this.setupDefaults();
  }

  public ngOnInit(): void {
    if (this.zoomOnLoad === undefined) {
      this.centerMapOnLoad = new BehaviorSubject(true);
    } else {
      this.centerMapOnLoad = new BehaviorSubject(this.zoomOnLoad);
    }

    console.debug('Creating map, ' +
      'singleSelect?', this.single,
      'mulitSelect?', this.multiSelect,
      'zoom?', this.centerMapOnLoad.getValue());

    // Merge the map configs together
    const mergedConfig = Object.assign(this.baseConfig, this.config);
    this.map = new MapboxMap(mergedConfig);

    this.map.on('click', this.layerClick);
    this.map.on('mouseover', this.mouseOver);
    this.map.on('mouseleave', this.mouseOut);
    this.map.on('moveend', this.moveHandler);

    // Register any additional handlers
    if (this.handlers) {
      this.handlers.forEach((h) => {
        this.map.on(h.event, h.handler);
      });
    }

    // Once the map is loaded, setup the subscriptions
    this.map.on('style.load', () => {
      // If it's null, create a dummy one
      if (this.dataChanges === undefined) {
        console.debug('Creating dummy data changes subscription');
        this.dataChanges = new Subject();
      }
      console.debug('Subscribing to data changes observable');
      this.dataChanges.subscribe((data) => {
        console.debug('Map has new data to load', data);
        if (data !== undefined) {
          if (this.single && this.previousValue) {
            this.removeSource(this.previousValue);
          }
          this.addSource(data);
          this.previousValue = data;
        }
      });

      if (this.attributeChanges === undefined) {
        console.debug('Creating dummy attribute subscription');
        this.attributeChanges = new Subject();
      }
      console.debug('Subscribing to attribute changes observable');
      this.attributeChanges.subscribe((change) => {
        this.changeIndividualAttribute(change);
      });
      this.mapBounds.emit(this.map.getBounds());
    });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    // Individual changes
    const inputChanges = changes['data'];
    if (inputChanges != null
      && !inputChanges.isFirstChange()
      && (inputChanges.currentValue !== inputChanges.previousValue)) {
      console.debug('New change, updating', inputChanges);
      if (inputChanges.previousValue != null && this.single) {
        // mapChanges.previousValue= inputChanges.previousValue;
        this.removeSource(inputChanges.previousValue);
      }
      // this.dataChanges.next(mapChanges);
      this.addSource(inputChanges.currentValue);
    }

    //    Zoom On Load changes
    const zoomChanges = changes['zoomOnLoad'];
    if (zoomChanges != null
      && !zoomChanges.isFirstChange()) {
      console.debug('Changing zoom value');
      this.centerMapOnLoad.next(zoomChanges.currentValue);
    }

    // Event handlers
    // const handlerChanges = changes["handlers"];
    // if (handlerChanges != null
    //     && (handlerChanges.currentValue !== handlerChanges.previousValue)) {
    //     console.debug("Registering event handlers");
    //     (handlerChanges.currentValue as IMapEventHandler[]).forEach((e) => {
    //         this.map.on(e.event, e.handler);
    //     });
    // }
  }

  /**
   * Get the current map bounds
   * @returns {mapboxgl.LngLatBounds}
   */
  public getMapBounds(): LngLatBounds {
    return this.map.getBounds();
  }

  /**
   * Remove individual from the map, which clears the source and linked layers
   * @param {string} individual
   */
  public removeIndividual(individual: string): void {
    console.debug('Removing selection %s from the map', individual);

    // Is the data a source?
    if (this.mapSources.has(individual)) {
      this.removeSource(individual);
    } else {
      //    Otherwise find the matching layer and remove it
    }
  }

  /**
   * Modify the specified attribute for the given individual
   * @param {IMapAttributeChange} attributeChange
   */
  public changeIndividualAttribute(attributeChange: IMapAttributeChange): void {
    console.debug('Changing attribute:', attributeChange);

    //    Try to get the source first
    const layers = this.mapSources.get(attributeChange.individual);
    if (layers !== undefined) {
      console.debug('Changing layers:', layers);
      layers.filter((layer) => !layer.startsWith('label'))
        .forEach((layer) => {
          attributeChange.changes.forEach((change) => {
            this.map.setPaintProperty(layer, change.attribute, change.value);
          });

        });
    }
    //    I don't think we can do this with individuals yet, but maybe?
  }

  /**
   * Toggle the visibility of the layers for the given individual
   * This changes all registered layers for the given source
   * @param {string} individual
   * @param {boolean} setVisible
   */
  public toggleIndividualVisibility(individual: string, setVisible: boolean): void {
    console.debug('setting visible?', setVisible);
    //    See if the data is a source
    const layers = this.mapSources.get(individual);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          if (setVisible) {
            this.map.setLayoutProperty(layer, 'visibility', 'visible');
          } else {
            this.map.setLayoutProperty(layer, 'visibility', 'none');
          }
        });
      //    If not, figure out which layers have the data
    } else {
      console.debug('Looking for matching individual id:',
        TrestleMapComponent.buildFilterID(individual));
      for (const source of Array.from(this.mapSources.keys())) {
        const mapSource = this.map.getSource(source);
        if (TrestleMapComponent.isGeoJSON(mapSource)) {
          console.debug('Checking source:', mapSource);
          console.debug('Has data:', (mapSource as any)._data);
          const data = mapSource._data;
          // If it's a feature collection, dive into it
          if (TrestleMapComponent.isCollection(data)) {
            for (const feature of data.features) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              if ((feature.properties as any).id === TrestleMapComponent
                .buildFilterID(individual)) {
                console.debug('Source %s matches individual %s',
                  source, individual);
                this.toggleSourceVisibility(source, setVisible, individual);
                break;
              }
            }
          } else {
            // TODO(nickrobison): This will fail if the features don't have an ID property
            if ((data.properties as any).id === TrestleMapComponent
              .buildFilterID(individual)) {
              console.debug('Source feature %s matches individual %s',
                source, individual);
              this.toggleSourceVisibility(source, setVisible, individual);
              break;
            }
          }
        }
      }
    }
  }

  /**
   * Clear all the layers and sources from the map
   */
  public clearMap(): void {
    console.debug('Clearing map');
    this.mapSources.forEach((_, source) => {
      console.debug('removing:', source);
      this.removeSource(source);
    });
  }

  /**
   * Center the map on the given input set, computes the bounding box if one doesn't exist
   * @param {FeatureCollection<GeometryObject> | Feature<GeometryObject>} geom
   */
  public centerMap(geom: FeatureCollection<GeometryObject> | Feature<GeometryObject>): void {
    // We have to lock the map in order to avoid sending out a notice that the move happened.
    if (geom.bbox) {
      // FIXME(nrobison): This is garbage. Fix it.
      this.map.fitBounds(LngLatBounds.convert(geom.bbox as any));
    } else {
      const bbox = extent(geom);
      if (bbox) {
        // This works, but it seems to confuse the type system, so any for the win!
        this.map.fitBounds(LngLatBounds.convert(bbox as any));
      }
    }
  }

  /**
   * Change the map base layer
   * WARNING: This will cause all the sources and layers to be removed and added again, which is slow
   * @param {string} style - New base layer URL
   */
  public setMapStyle(style: string): void {
    this.clearMap();
    this.map.setStyle(style);
  }

  /**
   * Update the map view angle
   * @param {number} pitch
   * @param {number} bearing
   */
  public setPitchBearing(pitch?: number, bearing?: number): void {
    if (pitch) {
      this.map.setPitch(pitch);
    }

    if (bearing) {
      this.map.setBearing(bearing);
    }
  }

  /**
   * Change the 3D offset of the individual, or all sources that are currently at the input height
   * @param {number} height
   * @param {number} offset
   * @param {string} individual
   */
  public change3DOffset(height: number, offset: number, individual?: string): void {

    if (individual) {
      this.mapSources.forEach((layers, key) => {
        // If we have the individual's source, change its layers
        if (key === individual) {
          layers.forEach((layer) => {
            // You can't get properties that don't exist on layers,
            // that's an error not a null, because of course
            // But I'm sure it'll still return a null
            if (!layer.startsWith('label')) {
              const layerHeight = this.map.getPaintProperty(layer,
                'fill-extrusion-height');
              if (layerHeight) {
                this.map.setPaintProperty(layer,
                  'fill-extrusion-height',
                  layerHeight + offset);
                const layerBase = this.map.getPaintProperty(layer,
                  'fill-extrusion-base');
                if (layerBase) {
                  this.map.setPaintProperty(layer,
                    'fill-extrusion-base',
                    layerBase + offset);
                }
              }
            }
          });
        }
      });
    } else {
      //    Find all the individuals that have the same property
      //    For each layer, get its height
      this.mapSources.forEach((layers) => {
        layers.forEach((layer) => {
          const layerHeight = this.map.getPaintProperty(layer,
            'fill-extrusion-height');
          // If it matches the height of the layer, increase it
          if (layerHeight === height) {
            console.debug('Changing individuals');
            const layerBase = this.map.getPaintProperty(layer,
              'fill-extrusion-base');
            if (layerBase) {
              this.map.setPaintProperty(layer,
                'fill-extrusion-base',
                layerBase + offset);
            }
            this.map.setPaintProperty(layer,
              'fill-extrusion-height',
              layerHeight + offset);
          }
        });
      });
    }

  }

  private toggleSourceVisibility(source: string, setVisible: boolean, individual?: string): void {
    const layers = this.mapSources.get(source);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          // If we're filtering a layer and not a source,
          // set a filter to remove the individual
          if (individual) {
            // If we're setting the layer visible again,
            // remove it from the list and update the filter
            const filteredID = TrestleMapComponent.buildFilterID(individual);
            if (setVisible) {
              const idx = this.filteredIDs
                .indexOf(filteredID);
              if (idx > -1) {
                this.filteredIDs.splice(idx, 1);
              }
              //   If we're setting the layer invisible,
              // add the individual to the list of filtered IDs
            } else {
              console.debug('Removing individual %s from layer %s',
                individual, layer);
              this.filteredIDs.push(filteredID);
            }
            // If we have items to filter, add them,
            // otherwise remove the filter
            if (this.filteredIDs.length > 0) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              const filterValues = ['!in', 'id'].concat(this.filteredIDs);

              console.debug('Filtered Features:', this.map.querySourceFeatures(source,
                {
                  sourceLayer: layer,
                  filter: filterValues
                }));
              console.debug('Setting filter of %O on layer:', filterValues, layer);
              this.map.setFilter(layer, filterValues);
            } else {
              console.debug('Removing filter from layer:', layer);
              (this.map as any).setFilter(layer, null);
            }
          } else {
            if (setVisible) {
              this.map.setLayoutProperty(layer, 'visibility', 'visible');
            } else {
              this.map.setLayoutProperty(layer, 'visibility', 'none');
            }
          }
        });
    }

  }

  private removeSource(source: MapSource | string): void {
    let sourceID;
    if (typeof source === 'string') {
      sourceID = source;
    } else {
      sourceID = source.id;
    }

    if (this.mapSources.has(sourceID)) {
      console.debug('Removing source %s from map', sourceID);
      // Remove all the layers for each source
      const layers = this.mapSources.get(sourceID);
      if (layers !== undefined) {
        layers
          .forEach((layer) => {
            this.map.removeLayer(layer);
          });
      }

      this.map.removeSource(sourceID);
      this.mapSources.delete(sourceID);
    }
  }

  private addSource(inputLayer: MapSource): void {
    console.debug('Adding source data:', inputLayer.data);

    // Merge the new source with the default layers
    // But only if we don't already have that layer
    if (this.mapSources.has(inputLayer.id)) {
      console.debug('Map already has source:', inputLayer.id);
      return;
    }

    this.map.addSource(inputLayer.id, {
      type: 'geojson',
      data: inputLayer.data
    });

    const attributeLayers: string[] = [];

    // If it's a 3D layer, add the extrusion, otherwise add the normal layers
    if (TrestleMapComponent.is3D(inputLayer)) {
      console.debug('Adding 3D layer:', inputLayer.extrude);
      this.map.addLayer(inputLayer.extrude);
      attributeLayers.push(inputLayer.extrude.id);
    } else {
      // Add fill layer
      const fillID = inputLayer.id + '-fill';
      this.map.addLayer({
        id: fillID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 0.7,
        } as FillPaint)
      });
      // Add polygon line changes
      const lineId = inputLayer.id + '-line';
      this.map.addLayer({
        id: lineId,
        type: 'line',
        source: inputLayer.id,
        paint: {
          'line-color': 'white',
          'line-width': 2
        }
      });
      // Add hover layer
      const hoverID = inputLayer.id + '-hover';
      this.map.addLayer({
        id: hoverID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 1,
          // Repaint the lines so that they're still visible
        } as FillPaint),
        filter: ['==', 'name', '']
      });
      attributeLayers.push(fillID, lineId, hoverID);
    }

    // Labels
    const labelField = inputLayer.labelField;
    const labelValue = inputLayer.labelValue;
    if (labelField || labelValue) {
      // If it's a collection for each entity, add the label
      const iData = inputLayer.data;
      if (TrestleMapComponent.isCollection(iData)) {
        // Collections only support field labels, since we need to know how to filter each one
        if (labelField == null) {
          throw new Error('Geometry collections only support label Fields, not values');
        }
        iData.features.forEach((feature) => {
          const labelLayerID = 'label-' + feature.id;
          // This is terrible, but so is the web, so who blinks first?
          // If we have a field, use it, otherwise, use the provided value
          const labelText: string = labelField ? (feature.properties as any)[(labelField as any)] : labelValue;
          this.map.addLayer({
            id: labelLayerID,
            type: 'symbol',
            source: inputLayer.id,
            layout: {
              // For the text field, if the label function exists, call it, otherwise just place the label
              'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
              'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
              'text-size': 11,
              'text-transform': 'uppercase',
              'text-letter-spacing': 0.05,
              'text-offset': [0, 1.5],
              'text-allow-overlap': true
              // "text-ignore-placement": true
            },
            paint: {
              'text-color': '#202',
              'text-halo-color': '#fff',
              'text-halo-width': 2
            },
            filter: ['==', labelField, labelText]
          });
          attributeLayers.push(labelLayerID);
        });
      } else {
        const labelLayerID = 'label-' + iData.id;
        // This is terrible, but so is the web, so who blinks first?
        const labelText: string = labelField ? (iData.properties as any)[(labelField as any)] : labelValue;
        this.map.addLayer({
          id: labelLayerID,
          type: 'symbol',
          source: inputLayer.id,
          layout: {
            // For the text field, if the label function exists, call it, otherwise just place the label
            'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
            'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
            'text-size': 11,
            'text-transform': 'uppercase',
            'text-letter-spacing': 0.05,
            'text-offset': [0, 1.5]
          },
          paint: {
            'text-color': '#202',
            'text-halo-color': '#fff',
            'text-halo-width': 2
          }
        });
        attributeLayers.push(labelLayerID);
      }
    }
    // Add the map sources
    this.mapSources.set(inputLayer.id, attributeLayers);

    //    Center map
    if (this.centerMapOnLoad.getValue()) {
      this.centerMap(inputLayer.data);
    }
  }

  private layerClick = (e: MapMouseEvent): void => {
    console.debug('Clicked:', e);
    // FIXME(nrobison): Get rid of this type cast.
    // Get all the fill fillLayers
    let fillLayers: string[] = [];
    this.mapSources.forEach((values) => {
      fillLayers = fillLayers
        .concat((values
          // If we have a clickLayerSuffix, filter on that, otherwise, just find the fill Â¬layers
          .filter((val) => val.includes(this.clickLayerSuffix ? this.clickLayerSuffix : '-fill'))));
    });
    console.debug('Querying on fillLayers:', fillLayers);
    const features: any[] = this.map.queryRenderedFeatures(e.point, {
      layers: fillLayers
    });
    // Set the hover filter using either the provided id field, or a default property
    console.debug('Filtering with data:', this.data);
    // const idField = this.data.idField === undefined ? "id" : this.data.idField;
    const idField = 'id';
    console.debug('Accessing ID field:', idField);

    // If we don't filter on anything, deselect it all
    if (!this.multiSelect && !(features.length > 0)) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((layers) => {
        hoverLayers = hoverLayers
          .concat(layers
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting', hoverLayers);
      hoverLayers.forEach((layer) => {
        this.map.setFilter(layer, ['==', idField, '']);
      });
      return;
    }
    console.debug('Filtered features', features);

    const feature: any = features[0];
    let layerID = features[0].layer.id;
    // Emit the clicked layer
    const featureID = feature.properties[idField];
    this.clicked.emit(featureID);
    layerID = layerID.replace('-fill', '');
    console.debug('Filtering on layer:', layerID + '-hover');
    this.map.setFilter(layerID + '-hover', ['==', idField, featureID]);
    // If multi-select is not enabled, deselect everything else
    if (!this.multiSelect) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((values) => {
        hoverLayers = hoverLayers
          .concat(values
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting:', hoverLayers);
      // Add hover back to the layerID, otherwise nothing will match
      layerID = layerID + '-hover';
      hoverLayers
        .forEach((layer) => {
          if (layer !== layerID) {
            this.map.setFilter(layer, ['==', idField, '']);
          }
        });
    }
  };


  private mouseOver = (e: MapMouseEvent): void => {
    console.debug('Moused over:', e);
  };

  private mouseOut = (e: MapMouseEvent): void => {
    console.debug('Mouse out:', e);
  };

  private moveHandler = () => {
    this.mapBounds.emit(this.map.getBounds());
  };

  private setupDefaults(): void {
    this.baseConfig = {
      container: 'map',
      style: 'mapbox://styles/mapbox/light-v9',
      center: {lng: 32.3558991, lat: -25.6854313},
      zoom: 8,
      accessToken: 'pk.eyJ1IjoibnJvYmlzb24iLCJhIjoiY2ozdDd5dmd2MDA3bTMxcW1kdHZrZ3ppMCJ9.YcJMRphQAfmZ0H8X9HnoKA'
    };
  }

  private static is3D(x: any): x is I3DMapSource {
    return (x as I3DMapSource).extrude !== undefined;
  }

  private static isGeoJSON(x: any): x is GeoJSONDataSource {
    return (x as GeoJSONSource).type === 'geojson';
  }

  private static isCollection(x: any): x is FeatureCollection<GeometryObject> {
    return (x as FeatureCollection<GeometryObject>).type === 'FeatureCollection';
  }

  private static buildFilterID(individual: string): string {
    console.debug('Filtering:', individual);
    return TrestleIndividual.filterID(individual)
      .replace(/-/g, ' ')
      .replace(':', '-');
  }
}

import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChange} from "@angular/core";
import {Feature, FeatureCollection, GeometryObject} from "geojson";
import {BehaviorSubject, Subject} from "rxjs";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import extent from "@mapbox/geojson-extent";
import {
  FillExtrusionLayer,
  FillLayer,
  FillPaint,
  GeoJSONSource,
  GeoJSONSourceRaw,
  ImageSource,
  LineLayer,
  LngLatBounds,
  Map as MapboxMap,
  MapboxOptions,
  MapMouseEvent,
  RasterSource,
  VectorSource,
  VideoSource
} from "mapbox-gl";

export interface IMapFillLayer extends FillLayer {
  type: 'fill';
}

export interface IMapLineLayer extends LineLayer {
  type: 'line';
}

export interface IMapHoverLayer extends FillLayer {
  type: 'fill';
  filter: ['==', 'name', ''];
}

export interface ITrestleMapLayers {
  fill?: IMapFillLayer;
  line?: IMapLineLayer;
  hover?: IMapHoverLayer;
}

export interface ITrestleMapSource {
  id: string;
  idField?: string;
  data: FeatureCollection<GeometryObject> | Feature<GeometryObject>;
  layers?: ITrestleMapLayers;
  labelField?: string;
  labelFunction?: (label: string) => string;
  labelValue?: string;
}

export interface I3DMapSource extends ITrestleMapSource {
  extrude: FillExtrusionLayer;
}

interface GeoJSONDataSource extends GeoJSONSource {
  _data: Feature<GeometryObject> | FeatureCollection<GeometryObject>;
}

export interface IMapAttributeChange {
  individual: string;
  changes: Array<{ attribute: string, value: any }>;
  // attribute: string;
  // value: any;
}

export interface IMapEventHandler {
  event: MapEvent;
  handler: (event: any) => void;
}

export type MapEvent = 'mousemove' | 'mouseleave' | 'click' | 'moveend';
export type MapSource = I3DMapSource | ITrestleMapSource;
export type MapBoxSource = GeoJSONSource | VectorSource | RasterSource | ImageSource | VideoSource | GeoJSONSourceRaw;

@Component({
  selector: 'trestle-map',
  templateUrl: './trestle-map.component.html',
  styleUrls: ['./trestle-map.component.scss']
})

export class TrestleMapComponent implements OnInit, OnChanges {

  @Input() public data: MapSource;
  @Input() public single: boolean;
  @Input() public multiSelect: boolean;
  @Input() public clickLayerSuffix: string;
  @Input() public zoomOnLoad?: boolean;
  @Input() public config?: MapboxOptions;
  @Input() public dataChanges: Subject<MapSource | undefined>;
  @Input() public attributeChanges: Subject<IMapAttributeChange>;
  @Input() public handlers: IMapEventHandler[];
  @Output() public mapBounds: EventEmitter<LngLatBounds> = new EventEmitter();
  @Output() public clicked: EventEmitter<string> = new EventEmitter();
  private centerMapOnLoad: BehaviorSubject<boolean>;
  private baseConfig: MapboxOptions;
  private map: MapboxMap;
  private mapSources: Map<string, string[]>;
  // This has to be integers, in order to match against the numeric IDs
  private filteredIDs: string[];
  private previousValue: MapSource;

  constructor() {

    this.mapSources = new Map();
    this.filteredIDs = [];

    //    Set defaults
    this.setupDefaults();
  }

  public ngOnInit(): void {
    if (this.zoomOnLoad === undefined) {
      this.centerMapOnLoad = new BehaviorSubject(true);
    } else {
      this.centerMapOnLoad = new BehaviorSubject(this.zoomOnLoad);
    }

    console.debug('Creating map, ' +
      'singleSelect?', this.single,
      'mulitSelect?', this.multiSelect,
      'zoom?', this.centerMapOnLoad.getValue());

    // Merge the map configs together
    const mergedConfig = Object.assign(this.baseConfig, this.config);
    this.map = new MapboxMap(mergedConfig);

    this.map.on('click', this.layerClick);
    this.map.on('mouseover', this.mouseOver);
    this.map.on('mouseleave', this.mouseOut);
    this.map.on('moveend', this.moveHandler);

    // Register any additional handlers
    if (this.handlers) {
      this.handlers.forEach((h) => {
        this.map.on(h.event, h.handler);
      });
    }

    // Once the map is loaded, setup the subscriptions
    this.map.on('style.load', () => {
      // If it's null, create a dummy one
      if (this.dataChanges === undefined) {
        console.debug('Creating dummy data changes subscription');
        this.dataChanges = new Subject();
      }
      console.debug('Subscribing to data changes observable');
      this.dataChanges.subscribe((data) => {
        console.debug('Map has new data to load', data);
        if (data !== undefined) {
          if (this.single && this.previousValue) {
            this.removeSource(this.previousValue);
          }
          this.addSource(data);
          this.previousValue = data;
        }
      });

      if (this.attributeChanges === undefined) {
        console.debug('Creating dummy attribute subscription');
        this.attributeChanges = new Subject();
      }
      console.debug('Subscribing to attribute changes observable');
      this.attributeChanges.subscribe((change) => {
        this.changeIndividualAttribute(change);
      });
      this.mapBounds.emit(this.map.getBounds());
    });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    // Individual changes
    const inputChanges = changes['data'];
    if (inputChanges != null
      && !inputChanges.isFirstChange()
      && (inputChanges.currentValue !== inputChanges.previousValue)) {
      console.debug('New change, updating', inputChanges);
      if (inputChanges.previousValue != null && this.single) {
        // mapChanges.previousValue= inputChanges.previousValue;
        this.removeSource(inputChanges.previousValue);
      }
      // this.dataChanges.next(mapChanges);
      this.addSource(inputChanges.currentValue);
    }

    //    Zoom On Load changes
    const zoomChanges = changes['zoomOnLoad'];
    if (zoomChanges != null
      && !zoomChanges.isFirstChange()) {
      console.debug('Changing zoom value');
      this.centerMapOnLoad.next(zoomChanges.currentValue);
    }

    // Event handlers
    // const handlerChanges = changes["handlers"];
    // if (handlerChanges != null
    //     && (handlerChanges.currentValue !== handlerChanges.previousValue)) {
    //     console.debug("Registering event handlers");
    //     (handlerChanges.currentValue as IMapEventHandler[]).forEach((e) => {
    //         this.map.on(e.event, e.handler);
    //     });
    // }
  }

  /**
   * Get the current map bounds
   * @returns {mapboxgl.LngLatBounds}
   */
  public getMapBounds(): LngLatBounds {
    return this.map.getBounds();
  }

  /**
   * Remove individual from the map, which clears the source and linked layers
   * @param {string} individual
   */
  public removeIndividual(individual: string): void {
    console.debug('Removing selection %s from the map', individual);

    // Is the data a source?
    if (this.mapSources.has(individual)) {
      this.removeSource(individual);
    } else {
      //    Otherwise find the matching layer and remove it
    }
  }

  /**
   * Modify the specified attribute for the given individual
   * @param {IMapAttributeChange} attributeChange
   */
  public changeIndividualAttribute(attributeChange: IMapAttributeChange): void {
    console.debug('Changing attribute:', attributeChange);

    //    Try to get the source first
    const layers = this.mapSources.get(attributeChange.individual);
    if (layers !== undefined) {
      console.debug('Changing layers:', layers);
      layers.filter((layer) => !layer.startsWith('label'))
        .forEach((layer) => {
          attributeChange.changes.forEach((change) => {
            this.map.setPaintProperty(layer, change.attribute, change.value);
          });

        });
    }
    //    I don't think we can do this with individuals yet, but maybe?
  }

  /**
   * Toggle the visibility of the layers for the given individual
   * This changes all registered layers for the given source
   * @param {string} individual
   * @param {boolean} setVisible
   */
  public toggleIndividualVisibility(individual: string, setVisible: boolean): void {
    console.debug('setting visible?', setVisible);
    //    See if the data is a source
    const layers = this.mapSources.get(individual);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          if (setVisible) {
            this.map.setLayoutProperty(layer, 'visibility', 'visible');
          } else {
            this.map.setLayoutProperty(layer, 'visibility', 'none');
          }
        });
      //    If not, figure out which layers have the data
    } else {
      console.debug('Looking for matching individual id:',
        TrestleMapComponent.buildFilterID(individual));
      for (const source of Array.from(this.mapSources.keys())) {
        const mapSource = this.map.getSource(source);
        if (TrestleMapComponent.isGeoJSON(mapSource)) {
          console.debug('Checking source:', mapSource);
          console.debug('Has data:', (mapSource as any)._data);
          const data = mapSource._data;
          // If it's a feature collection, dive into it
          if (TrestleMapComponent.isCollection(data)) {
            for (const feature of data.features) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              if ((feature.properties as any).id === TrestleMapComponent
                .buildFilterID(individual)) {
                console.debug('Source %s matches individual %s',
                  source, individual);
                this.toggleSourceVisibility(source, setVisible, individual);
                break;
              }
            }
          } else {
            // TODO(nickrobison): This will fail if the features don't have an ID property
            if ((data.properties as any).id === TrestleMapComponent
              .buildFilterID(individual)) {
              console.debug('Source feature %s matches individual %s',
                source, individual);
              this.toggleSourceVisibility(source, setVisible, individual);
              break;
            }
          }
        }
      }
    }
  }

  /**
   * Clear all the layers and sources from the map
   */
  public clearMap(): void {
    console.debug('Clearing map');
    this.mapSources.forEach((_, source) => {
      console.debug('removing:', source);
      this.removeSource(source);
    });
  }

  /**
   * Center the map on the given input set, computes the bounding box if one doesn't exist
   * @param {FeatureCollection<GeometryObject> | Feature<GeometryObject>} geom
   */
  public centerMap(geom: FeatureCollection<GeometryObject> | Feature<GeometryObject>): void {
    // We have to lock the map in order to avoid sending out a notice that the move happened.
    if (geom.bbox) {
      // FIXME(nrobison): This is garbage. Fix it.
      this.map.fitBounds(LngLatBounds.convert(geom.bbox as any));
    } else {
      const bbox = extent(geom);
      if (bbox) {
        // This works, but it seems to confuse the type system, so any for the win!
        this.map.fitBounds(LngLatBounds.convert(bbox as any));
      }
    }
  }

  /**
   * Change the map base layer
   * WARNING: This will cause all the sources and layers to be removed and added again, which is slow
   * @param {string} style - New base layer URL
   */
  public setMapStyle(style: string): void {
    this.clearMap();
    this.map.setStyle(style);
  }

  /**
   * Update the map view angle
   * @param {number} pitch
   * @param {number} bearing
   */
  public setPitchBearing(pitch?: number, bearing?: number): void {
    if (pitch) {
      this.map.setPitch(pitch);
    }

    if (bearing) {
      this.map.setBearing(bearing);
    }
  }

  /**
   * Change the 3D offset of the individual, or all sources that are currently at the input height
   * @param {number} height
   * @param {number} offset
   * @param {string} individual
   */
  public change3DOffset(height: number, offset: number, individual?: string): void {

    if (individual) {
      this.mapSources.forEach((layers, key) => {
        // If we have the individual's source, change its layers
        if (key === individual) {
          layers.forEach((layer) => {
            // You can't get properties that don't exist on layers,
            // that's an error not a null, because of course
            // But I'm sure it'll still return a null
            if (!layer.startsWith('label')) {
              const layerHeight = this.map.getPaintProperty(layer,
                'fill-extrusion-height');
              if (layerHeight) {
                this.map.setPaintProperty(layer,
                  'fill-extrusion-height',
                  layerHeight + offset);
                const layerBase = this.map.getPaintProperty(layer,
                  'fill-extrusion-base');
                if (layerBase) {
                  this.map.setPaintProperty(layer,
                    'fill-extrusion-base',
                    layerBase + offset);
                }
              }
            }
          });
        }
      });
    } else {
      //    Find all the individuals that have the same property
      //    For each layer, get its height
      this.mapSources.forEach((layers) => {
        layers.forEach((layer) => {
          const layerHeight = this.map.getPaintProperty(layer,
            'fill-extrusion-height');
          // If it matches the height of the layer, increase it
          if (layerHeight === height) {
            console.debug('Changing individuals');
            const layerBase = this.map.getPaintProperty(layer,
              'fill-extrusion-base');
            if (layerBase) {
              this.map.setPaintProperty(layer,
                'fill-extrusion-base',
                layerBase + offset);
            }
            this.map.setPaintProperty(layer,
              'fill-extrusion-height',
              layerHeight + offset);
          }
        });
      });
    }

  }

  private toggleSourceVisibility(source: string, setVisible: boolean, individual?: string): void {
    const layers = this.mapSources.get(source);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          // If we're filtering a layer and not a source,
          // set a filter to remove the individual
          if (individual) {
            // If we're setting the layer visible again,
            // remove it from the list and update the filter
            const filteredID = TrestleMapComponent.buildFilterID(individual);
            if (setVisible) {
              const idx = this.filteredIDs
                .indexOf(filteredID);
              if (idx > -1) {
                this.filteredIDs.splice(idx, 1);
              }
              //   If we're setting the layer invisible,
              // add the individual to the list of filtered IDs
            } else {
              console.debug('Removing individual %s from layer %s',
                individual, layer);
              this.filteredIDs.push(filteredID);
            }
            // If we have items to filter, add them,
            // otherwise remove the filter
            if (this.filteredIDs.length > 0) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              const filterValues = ['!in', 'id'].concat(this.filteredIDs);

              console.debug('Filtered Features:', this.map.querySourceFeatures(source,
                {
                  sourceLayer: layer,
                  filter: filterValues
                }));
              console.debug('Setting filter of %O on layer:', filterValues, layer);
              this.map.setFilter(layer, filterValues);
            } else {
              console.debug('Removing filter from layer:', layer);
              (this.map as any).setFilter(layer, null);
            }
          } else {
            if (setVisible) {
              this.map.setLayoutProperty(layer, 'visibility', 'visible');
            } else {
              this.map.setLayoutProperty(layer, 'visibility', 'none');
            }
          }
        });
    }

  }

  private removeSource(source: MapSource | string): void {
    let sourceID;
    if (typeof source === 'string') {
      sourceID = source;
    } else {
      sourceID = source.id;
    }

    if (this.mapSources.has(sourceID)) {
      console.debug('Removing source %s from map', sourceID);
      // Remove all the layers for each source
      const layers = this.mapSources.get(sourceID);
      if (layers !== undefined) {
        layers
          .forEach((layer) => {
            this.map.removeLayer(layer);
          });
      }

      this.map.removeSource(sourceID);
      this.mapSources.delete(sourceID);
    }
  }

  private addSource(inputLayer: MapSource): void {
    console.debug('Adding source data:', inputLayer.data);

    // Merge the new source with the default layers
    // But only if we don't already have that layer
    if (this.mapSources.has(inputLayer.id)) {
      console.debug('Map already has source:', inputLayer.id);
      return;
    }

    this.map.addSource(inputLayer.id, {
      type: 'geojson',
      data: inputLayer.data
    });

    const attributeLayers: string[] = [];

    // If it's a 3D layer, add the extrusion, otherwise add the normal layers
    if (TrestleMapComponent.is3D(inputLayer)) {
      console.debug('Adding 3D layer:', inputLayer.extrude);
      this.map.addLayer(inputLayer.extrude);
      attributeLayers.push(inputLayer.extrude.id);
    } else {
      // Add fill layer
      const fillID = inputLayer.id + '-fill';
      this.map.addLayer({
        id: fillID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 0.7,
        } as FillPaint)
      });
      // Add polygon line changes
      const lineId = inputLayer.id + '-line';
      this.map.addLayer({
        id: lineId,
        type: 'line',
        source: inputLayer.id,
        paint: {
          'line-color': 'white',
          'line-width': 2
        }
      });
      // Add hover layer
      const hoverID = inputLayer.id + '-hover';
      this.map.addLayer({
        id: hoverID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 1,
          // Repaint the lines so that they're still visible
        } as FillPaint),
        filter: ['==', 'name', '']
      });
      attributeLayers.push(fillID, lineId, hoverID);
    }

    // Labels
    const labelField = inputLayer.labelField;
    const labelValue = inputLayer.labelValue;
    if (labelField || labelValue) {
      // If it's a collection for each entity, add the label
      const iData = inputLayer.data;
      if (TrestleMapComponent.isCollection(iData)) {
        // Collections only support field labels, since we need to know how to filter each one
        if (labelField == null) {
          throw new Error('Geometry collections only support label Fields, not values');
        }
        iData.features.forEach((feature) => {
          const labelLayerID = 'label-' + feature.id;
          // This is terrible, but so is the web, so who blinks first?
          // If we have a field, use it, otherwise, use the provided value
          const labelText: string = labelField ? (feature.properties as any)[(labelField as any)] : labelValue;
          this.map.addLayer({
            id: labelLayerID,
            type: 'symbol',
            source: inputLayer.id,
            layout: {
              // For the text field, if the label function exists, call it, otherwise just place the label
              'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
              'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
              'text-size': 11,
              'text-transform': 'uppercase',
              'text-letter-spacing': 0.05,
              'text-offset': [0, 1.5],
              'text-allow-overlap': true
              // "text-ignore-placement": true
            },
            paint: {
              'text-color': '#202',
              'text-halo-color': '#fff',
              'text-halo-width': 2
            },
            filter: ['==', labelField, labelText]
          });
          attributeLayers.push(labelLayerID);
        });
      } else {
        const labelLayerID = 'label-' + iData.id;
        // This is terrible, but so is the web, so who blinks first?
        const labelText: string = labelField ? (iData.properties as any)[(labelField as any)] : labelValue;
        this.map.addLayer({
          id: labelLayerID,
          type: 'symbol',
          source: inputLayer.id,
          layout: {
            // For the text field, if the label function exists, call it, otherwise just place the label
            'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
            'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
            'text-size': 11,
            'text-transform': 'uppercase',
            'text-letter-spacing': 0.05,
            'text-offset': [0, 1.5]
          },
          paint: {
            'text-color': '#202',
            'text-halo-color': '#fff',
            'text-halo-width': 2
          }
        });
        attributeLayers.push(labelLayerID);
      }
    }
    // Add the map sources
    this.mapSources.set(inputLayer.id, attributeLayers);

    //    Center map
    if (this.centerMapOnLoad.getValue()) {
      this.centerMap(inputLayer.data);
    }
  }

  private layerClick = (e: MapMouseEvent): void => {
    console.debug('Clicked:', e);
    // FIXME(nrobison): Get rid of this type cast.
    // Get all the fill fillLayers
    let fillLayers: string[] = [];
    this.mapSources.forEach((values) => {
      fillLayers = fillLayers
        .concat((values
          // If we have a clickLayerSuffix, filter on that, otherwise, just find the fill Â¬layers
          .filter((val) => val.includes(this.clickLayerSuffix ? this.clickLayerSuffix : '-fill'))));
    });
    console.debug('Querying on fillLayers:', fillLayers);
    const features: any[] = this.map.queryRenderedFeatures(e.point, {
      layers: fillLayers
    });
    // Set the hover filter using either the provided id field, or a default property
    console.debug('Filtering with data:', this.data);
    // const idField = this.data.idField === undefined ? "id" : this.data.idField;
    const idField = 'id';
    console.debug('Accessing ID field:', idField);

    // If we don't filter on anything, deselect it all
    if (!this.multiSelect && !(features.length > 0)) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((layers) => {
        hoverLayers = hoverLayers
          .concat(layers
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting', hoverLayers);
      hoverLayers.forEach((layer) => {
        this.map.setFilter(layer, ['==', idField, '']);
      });
      return;
    }
    console.debug('Filtered features', features);

    const feature: any = features[0];
    let layerID = features[0].layer.id;
    // Emit the clicked layer
    const featureID = feature.properties[idField];
    this.clicked.emit(featureID);
    layerID = layerID.replace('-fill', '');
    console.debug('Filtering on layer:', layerID + '-hover');
    this.map.setFilter(layerID + '-hover', ['==', idField, featureID]);
    // If multi-select is not enabled, deselect everything else
    if (!this.multiSelect) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((values) => {
        hoverLayers = hoverLayers
          .concat(values
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting:', hoverLayers);
      // Add hover back to the layerID, otherwise nothing will match
      layerID = layerID + '-hover';
      hoverLayers
        .forEach((layer) => {
          if (layer !== layerID) {
            this.map.setFilter(layer, ['==', idField, '']);
          }
        });
    }
  };


  private mouseOver = (e: MapMouseEvent): void => {
    console.debug('Moused over:', e);
  };

  private mouseOut = (e: MapMouseEvent): void => {
    console.debug('Mouse out:', e);
  };

  private moveHandler = () => {
    this.mapBounds.emit(this.map.getBounds());
  };

  private setupDefaults(): void {
    this.baseConfig = {
      container: 'map',
      style: 'mapbox://styles/mapbox/light-v9',
      center: {lng: 32.3558991, lat: -25.6854313},
      zoom: 8,
      accessToken: 'pk.eyJ1IjoibnJvYmlzb24iLCJhIjoiY2ozdDd5dmd2MDA3bTMxcW1kdHZrZ3ppMCJ9.YcJMRphQAfmZ0H8X9HnoKA'
    };
  }

  private static is3D(x: any): x is I3DMapSource {
    return (x as I3DMapSource).extrude !== undefined;
  }

  private static isGeoJSON(x: any): x is GeoJSONDataSource {
    return (x as GeoJSONSource).type === 'geojson';
  }

  private static isCollection(x: any): x is FeatureCollection<GeometryObject> {
    return (x as FeatureCollection<GeometryObject>).type === 'FeatureCollection';
  }

  private static buildFilterID(individual: string): string {
    console.debug('Filtering:', individual);
    return TrestleIndividual.filterID(individual)
      .replace(/-/g, ' ')
      .replace(':', '-');
  }
}

import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChange} from "@angular/core";
import {Feature, FeatureCollection, GeometryObject} from "geojson";
import {BehaviorSubject, Subject} from "rxjs";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import extent from "@mapbox/geojson-extent";
import {
  FillExtrusionLayer,
  FillLayer,
  FillPaint,
  GeoJSONSource,
  GeoJSONSourceRaw,
  ImageSource,
  LineLayer,
  LngLatBounds,
  Map as MapboxMap,
  MapboxOptions,
  MapMouseEvent,
  RasterSource,
  VectorSource,
  VideoSource
} from "mapbox-gl";

export interface IMapFillLayer extends FillLayer {
  type: 'fill';
}

export interface IMapLineLayer extends LineLayer {
  type: 'line';
}

export interface IMapHoverLayer extends FillLayer {
  type: 'fill';
  filter: ['==', 'name', ''];
}

export interface ITrestleMapLayers {
  fill?: IMapFillLayer;
  line?: IMapLineLayer;
  hover?: IMapHoverLayer;
}

export interface ITrestleMapSource {
  id: string;
  idField?: string;
  data: FeatureCollection<GeometryObject> | Feature<GeometryObject>;
  layers?: ITrestleMapLayers;
  labelField?: string;
  labelFunction?: (label: string) => string;
  labelValue?: string;
}

export interface I3DMapSource extends ITrestleMapSource {
  extrude: FillExtrusionLayer;
}

interface GeoJSONDataSource extends GeoJSONSource {
  _data: Feature<GeometryObject> | FeatureCollection<GeometryObject>;
}

export interface IMapAttributeChange {
  individual: string;
  changes: Array<{ attribute: string, value: any }>;
  // attribute: string;
  // value: any;
}

export interface IMapEventHandler {
  event: MapEvent;
  handler: (event: any) => void;
}

export type MapEvent = 'mousemove' | 'mouseleave' | 'click' | 'moveend';
export type MapSource = I3DMapSource | ITrestleMapSource;
export type MapBoxSource = GeoJSONSource | VectorSource | RasterSource | ImageSource | VideoSource | GeoJSONSourceRaw;

@Component({
  selector: 'trestle-map',
  templateUrl: './trestle-map.component.html',
  styleUrls: ['./trestle-map.component.scss']
})

export class TrestleMapComponent implements OnInit, OnChanges {

  @Input() public data: MapSource;
  @Input() public single: boolean;
  @Input() public multiSelect: boolean;
  @Input() public clickLayerSuffix: string;
  @Input() public zoomOnLoad?: boolean;
  @Input() public config?: MapboxOptions;
  @Input() public dataChanges: Subject<MapSource | undefined>;
  @Input() public attributeChanges: Subject<IMapAttributeChange>;
  @Input() public handlers: IMapEventHandler[];
  @Output() public mapBounds: EventEmitter<LngLatBounds> = new EventEmitter();
  @Output() public clicked: EventEmitter<string> = new EventEmitter();
  private centerMapOnLoad: BehaviorSubject<boolean>;
  private baseConfig: MapboxOptions;
  private map: MapboxMap;
  private mapSources: Map<string, string[]>;
  // This has to be integers, in order to match against the numeric IDs
  private filteredIDs: string[];
  private previousValue: MapSource;

  constructor() {

    this.mapSources = new Map();
    this.filteredIDs = [];

    //    Set defaults
    this.setupDefaults();
  }

  public ngOnInit(): void {
    if (this.zoomOnLoad === undefined) {
      this.centerMapOnLoad = new BehaviorSubject(true);
    } else {
      this.centerMapOnLoad = new BehaviorSubject(this.zoomOnLoad);
    }

    console.debug('Creating map, ' +
      'singleSelect?', this.single,
      'mulitSelect?', this.multiSelect,
      'zoom?', this.centerMapOnLoad.getValue());

    // Merge the map configs together
    const mergedConfig = Object.assign(this.baseConfig, this.config);
    this.map = new MapboxMap(mergedConfig);

    this.map.on('click', this.layerClick);
    this.map.on('mouseover', this.mouseOver);
    this.map.on('mouseleave', this.mouseOut);
    this.map.on('moveend', this.moveHandler);

    // Register any additional handlers
    if (this.handlers) {
      this.handlers.forEach((h) => {
        this.map.on(h.event, h.handler);
      });
    }

    // Once the map is loaded, setup the subscriptions
    this.map.on('style.load', () => {
      // If it's null, create a dummy one
      if (this.dataChanges === undefined) {
        console.debug('Creating dummy data changes subscription');
        this.dataChanges = new Subject();
      }
      console.debug('Subscribing to data changes observable');
      this.dataChanges.subscribe((data) => {
        console.debug('Map has new data to load', data);
        if (data !== undefined) {
          if (this.single && this.previousValue) {
            this.removeSource(this.previousValue);
          }
          this.addSource(data);
          this.previousValue = data;
        }
      });

      if (this.attributeChanges === undefined) {
        console.debug('Creating dummy attribute subscription');
        this.attributeChanges = new Subject();
      }
      console.debug('Subscribing to attribute changes observable');
      this.attributeChanges.subscribe((change) => {
        this.changeIndividualAttribute(change);
      });
      this.mapBounds.emit(this.map.getBounds());
    });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    // Individual changes
    const inputChanges = changes['data'];
    if (inputChanges != null
      && !inputChanges.isFirstChange()
      && (inputChanges.currentValue !== inputChanges.previousValue)) {
      console.debug('New change, updating', inputChanges);
      if (inputChanges.previousValue != null && this.single) {
        // mapChanges.previousValue= inputChanges.previousValue;
        this.removeSource(inputChanges.previousValue);
      }
      // this.dataChanges.next(mapChanges);
      this.addSource(inputChanges.currentValue);
    }

    //    Zoom On Load changes
    const zoomChanges = changes['zoomOnLoad'];
    if (zoomChanges != null
      && !zoomChanges.isFirstChange()) {
      console.debug('Changing zoom value');
      this.centerMapOnLoad.next(zoomChanges.currentValue);
    }

    // Event handlers
    // const handlerChanges = changes["handlers"];
    // if (handlerChanges != null
    //     && (handlerChanges.currentValue !== handlerChanges.previousValue)) {
    //     console.debug("Registering event handlers");
    //     (handlerChanges.currentValue as IMapEventHandler[]).forEach((e) => {
    //         this.map.on(e.event, e.handler);
    //     });
    // }
  }

  /**
   * Get the current map bounds
   * @returns {mapboxgl.LngLatBounds}
   */
  public getMapBounds(): LngLatBounds {
    return this.map.getBounds();
  }

  /**
   * Remove individual from the map, which clears the source and linked layers
   * @param {string} individual
   */
  public removeIndividual(individual: string): void {
    console.debug('Removing selection %s from the map', individual);

    // Is the data a source?
    if (this.mapSources.has(individual)) {
      this.removeSource(individual);
    } else {
      //    Otherwise find the matching layer and remove it
    }
  }

  /**
   * Modify the specified attribute for the given individual
   * @param {IMapAttributeChange} attributeChange
   */
  public changeIndividualAttribute(attributeChange: IMapAttributeChange): void {
    console.debug('Changing attribute:', attributeChange);

    //    Try to get the source first
    const layers = this.mapSources.get(attributeChange.individual);
    if (layers !== undefined) {
      console.debug('Changing layers:', layers);
      layers.filter((layer) => !layer.startsWith('label'))
        .forEach((layer) => {
          attributeChange.changes.forEach((change) => {
            this.map.setPaintProperty(layer, change.attribute, change.value);
          });

        });
    }
    //    I don't think we can do this with individuals yet, but maybe?
  }

  /**
   * Toggle the visibility of the layers for the given individual
   * This changes all registered layers for the given source
   * @param {string} individual
   * @param {boolean} setVisible
   */
  public toggleIndividualVisibility(individual: string, setVisible: boolean): void {
    console.debug('setting visible?', setVisible);
    //    See if the data is a source
    const layers = this.mapSources.get(individual);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          if (setVisible) {
            this.map.setLayoutProperty(layer, 'visibility', 'visible');
          } else {
            this.map.setLayoutProperty(layer, 'visibility', 'none');
          }
        });
      //    If not, figure out which layers have the data
    } else {
      console.debug('Looking for matching individual id:',
        TrestleMapComponent.buildFilterID(individual));
      for (const source of Array.from(this.mapSources.keys())) {
        const mapSource = this.map.getSource(source);
        if (TrestleMapComponent.isGeoJSON(mapSource)) {
          console.debug('Checking source:', mapSource);
          console.debug('Has data:', (mapSource as any)._data);
          const data = mapSource._data;
          // If it's a feature collection, dive into it
          if (TrestleMapComponent.isCollection(data)) {
            for (const feature of data.features) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              if ((feature.properties as any).id === TrestleMapComponent
                .buildFilterID(individual)) {
                console.debug('Source %s matches individual %s',
                  source, individual);
                this.toggleSourceVisibility(source, setVisible, individual);
                break;
              }
            }
          } else {
            // TODO(nickrobison): This will fail if the features don't have an ID property
            if ((data.properties as any).id === TrestleMapComponent
              .buildFilterID(individual)) {
              console.debug('Source feature %s matches individual %s',
                source, individual);
              this.toggleSourceVisibility(source, setVisible, individual);
              break;
            }
          }
        }
      }
    }
  }

  /**
   * Clear all the layers and sources from the map
   */
  public clearMap(): void {
    console.debug('Clearing map');
    this.mapSources.forEach((_, source) => {
      console.debug('removing:', source);
      this.removeSource(source);
    });
  }

  /**
   * Center the map on the given input set, computes the bounding box if one doesn't exist
   * @param {FeatureCollection<GeometryObject> | Feature<GeometryObject>} geom
   */
  public centerMap(geom: FeatureCollection<GeometryObject> | Feature<GeometryObject>): void {
    // We have to lock the map in order to avoid sending out a notice that the move happened.
    if (geom.bbox) {
      // FIXME(nrobison): This is garbage. Fix it.
      this.map.fitBounds(LngLatBounds.convert(geom.bbox as any));
    } else {
      const bbox = extent(geom);
      if (bbox) {
        // This works, but it seems to confuse the type system, so any for the win!
        this.map.fitBounds(LngLatBounds.convert(bbox as any));
      }
    }
  }

  /**
   * Change the map base layer
   * WARNING: This will cause all the sources and layers to be removed and added again, which is slow
   * @param {string} style - New base layer URL
   */
  public setMapStyle(style: string): void {
    this.clearMap();
    this.map.setStyle(style);
  }

  /**
   * Update the map view angle
   * @param {number} pitch
   * @param {number} bearing
   */
  public setPitchBearing(pitch?: number, bearing?: number): void {
    if (pitch) {
      this.map.setPitch(pitch);
    }

    if (bearing) {
      this.map.setBearing(bearing);
    }
  }

  /**
   * Change the 3D offset of the individual, or all sources that are currently at the input height
   * @param {number} height
   * @param {number} offset
   * @param {string} individual
   */
  public change3DOffset(height: number, offset: number, individual?: string): void {

    if (individual) {
      this.mapSources.forEach((layers, key) => {
        // If we have the individual's source, change its layers
        if (key === individual) {
          layers.forEach((layer) => {
            // You can't get properties that don't exist on layers,
            // that's an error not a null, because of course
            // But I'm sure it'll still return a null
            if (!layer.startsWith('label')) {
              const layerHeight = this.map.getPaintProperty(layer,
                'fill-extrusion-height');
              if (layerHeight) {
                this.map.setPaintProperty(layer,
                  'fill-extrusion-height',
                  layerHeight + offset);
                const layerBase = this.map.getPaintProperty(layer,
                  'fill-extrusion-base');
                if (layerBase) {
                  this.map.setPaintProperty(layer,
                    'fill-extrusion-base',
                    layerBase + offset);
                }
              }
            }
          });
        }
      });
    } else {
      //    Find all the individuals that have the same property
      //    For each layer, get its height
      this.mapSources.forEach((layers) => {
        layers.forEach((layer) => {
          const layerHeight = this.map.getPaintProperty(layer,
            'fill-extrusion-height');
          // If it matches the height of the layer, increase it
          if (layerHeight === height) {
            console.debug('Changing individuals');
            const layerBase = this.map.getPaintProperty(layer,
              'fill-extrusion-base');
            if (layerBase) {
              this.map.setPaintProperty(layer,
                'fill-extrusion-base',
                layerBase + offset);
            }
            this.map.setPaintProperty(layer,
              'fill-extrusion-height',
              layerHeight + offset);
          }
        });
      });
    }

  }

  private toggleSourceVisibility(source: string, setVisible: boolean, individual?: string): void {
    const layers = this.mapSources.get(source);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          // If we're filtering a layer and not a source,
          // set a filter to remove the individual
          if (individual) {
            // If we're setting the layer visible again,
            // remove it from the list and update the filter
            const filteredID = TrestleMapComponent.buildFilterID(individual);
            if (setVisible) {
              const idx = this.filteredIDs
                .indexOf(filteredID);
              if (idx > -1) {
                this.filteredIDs.splice(idx, 1);
              }
              //   If we're setting the layer invisible,
              // add the individual to the list of filtered IDs
            } else {
              console.debug('Removing individual %s from layer %s',
                individual, layer);
              this.filteredIDs.push(filteredID);
            }
            // If we have items to filter, add them,
            // otherwise remove the filter
            if (this.filteredIDs.length > 0) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              const filterValues = ['!in', 'id'].concat(this.filteredIDs);

              console.debug('Filtered Features:', this.map.querySourceFeatures(source,
                {
                  sourceLayer: layer,
                  filter: filterValues
                }));
              console.debug('Setting filter of %O on layer:', filterValues, layer);
              this.map.setFilter(layer, filterValues);
            } else {
              console.debug('Removing filter from layer:', layer);
              (this.map as any).setFilter(layer, null);
            }
          } else {
            if (setVisible) {
              this.map.setLayoutProperty(layer, 'visibility', 'visible');
            } else {
              this.map.setLayoutProperty(layer, 'visibility', 'none');
            }
          }
        });
    }

  }

  private removeSource(source: MapSource | string): void {
    let sourceID;
    if (typeof source === 'string') {
      sourceID = source;
    } else {
      sourceID = source.id;
    }

    if (this.mapSources.has(sourceID)) {
      console.debug('Removing source %s from map', sourceID);
      // Remove all the layers for each source
      const layers = this.mapSources.get(sourceID);
      if (layers !== undefined) {
        layers
          .forEach((layer) => {
            this.map.removeLayer(layer);
          });
      }

      this.map.removeSource(sourceID);
      this.mapSources.delete(sourceID);
    }
  }

  private addSource(inputLayer: MapSource): void {
    console.debug('Adding source data:', inputLayer.data);

    // Merge the new source with the default layers
    // But only if we don't already have that layer
    if (this.mapSources.has(inputLayer.id)) {
      console.debug('Map already has source:', inputLayer.id);
      return;
    }

    this.map.addSource(inputLayer.id, {
      type: 'geojson',
      data: inputLayer.data
    });

    const attributeLayers: string[] = [];

    // If it's a 3D layer, add the extrusion, otherwise add the normal layers
    if (TrestleMapComponent.is3D(inputLayer)) {
      console.debug('Adding 3D layer:', inputLayer.extrude);
      this.map.addLayer(inputLayer.extrude);
      attributeLayers.push(inputLayer.extrude.id);
    } else {
      // Add fill layer
      const fillID = inputLayer.id + '-fill';
      this.map.addLayer({
        id: fillID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 0.7,
        } as FillPaint)
      });
      // Add polygon line changes
      const lineId = inputLayer.id + '-line';
      this.map.addLayer({
        id: lineId,
        type: 'line',
        source: inputLayer.id,
        paint: {
          'line-color': 'white',
          'line-width': 2
        }
      });
      // Add hover layer
      const hoverID = inputLayer.id + '-hover';
      this.map.addLayer({
        id: hoverID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 1,
          // Repaint the lines so that they're still visible
        } as FillPaint),
        filter: ['==', 'name', '']
      });
      attributeLayers.push(fillID, lineId, hoverID);
    }

    // Labels
    const labelField = inputLayer.labelField;
    const labelValue = inputLayer.labelValue;
    if (labelField || labelValue) {
      // If it's a collection for each entity, add the label
      const iData = inputLayer.data;
      if (TrestleMapComponent.isCollection(iData)) {
        // Collections only support field labels, since we need to know how to filter each one
        if (labelField == null) {
          throw new Error('Geometry collections only support label Fields, not values');
        }
        iData.features.forEach((feature) => {
          const labelLayerID = 'label-' + feature.id;
          // This is terrible, but so is the web, so who blinks first?
          // If we have a field, use it, otherwise, use the provided value
          const labelText: string = labelField ? (feature.properties as any)[(labelField as any)] : labelValue;
          this.map.addLayer({
            id: labelLayerID,
            type: 'symbol',
            source: inputLayer.id,
            layout: {
              // For the text field, if the label function exists, call it, otherwise just place the label
              'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
              'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
              'text-size': 11,
              'text-transform': 'uppercase',
              'text-letter-spacing': 0.05,
              'text-offset': [0, 1.5],
              'text-allow-overlap': true
              // "text-ignore-placement": true
            },
            paint: {
              'text-color': '#202',
              'text-halo-color': '#fff',
              'text-halo-width': 2
            },
            filter: ['==', labelField, labelText]
          });
          attributeLayers.push(labelLayerID);
        });
      } else {
        const labelLayerID = 'label-' + iData.id;
        // This is terrible, but so is the web, so who blinks first?
        const labelText: string = labelField ? (iData.properties as any)[(labelField as any)] : labelValue;
        this.map.addLayer({
          id: labelLayerID,
          type: 'symbol',
          source: inputLayer.id,
          layout: {
            // For the text field, if the label function exists, call it, otherwise just place the label
            'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
            'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
            'text-size': 11,
            'text-transform': 'uppercase',
            'text-letter-spacing': 0.05,
            'text-offset': [0, 1.5]
          },
          paint: {
            'text-color': '#202',
            'text-halo-color': '#fff',
            'text-halo-width': 2
          }
        });
        attributeLayers.push(labelLayerID);
      }
    }
    // Add the map sources
    this.mapSources.set(inputLayer.id, attributeLayers);

    //    Center map
    if (this.centerMapOnLoad.getValue()) {
      this.centerMap(inputLayer.data);
    }
  }

  private layerClick = (e: MapMouseEvent): void => {
    console.debug('Clicked:', e);
    // FIXME(nrobison): Get rid of this type cast.
    // Get all the fill fillLayers
    let fillLayers: string[] = [];
    this.mapSources.forEach((values) => {
      fillLayers = fillLayers
        .concat((values
          // If we have a clickLayerSuffix, filter on that, otherwise, just find the fill Â¬layers
          .filter((val) => val.includes(this.clickLayerSuffix ? this.clickLayerSuffix : '-fill'))));
    });
    console.debug('Querying on fillLayers:', fillLayers);
    const features: any[] = this.map.queryRenderedFeatures(e.point, {
      layers: fillLayers
    });
    // Set the hover filter using either the provided id field, or a default property
    console.debug('Filtering with data:', this.data);
    // const idField = this.data.idField === undefined ? "id" : this.data.idField;
    const idField = 'id';
    console.debug('Accessing ID field:', idField);

    // If we don't filter on anything, deselect it all
    if (!this.multiSelect && !(features.length > 0)) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((layers) => {
        hoverLayers = hoverLayers
          .concat(layers
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting', hoverLayers);
      hoverLayers.forEach((layer) => {
        this.map.setFilter(layer, ['==', idField, '']);
      });
      return;
    }
    console.debug('Filtered features', features);

    const feature: any = features[0];
    let layerID = features[0].layer.id;
    // Emit the clicked layer
    const featureID = feature.properties[idField];
    this.clicked.emit(featureID);
    layerID = layerID.replace('-fill', '');
    console.debug('Filtering on layer:', layerID + '-hover');
    this.map.setFilter(layerID + '-hover', ['==', idField, featureID]);
    // If multi-select is not enabled, deselect everything else
    if (!this.multiSelect) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((values) => {
        hoverLayers = hoverLayers
          .concat(values
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting:', hoverLayers);
      // Add hover back to the layerID, otherwise nothing will match
      layerID = layerID + '-hover';
      hoverLayers
        .forEach((layer) => {
          if (layer !== layerID) {
            this.map.setFilter(layer, ['==', idField, '']);
          }
        });
    }
  };


  private mouseOver = (e: MapMouseEvent): void => {
    console.debug('Moused over:', e);
  };

  private mouseOut = (e: MapMouseEvent): void => {
    console.debug('Mouse out:', e);
  };

  private moveHandler = () => {
    this.mapBounds.emit(this.map.getBounds());
  };

  private setupDefaults(): void {
    this.baseConfig = {
      container: 'map',
      style: 'mapbox://styles/mapbox/light-v9',
      center: {lng: 32.3558991, lat: -25.6854313},
      zoom: 8,
      accessToken: 'pk.eyJ1IjoibnJvYmlzb24iLCJhIjoiY2ozdDd5dmd2MDA3bTMxcW1kdHZrZ3ppMCJ9.YcJMRphQAfmZ0H8X9HnoKA'
    };
  }

  private static is3D(x: any): x is I3DMapSource {
    return (x as I3DMapSource).extrude !== undefined;
  }

  private static isGeoJSON(x: any): x is GeoJSONDataSource {
    return (x as GeoJSONSource).type === 'geojson';
  }

  private static isCollection(x: any): x is FeatureCollection<GeometryObject> {
    return (x as FeatureCollection<GeometryObject>).type === 'FeatureCollection';
  }

  private static buildFilterID(individual: string): string {
    console.debug('Filtering:', individual);
    return TrestleIndividual.filterID(individual)
      .replace(/-/g, ' ')
      .replace(':', '-');
  }
}

import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChange} from "@angular/core";
import {Feature, FeatureCollection, GeometryObject} from "geojson";
import {BehaviorSubject, Subject} from "rxjs";
import {TrestleIndividual} from "../../shared/individual/TrestleIndividual/trestle-individual";
import extent from "@mapbox/geojson-extent";
import {
  FillExtrusionLayer,
  FillLayer,
  FillPaint,
  GeoJSONSource,
  GeoJSONSourceRaw,
  ImageSource,
  LineLayer,
  LngLatBounds,
  Map as MapboxMap,
  MapboxOptions,
  MapMouseEvent,
  RasterSource,
  VectorSource,
  VideoSource
} from "mapbox-gl";

export interface IMapFillLayer extends FillLayer {
  type: 'fill';
}

export interface IMapLineLayer extends LineLayer {
  type: 'line';
}

export interface IMapHoverLayer extends FillLayer {
  type: 'fill';
  filter: ['==', 'name', ''];
}

export interface ITrestleMapLayers {
  fill?: IMapFillLayer;
  line?: IMapLineLayer;
  hover?: IMapHoverLayer;
}

export interface ITrestleMapSource {
  id: string;
  idField?: string;
  data: FeatureCollection<GeometryObject> | Feature<GeometryObject>;
  layers?: ITrestleMapLayers;
  labelField?: string;
  labelFunction?: (label: string) => string;
  labelValue?: string;
}

export interface I3DMapSource extends ITrestleMapSource {
  extrude: FillExtrusionLayer;
}

interface GeoJSONDataSource extends GeoJSONSource {
  _data: Feature<GeometryObject> | FeatureCollection<GeometryObject>;
}

export interface IMapAttributeChange {
  individual: string;
  changes: Array<{ attribute: string, value: any }>;
  // attribute: string;
  // value: any;
}

export interface IMapEventHandler {
  event: MapEvent;
  handler: (event: any) => void;
}

export type MapEvent = 'mousemove' | 'mouseleave' | 'click' | 'moveend';
export type MapSource = I3DMapSource | ITrestleMapSource;
export type MapBoxSource = GeoJSONSource | VectorSource | RasterSource | ImageSource | VideoSource | GeoJSONSourceRaw;

@Component({
  selector: 'trestle-map',
  templateUrl: './trestle-map.component.html',
  styleUrls: ['./trestle-map.component.scss']
})

export class TrestleMapComponent implements OnInit, OnChanges {

  @Input() public data: MapSource;
  @Input() public single: boolean;
  @Input() public multiSelect: boolean;
  @Input() public clickLayerSuffix: string;
  @Input() public zoomOnLoad?: boolean;
  @Input() public config?: MapboxOptions;
  @Input() public dataChanges: Subject<MapSource | undefined>;
  @Input() public attributeChanges: Subject<IMapAttributeChange>;
  @Input() public handlers: IMapEventHandler[];
  @Output() public mapBounds: EventEmitter<LngLatBounds> = new EventEmitter();
  @Output() public clicked: EventEmitter<string> = new EventEmitter();
  private centerMapOnLoad: BehaviorSubject<boolean>;
  private baseConfig: MapboxOptions;
  private map: MapboxMap;
  private mapSources: Map<string, string[]>;
  // This has to be integers, in order to match against the numeric IDs
  private filteredIDs: string[];
  private previousValue: MapSource;

  constructor() {

    this.mapSources = new Map();
    this.filteredIDs = [];

    //    Set defaults
    this.setupDefaults();
  }

  public ngOnInit(): void {
    if (this.zoomOnLoad === undefined) {
      this.centerMapOnLoad = new BehaviorSubject(true);
    } else {
      this.centerMapOnLoad = new BehaviorSubject(this.zoomOnLoad);
    }

    console.debug('Creating map, ' +
      'singleSelect?', this.single,
      'mulitSelect?', this.multiSelect,
      'zoom?', this.centerMapOnLoad.getValue());

    // Merge the map configs together
    const mergedConfig = Object.assign(this.baseConfig, this.config);
    this.map = new MapboxMap(mergedConfig);

    this.map.on('click', this.layerClick);
    this.map.on('mouseover', this.mouseOver);
    this.map.on('mouseleave', this.mouseOut);
    this.map.on('moveend', this.moveHandler);

    // Register any additional handlers
    if (this.handlers) {
      this.handlers.forEach((h) => {
        this.map.on(h.event, h.handler);
      });
    }

    // Once the map is loaded, setup the subscriptions
    this.map.on('style.load', () => {
      // If it's null, create a dummy one
      if (this.dataChanges === undefined) {
        console.debug('Creating dummy data changes subscription');
        this.dataChanges = new Subject();
      }
      console.debug('Subscribing to data changes observable');
      this.dataChanges.subscribe((data) => {
        console.debug('Map has new data to load', data);
        if (data !== undefined) {
          if (this.single && this.previousValue) {
            this.removeSource(this.previousValue);
          }
          this.addSource(data);
          this.previousValue = data;
        }
      });

      if (this.attributeChanges === undefined) {
        console.debug('Creating dummy attribute subscription');
        this.attributeChanges = new Subject();
      }
      console.debug('Subscribing to attribute changes observable');
      this.attributeChanges.subscribe((change) => {
        this.changeIndividualAttribute(change);
      });
      this.mapBounds.emit(this.map.getBounds());
    });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    // Individual changes
    const inputChanges = changes['data'];
    if (inputChanges != null
      && !inputChanges.isFirstChange()
      && (inputChanges.currentValue !== inputChanges.previousValue)) {
      console.debug('New change, updating', inputChanges);
      if (inputChanges.previousValue != null && this.single) {
        // mapChanges.previousValue= inputChanges.previousValue;
        this.removeSource(inputChanges.previousValue);
      }
      // this.dataChanges.next(mapChanges);
      this.addSource(inputChanges.currentValue);
    }

    //    Zoom On Load changes
    const zoomChanges = changes['zoomOnLoad'];
    if (zoomChanges != null
      && !zoomChanges.isFirstChange()) {
      console.debug('Changing zoom value');
      this.centerMapOnLoad.next(zoomChanges.currentValue);
    }

    // Event handlers
    // const handlerChanges = changes["handlers"];
    // if (handlerChanges != null
    //     && (handlerChanges.currentValue !== handlerChanges.previousValue)) {
    //     console.debug("Registering event handlers");
    //     (handlerChanges.currentValue as IMapEventHandler[]).forEach((e) => {
    //         this.map.on(e.event, e.handler);
    //     });
    // }
  }

  /**
   * Get the current map bounds
   * @returns {mapboxgl.LngLatBounds}
   */
  public getMapBounds(): LngLatBounds {
    return this.map.getBounds();
  }

  /**
   * Remove individual from the map, which clears the source and linked layers
   * @param {string} individual
   */
  public removeIndividual(individual: string): void {
    console.debug('Removing selection %s from the map', individual);

    // Is the data a source?
    if (this.mapSources.has(individual)) {
      this.removeSource(individual);
    } else {
      //    Otherwise find the matching layer and remove it
    }
  }

  /**
   * Modify the specified attribute for the given individual
   * @param {IMapAttributeChange} attributeChange
   */
  public changeIndividualAttribute(attributeChange: IMapAttributeChange): void {
    console.debug('Changing attribute:', attributeChange);

    //    Try to get the source first
    const layers = this.mapSources.get(attributeChange.individual);
    if (layers !== undefined) {
      console.debug('Changing layers:', layers);
      layers.filter((layer) => !layer.startsWith('label'))
        .forEach((layer) => {
          attributeChange.changes.forEach((change) => {
            this.map.setPaintProperty(layer, change.attribute, change.value);
          });

        });
    }
    //    I don't think we can do this with individuals yet, but maybe?
  }

  /**
   * Toggle the visibility of the layers for the given individual
   * This changes all registered layers for the given source
   * @param {string} individual
   * @param {boolean} setVisible
   */
  public toggleIndividualVisibility(individual: string, setVisible: boolean): void {
    console.debug('setting visible?', setVisible);
    //    See if the data is a source
    const layers = this.mapSources.get(individual);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          if (setVisible) {
            this.map.setLayoutProperty(layer, 'visibility', 'visible');
          } else {
            this.map.setLayoutProperty(layer, 'visibility', 'none');
          }
        });
      //    If not, figure out which layers have the data
    } else {
      console.debug('Looking for matching individual id:',
        TrestleMapComponent.buildFilterID(individual));
      for (const source of Array.from(this.mapSources.keys())) {
        const mapSource = this.map.getSource(source);
        if (TrestleMapComponent.isGeoJSON(mapSource)) {
          console.debug('Checking source:', mapSource);
          console.debug('Has data:', (mapSource as any)._data);
          const data = mapSource._data;
          // If it's a feature collection, dive into it
          if (TrestleMapComponent.isCollection(data)) {
            for (const feature of data.features) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              if ((feature.properties as any).id === TrestleMapComponent
                .buildFilterID(individual)) {
                console.debug('Source %s matches individual %s',
                  source, individual);
                this.toggleSourceVisibility(source, setVisible, individual);
                break;
              }
            }
          } else {
            // TODO(nickrobison): This will fail if the features don't have an ID property
            if ((data.properties as any).id === TrestleMapComponent
              .buildFilterID(individual)) {
              console.debug('Source feature %s matches individual %s',
                source, individual);
              this.toggleSourceVisibility(source, setVisible, individual);
              break;
            }
          }
        }
      }
    }
  }

  /**
   * Clear all the layers and sources from the map
   */
  public clearMap(): void {
    console.debug('Clearing map');
    this.mapSources.forEach((_, source) => {
      console.debug('removing:', source);
      this.removeSource(source);
    });
  }

  /**
   * Center the map on the given input set, computes the bounding box if one doesn't exist
   * @param {FeatureCollection<GeometryObject> | Feature<GeometryObject>} geom
   */
  public centerMap(geom: FeatureCollection<GeometryObject> | Feature<GeometryObject>): void {
    // We have to lock the map in order to avoid sending out a notice that the move happened.
    if (geom.bbox) {
      // FIXME(nrobison): This is garbage. Fix it.
      this.map.fitBounds(LngLatBounds.convert(geom.bbox as any));
    } else {
      const bbox = extent(geom);
      if (bbox) {
        // This works, but it seems to confuse the type system, so any for the win!
        this.map.fitBounds(LngLatBounds.convert(bbox as any));
      }
    }
  }

  /**
   * Change the map base layer
   * WARNING: This will cause all the sources and layers to be removed and added again, which is slow
   * @param {string} style - New base layer URL
   */
  public setMapStyle(style: string): void {
    this.clearMap();
    this.map.setStyle(style);
  }

  /**
   * Update the map view angle
   * @param {number} pitch
   * @param {number} bearing
   */
  public setPitchBearing(pitch?: number, bearing?: number): void {
    if (pitch) {
      this.map.setPitch(pitch);
    }

    if (bearing) {
      this.map.setBearing(bearing);
    }
  }

  /**
   * Change the 3D offset of the individual, or all sources that are currently at the input height
   * @param {number} height
   * @param {number} offset
   * @param {string} individual
   */
  public change3DOffset(height: number, offset: number, individual?: string): void {

    if (individual) {
      this.mapSources.forEach((layers, key) => {
        // If we have the individual's source, change its layers
        if (key === individual) {
          layers.forEach((layer) => {
            // You can't get properties that don't exist on layers,
            // that's an error not a null, because of course
            // But I'm sure it'll still return a null
            if (!layer.startsWith('label')) {
              const layerHeight = this.map.getPaintProperty(layer,
                'fill-extrusion-height');
              if (layerHeight) {
                this.map.setPaintProperty(layer,
                  'fill-extrusion-height',
                  layerHeight + offset);
                const layerBase = this.map.getPaintProperty(layer,
                  'fill-extrusion-base');
                if (layerBase) {
                  this.map.setPaintProperty(layer,
                    'fill-extrusion-base',
                    layerBase + offset);
                }
              }
            }
          });
        }
      });
    } else {
      //    Find all the individuals that have the same property
      //    For each layer, get its height
      this.mapSources.forEach((layers) => {
        layers.forEach((layer) => {
          const layerHeight = this.map.getPaintProperty(layer,
            'fill-extrusion-height');
          // If it matches the height of the layer, increase it
          if (layerHeight === height) {
            console.debug('Changing individuals');
            const layerBase = this.map.getPaintProperty(layer,
              'fill-extrusion-base');
            if (layerBase) {
              this.map.setPaintProperty(layer,
                'fill-extrusion-base',
                layerBase + offset);
            }
            this.map.setPaintProperty(layer,
              'fill-extrusion-height',
              layerHeight + offset);
          }
        });
      });
    }

  }

  private toggleSourceVisibility(source: string, setVisible: boolean, individual?: string): void {
    const layers = this.mapSources.get(source);
    if (layers !== undefined) {
      console.debug('Has layers:', layers);
      //    If we're a source, turn off all the layers
      layers
        .forEach((layer) => {
          // If we're filtering a layer and not a source,
          // set a filter to remove the individual
          if (individual) {
            // If we're setting the layer visible again,
            // remove it from the list and update the filter
            const filteredID = TrestleMapComponent.buildFilterID(individual);
            if (setVisible) {
              const idx = this.filteredIDs
                .indexOf(filteredID);
              if (idx > -1) {
                this.filteredIDs.splice(idx, 1);
              }
              //   If we're setting the layer invisible,
              // add the individual to the list of filtered IDs
            } else {
              console.debug('Removing individual %s from layer %s',
                individual, layer);
              this.filteredIDs.push(filteredID);
            }
            // If we have items to filter, add them,
            // otherwise remove the filter
            if (this.filteredIDs.length > 0) {
              // TODO(nickrobison): This will fail if the features don't have an ID property
              const filterValues = ['!in', 'id'].concat(this.filteredIDs);

              console.debug('Filtered Features:', this.map.querySourceFeatures(source,
                {
                  sourceLayer: layer,
                  filter: filterValues
                }));
              console.debug('Setting filter of %O on layer:', filterValues, layer);
              this.map.setFilter(layer, filterValues);
            } else {
              console.debug('Removing filter from layer:', layer);
              (this.map as any).setFilter(layer, null);
            }
          } else {
            if (setVisible) {
              this.map.setLayoutProperty(layer, 'visibility', 'visible');
            } else {
              this.map.setLayoutProperty(layer, 'visibility', 'none');
            }
          }
        });
    }

  }

  private removeSource(source: MapSource | string): void {
    let sourceID;
    if (typeof source === 'string') {
      sourceID = source;
    } else {
      sourceID = source.id;
    }

    if (this.mapSources.has(sourceID)) {
      console.debug('Removing source %s from map', sourceID);
      // Remove all the layers for each source
      const layers = this.mapSources.get(sourceID);
      if (layers !== undefined) {
        layers
          .forEach((layer) => {
            this.map.removeLayer(layer);
          });
      }

      this.map.removeSource(sourceID);
      this.mapSources.delete(sourceID);
    }
  }

  private addSource(inputLayer: MapSource): void {
    console.debug('Adding source data:', inputLayer.data);

    // Merge the new source with the default layers
    // But only if we don't already have that layer
    if (this.mapSources.has(inputLayer.id)) {
      console.debug('Map already has source:', inputLayer.id);
      return;
    }

    this.map.addSource(inputLayer.id, {
      type: 'geojson',
      data: inputLayer.data
    });

    const attributeLayers: string[] = [];

    // If it's a 3D layer, add the extrusion, otherwise add the normal layers
    if (TrestleMapComponent.is3D(inputLayer)) {
      console.debug('Adding 3D layer:', inputLayer.extrude);
      this.map.addLayer(inputLayer.extrude);
      attributeLayers.push(inputLayer.extrude.id);
    } else {
      // Add fill layer
      const fillID = inputLayer.id + '-fill';
      this.map.addLayer({
        id: fillID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 0.7,
        } as FillPaint)
      });
      // Add polygon line changes
      const lineId = inputLayer.id + '-line';
      this.map.addLayer({
        id: lineId,
        type: 'line',
        source: inputLayer.id,
        paint: {
          'line-color': 'white',
          'line-width': 2
        }
      });
      // Add hover layer
      const hoverID = inputLayer.id + '-hover';
      this.map.addLayer({
        id: hoverID,
        type: 'fill',
        source: inputLayer.id,
        paint: ({
          'fill-color': '#627BC1',
          'fill-opacity': 1,
          // Repaint the lines so that they're still visible
        } as FillPaint),
        filter: ['==', 'name', '']
      });
      attributeLayers.push(fillID, lineId, hoverID);
    }

    // Labels
    const labelField = inputLayer.labelField;
    const labelValue = inputLayer.labelValue;
    if (labelField || labelValue) {
      // If it's a collection for each entity, add the label
      const iData = inputLayer.data;
      if (TrestleMapComponent.isCollection(iData)) {
        // Collections only support field labels, since we need to know how to filter each one
        if (labelField == null) {
          throw new Error('Geometry collections only support label Fields, not values');
        }
        iData.features.forEach((feature) => {
          const labelLayerID = 'label-' + feature.id;
          // This is terrible, but so is the web, so who blinks first?
          // If we have a field, use it, otherwise, use the provided value
          const labelText: string = labelField ? (feature.properties as any)[(labelField as any)] : labelValue;
          this.map.addLayer({
            id: labelLayerID,
            type: 'symbol',
            source: inputLayer.id,
            layout: {
              // For the text field, if the label function exists, call it, otherwise just place the label
              'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
              'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
              'text-size': 11,
              'text-transform': 'uppercase',
              'text-letter-spacing': 0.05,
              'text-offset': [0, 1.5],
              'text-allow-overlap': true
              // "text-ignore-placement": true
            },
            paint: {
              'text-color': '#202',
              'text-halo-color': '#fff',
              'text-halo-width': 2
            },
            filter: ['==', labelField, labelText]
          });
          attributeLayers.push(labelLayerID);
        });
      } else {
        const labelLayerID = 'label-' + iData.id;
        // This is terrible, but so is the web, so who blinks first?
        const labelText: string = labelField ? (iData.properties as any)[(labelField as any)] : labelValue;
        this.map.addLayer({
          id: labelLayerID,
          type: 'symbol',
          source: inputLayer.id,
          layout: {
            // For the text field, if the label function exists, call it, otherwise just place the label
            'text-field': inputLayer.labelFunction ? inputLayer.labelFunction(labelText) : labelText,
            'text-font': ['Open Sans Bold', 'Arial Unicode MS Bold'],
            'text-size': 11,
            'text-transform': 'uppercase',
            'text-letter-spacing': 0.05,
            'text-offset': [0, 1.5]
          },
          paint: {
            'text-color': '#202',
            'text-halo-color': '#fff',
            'text-halo-width': 2
          }
        });
        attributeLayers.push(labelLayerID);
      }
    }
    // Add the map sources
    this.mapSources.set(inputLayer.id, attributeLayers);

    //    Center map
    if (this.centerMapOnLoad.getValue()) {
      this.centerMap(inputLayer.data);
    }
  }

  private layerClick = (e: MapMouseEvent): void => {
    console.debug('Clicked:', e);
    // FIXME(nrobison): Get rid of this type cast.
    // Get all the fill fillLayers
    let fillLayers: string[] = [];
    this.mapSources.forEach((values) => {
      fillLayers = fillLayers
        .concat((values
          // If we have a clickLayerSuffix, filter on that, otherwise, just find the fill Â¬layers
          .filter((val) => val.includes(this.clickLayerSuffix ? this.clickLayerSuffix : '-fill'))));
    });
    console.debug('Querying on fillLayers:', fillLayers);
    const features: any[] = this.map.queryRenderedFeatures(e.point, {
      layers: fillLayers
    });
    // Set the hover filter using either the provided id field, or a default property
    console.debug('Filtering with data:', this.data);
    // const idField = this.data.idField === undefined ? "id" : this.data.idField;
    const idField = 'id';
    console.debug('Accessing ID field:', idField);

    // If we don't filter on anything, deselect it all
    if (!this.multiSelect && !(features.length > 0)) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((layers) => {
        hoverLayers = hoverLayers
          .concat(layers
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting', hoverLayers);
      hoverLayers.forEach((layer) => {
        this.map.setFilter(layer, ['==', idField, '']);
      });
      return;
    }
    console.debug('Filtered features', features);

    const feature: any = features[0];
    let layerID = features[0].layer.id;
    // Emit the clicked layer
    const featureID = feature.properties[idField];
    this.clicked.emit(featureID);
    layerID = layerID.replace('-fill', '');
    console.debug('Filtering on layer:', layerID + '-hover');
    this.map.setFilter(layerID + '-hover', ['==', idField, featureID]);
    // If multi-select is not enabled, deselect everything else
    if (!this.multiSelect) {
      let hoverLayers: string[] = [];
      this.mapSources.forEach((values) => {
        hoverLayers = hoverLayers
          .concat(values
            .filter((val) => val.includes('-hover')));
      });
      console.debug('Deselecting:', hoverLayers);
      // Add hover back to the layerID, otherwise nothing will match
      layerID = layerID + '-hover';
      hoverLayers
        .forEach((layer) => {
          if (layer !== layerID) {
            this.map.setFilter(layer, ['==', idField, '']);
          }
        });
    }
  };


  private mouseOver = (e: MapMouseEvent): void => {
    console.debug('Moused over:', e);
  };

  private mouseOut = (e: MapMouseEvent): void => {
    console.debug('Mouse out:', e);
  };

  private moveHandler = () => {
    this.mapBounds.emit(this.map.getBounds());
  };

  private setupDefaults(): void {
    this.baseConfig = {
      container: 'map',
      style: 'mapbox://styles/mapbox/light-v9',
      center: {lng: 32.3558991, lat: -25.6854313},
      zoom: 8,
      accessToken: 'pk.eyJ1IjoibnJvYmlzb24iLCJhIjoiY2ozdDd5dmd2MDA3bTMxcW1kdHZrZ3ppMCJ9.YcJMRphQAfmZ0H8X9HnoKA'
    };
  }

  private static is3D(x: any): x is I3DMapSource {
    return (x as I3DMapSource).extrude !== undefined;
  }

  private static isGeoJSON(x: any): x is GeoJSONDataSource {
    return (x as GeoJSONSource).type === 'geojson';
  }

  private static isCollection(x: any): x is FeatureCollection<GeometryObject> {
    return (x as FeatureCollection<GeometryObject>).type === 'FeatureCollection';
  }

  private static buildFilterID(individual: string): string {
    console.debug('Filtering:', individual);
    return TrestleIndividual.filterID(individual)
      .replace(/-/g, ' ')
      .replace(':', '-');
  }
}

import {Injectable} from '@angular/core';
import {LngLatBounds} from 'mapbox-gl';
import {FeatureCollection, GeometryObject, MultiPolygon, Polygon} from 'geojson';
import {Moment} from 'moment';
import {fromEvent, Observable, Subscriber, throwError} from 'rxjs';
import {filter, flatMap, map} from 'rxjs/operators';
import {HttpClient} from '@angular/common/http';
import {environment} from '../../../environments/environment';
import {ITrestleIndividual, TrestleIndividual} from '../../shared/individual/TrestleIndividual/trestle-individual';

export type wktType = 'POINT' |
  'MULTIPOINT' |
  'LINESTRING' |
  'MULTILINESTRING' |
  'POLYGON' |
  'MULTIPOLYGON';

export type wktValue = LngLatBounds | GeometryObject;

export interface IContributionReport {
  object: any;
  area: number;
  contributingParts: IContributionPart[];
}

export interface IComparisonReport {
  union: IContributionReport | null;
  reports: ISpatialComparisonReport[];
}

export interface IContributionPart {
  object: any;
  contribution: number;
}

export interface ISpatialComparisonReport {
  objectAID: string;
  objectBID: string;
  relations: string[];
  equality?: number;
  spatialOverlap?: string;
  spatialOverlapPercentage?: number;
}

interface IIntersectionBody {
  dataset: string;
  geojson: Polygon | MultiPolygon;
  buffer: number;
  validAt?: string;
  databaseAt?: string;
}

interface ICompareBody {
  compare: string;
  compareAgainst: string[];
}

export interface IMapWorkerRequest {
  id: number;
  response: object[];
}

export interface IMapWorkerResponse {
  id: number;
  geom: FeatureCollection<GeometryObject>;
}


@Injectable()
export class MapService {
  private worker: Worker;
  private workerStream: Observable<IMapWorkerResponse>;
  private readonly baseURL;

  constructor(private http: HttpClient) {
    //    Create the worker and register a stream for the results
    this.worker = new Worker("/projector");
    this.workerStream = fromEvent(this.worker, 'message')
      .pipe(map((m: MessageEvent) => (m.data as IMapWorkerResponse)));
    this.baseURL = environment.baseUrl;
  }

  /**
   * Before a spatio-temporal interesction for the given WKT bounding box, returning a GeoJSON Feature Collection
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  public stIntersect(dataset: string,
                     wkt: wktValue,
                     validTime: Moment,
                     dbTime?: Moment,
                     buffer: number = 0): Observable<FeatureCollection<GeometryObject>> {
    console.debug('Intersecting at:', wkt, validTime.toISOString());

    if (wkt === null || wkt === undefined) {
      return throwError('Intersection boundary cannot be empty');
    }

    const postBody: IIntersectionBody = {
      dataset,
      validAt: validTime.toISOString(),
      databaseAt: new Date().toISOString(),
      geojson: MapService.normalizeToGeoJSON(wkt),
      buffer
    };
    console.debug('Post body', postBody);
    return this.http.post(this.baseURL + '/visualize/intersect', postBody)
      .pipe(flatMap(this.parseToGeoJSONWorker))
  }

  /**
   * Performa a spatio-temporal intersection for the given WKT boundary, returning the results as a list of {TrestleIndividual}
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<TrestleIndividual[]>}
   */
  public stIntersectIndividual(dataset: string,
                               wkt: wktValue,
                               validTime?: Moment,
                               dbTime?: Moment,
                               buffer: number = 0): Observable<TrestleIndividual[]> {
    const postBody: IIntersectionBody = {
      dataset,
      buffer,
      geojson: MapService.normalizeToGeoJSON(wkt)
    };

    if (validTime) {
      postBody.validAt = validTime.toISOString();
    }

    if (dbTime) {
      postBody.databaseAt = dbTime.toISOString();
    }

    console.debug('Intersecting individuals with', postBody);

    return this.http.post(this.baseURL + '/individual/intersect-individuals', postBody)
      .pipe(map(MapService.parseResponseToIndividuals));
  }

  /**
   * Perform a spatio-temporal comparison between the input object and the given set of comparison objects
   * @param {ICompareBody} request
   * @returns {Observable<IComparisonReport>}
   */
  public compareIndividuals(request: ICompareBody): Observable<IComparisonReport> {
    return this.http.post<IComparisonReport>(this.baseURL+ '/visualize/compare', request);
  }

  /**
   * Parses an input set of generic objects, by sending them to a web worker to do the interesting stuff
   * @param {object[]} objects
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  private parseToGeoJSONWorker = (objects: object[]): Observable<FeatureCollection<GeometryObject>> => {
    console.debug('Sending to worker');
    const id = new Date().getTime();
    const workerRequest: IMapWorkerRequest = {
      id,
      response: objects
    };

    //    Dispatch the event
    this.worker.postMessage(workerRequest);

    //    Subscribe to the event stream
    return new Observable((observer: Subscriber<FeatureCollection<GeometryObject>>) => {
      this.workerStream
        .pipe(filter(m => m.id === id))
        .subscribe((msg) => {
          console.debug('Has from worker:', msg);
          observer.next(msg.geom);
          observer.complete();
        });
    });
  };

  public static normalizeToGeoJSON(geom: wktValue): Polygon | MultiPolygon {
    if (MapService.isGeometryObject(geom)) {
      if (geom.type === 'Polygon') {
        return (geom as Polygon);
      } else if (geom.type === 'MultiPolygon') {
        return (geom as MultiPolygon);
      } else {
        console.error('Not correct geom', geom);
        throw new Error('Not correct geometry');
      }
    }
    return {
      type: 'Polygon',
      // need to return and array of bounds as an array of SW -> NW -> NE -> SE -> SW
      coordinates: [[geom.getSouthWest().toArray(),
        geom.getNorthWest().toArray(),
        geom.getNorthEast().toArray(),
        geom.getSouthEast().toArray(),
        geom.getSouthWest().toArray()]]
      // crs: {type: "name", properties: {name: "EPSG:4326"}}
    };
  }

  private static parseResponseToIndividuals(res: ITrestleIndividual[]): TrestleIndividual[] {
    console.debug('Intersected result from server:', res);
    return res
      .map((individual: ITrestleIndividual) => new TrestleIndividual(individual));
  }

  private static isGeometryObject(x: any): x is GeometryObject {
    return (x as GeometryObject).type !== undefined;
  }
}

