                               validTime?: Moment,
                               dbTime?: Moment,
                               buffer: number = 0): Observable<TrestleIndividual[]> {
    const postBody: IIntersectionBody = {
      dataset,
      buffer,
      geojson: MapService.normalizeToGeoJSON(wkt)
    };

    if (validTime) {
      postBody.validAt = validTime.toISOString();
    }

    if (dbTime) {
      postBody.databaseAt = dbTime.toISOString();
    }

    console.debug('Intersecting individuals with', postBody);

    return this.http.post(this.baseURL + '/individual/intersect-individuals', postBody)
      .pipe(map(MapService.parseResponseToIndividuals));
  }

  /**
   * Perform a spatio-temporal comparison between the input object and the given set of comparison objects
   * @param {ICompareBody} request
   * @returns {Observable<IComparisonReport>}
   */
  public compareIndividuals(request: ICompareBody): Observable<IComparisonReport> {
    return this.http.post<IComparisonReport>(this.baseURL+ '/visualize/compare', request);
  }

  /**
   * Parses an input set of generic objects, by sending them to a web worker to do the interesting stuff
   * @param {object[]} objects
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  private parseToGeoJSONWorker = (objects: object[]): Observable<FeatureCollection<GeometryObject>> => {
    console.debug('Sending to worker');
    const id = new Date().getTime();
    const workerRequest: IMapWorkerRequest = {
      id,
      response: objects
    };

    //    Dispatch the event
    this.worker.postMessage(workerRequest);

    //    Subscribe to the event stream
    return new Observable((observer: Subscriber<FeatureCollection<GeometryObject>>) => {
      this.workerStream
        .pipe(filter(m => m.id === id))
        .subscribe((msg) => {
          console.debug('Has from worker:', msg);
          observer.next(msg.geom);
          observer.complete();
        });
    });
  };

  public static normalizeToGeoJSON(geom: wktValue): Polygon | MultiPolygon {
    if (MapService.isGeometryObject(geom)) {
      if (geom.type === 'Polygon') {
        return (geom as Polygon);
      } else if (geom.type === 'MultiPolygon') {
        return (geom as MultiPolygon);
      } else {
        console.error('Not correct geom', geom);
        throw new Error('Not correct geometry');
      }
    }
    return {
      type: 'Polygon',
      // need to return and array of bounds as an array of SW -> NW -> NE -> SE -> SW
      coordinates: [[geom.getSouthWest().toArray(),
        geom.getNorthWest().toArray(),
        geom.getNorthEast().toArray(),
        geom.getSouthEast().toArray(),
        geom.getSouthWest().toArray()]]
      // crs: {type: "name", properties: {name: "EPSG:4326"}}
    };
  }

  private static parseResponseToIndividuals(res: ITrestleIndividual[]): TrestleIndividual[] {
    console.debug('Intersected result from server:', res);
    return res
      .map((individual: ITrestleIndividual) => new TrestleIndividual(individual));
  }

  private static isGeometryObject(x: any): x is GeometryObject {
    return (x as GeometryObject).type !== undefined;
  }
}

import {AfterViewInit, Component, ElementRef, Input, OnChanges, SimpleChange, ViewChild, ViewEncapsulation} from '@angular/core';
import {BehaviorSubject} from 'rxjs';
import {BaseType, select, Selection} from 'd3-selection';
import {scaleBand, scaleOrdinal, ScaleTime, scaleTime} from 'd3-scale';
import {axisBottom, axisLeft} from 'd3-axis';
import {schemeCategory10} from 'd3';
import {ID3Margin} from '../common';
import moment from 'moment';
import Base = moment.unitOfTime.Base;

export interface ITemporalEntity {
  label: string;
  start: Date;
  end?: Date;
  value: any;
}

export interface IIndividualHistory {
  entities: ITemporalEntity[];
}

@Component({
  selector: 'history-graph',
  templateUrl: './history-graph.component.html',
  styleUrls: ['./history-graph.component.scss'],
    encapsulation: ViewEncapsulation.None,
})
export class HistoryGraphComponent implements AfterViewInit, OnChanges {
  @ViewChild('graph') public element: ElementRef;
  @Input() public data: IIndividualHistory;
  @Input() public graphHeight: number;
  @Input() public minTime: Date;
  @Input() public maxTime: Date;
  private htmlElement: HTMLElement;
  private host: Selection<HTMLElement, ITemporalEntity, BaseType, ITemporalEntity>;
  private svg: Selection<BaseType, ITemporalEntity, BaseType, ITemporalEntity>;
  private width: number;
  private height: number;
  private margin: ID3Margin;
  private x: ScaleTime<number, number>;
  private dataChanges: BehaviorSubject<IIndividualHistory | undefined>;

  constructor() {
    this.dataChanges = new BehaviorSubject(undefined);
  }

  public ngAfterViewInit(): void {
    this.htmlElement = this.element.nativeElement;
    this.setupD3();
    this.dataChanges
      .subscribe((value) => {
        console.debug('Updating plot with:', this.data);
        if (value !== undefined) {
          this.plotData();
        }
      });
  }

  public ngOnChanges(changes: { [propKey: string]: SimpleChange }): void {
    const dataChange = changes['data'];
    if (dataChange != null && (dataChange.previousValue !== dataChange.currentValue)) {
      this.dataChanges.next(dataChange.currentValue);
    }
  }

  private plotData(): void {
    // Nuke everything, because I can't figure out the update pattern
    this.svg.selectAll('*').remove();
    this.x = scaleTime().range([0, this.width]);
    this.x.domain([this.minTime, this.maxTime]);
    this.svg
      .append('g')
      .attr('class', 'axis x-axis')
      .attr('transform', 'translate(0,' + this.height + ')')
      .call(axisBottom(this.x));

    //    Build the domain values
    console.debug('Building with data:', this.data);
    const entityNames = this.data.entities.map((d) => d.label);
    console.debug('Names:', entityNames);
    this.x = scaleTime().range([0, this.width]);
    this.x.domain([this.minTime, this.maxTime]);
    const y = scaleBand()
      .range([this.height, 0])
      .domain(entityNames);
    console.debug('Y values', y.range());
    console.debug('Y values', y.domain());

    const z = scaleOrdinal(schemeCategory10)
      .domain(entityNames);

    // Build the lane lines
    this.svg.selectAll('.laneLine')
      .data(this.data.entities.map((entity) => entity.label))
      .enter().append('line')
      .attr('class', 'laneLine')
      .attr('x1', 0)
      .attr('y1', (d) => y(d) || 0)
      .attr('x2', this.width)
      .attr('y2', (d) => y(d) || 0);

    //    Build the Y-Axis
    const ySelection = this.svg.selectAll('g.y-axis');
    if (ySelection.empty()) {
      this.svg
        .append('g')
        .attr('class', 'axis y-axis')
        .call(axisLeft(y));
    } else {
      ySelection
        .call(axisLeft(y));
    }

    // And the X-Axis
    this.svg.select('.x-axis')
      .call(axisBottom(this.x));

    //    Add the data
    const mainItems = this.svg.selectAll<SVGRectElement, Base>('.fact')
      .data(this.data.entities, (entity: ITemporalEntity) => entity.label);

    mainItems
      .enter()
      .append('rect')
      .attr('class', 'fact')
      .attr('x', (d) => this.normalizeAxis('x', this.x(d.start)))
      .attr('y', (d) => y(d.label) || 0)
      .attr('width',
        (d) => {
          const end = this.normalizeAxis('x',
            this.x(this.maybeDate(d.end)));
          const start = this.normalizeAxis('x',
            this.x(d.start));
          return end - start;
        })
      .attr('height', () => y.bandwidth())
      // .style("fill", (d: TrestleFact) => z(d.getName()))
      .style('fill', (d) => z(d.label))
      .style('fill-opacity', 0.7)
      .merge(mainItems);

    // Labels
    const mainLabels = this.svg.selectAll<SVGTextElement, BaseType>('.mainLabels')
      .data(this.data.entities, (d: ITemporalEntity) => d.label);

    mainLabels
      .enter()
      .append('text')
      .text((d) => HistoryGraphComponent.parseValue(d.value))
      .attr('class', 'mainLabels')
      .attr('x', (d) => {
        const end = d.end;
        const start = d.start;
        const width = this.x(this.maybeDate(end)) - this.x(start);
        return this.x(start) + width / 2;
      })
      .attr('y', (d) => (y(d.label) || 0) + y.bandwidth() - 5)
      .attr('text-anchor', 'middle')
      .attr('dy', '.1ex')
      .merge(mainLabels);

    mainItems.exit().remove();
    mainLabels.exit().remove();
  }

  private maybeDate(date: string | Date | undefined): Date {
    if (date instanceof Date) {
      return date;
    }
    if (date === undefined) {
      return this.minTime;
    }
    if (date === '') {
      return this.maxTime;
    }
    return new Date(date);
  }

  private static parseValue(value: string | number): string {
    if (typeof value === 'number') {
      return value.toString();
    }
    if (value.length > 20) {
      return value.substring(0, 20) + '...';
    }
    return value;
  }

  private normalizeAxis(axis: 'x' | 'y', value: number): number {
    // Normalize X Axis
    if (axis === 'x') {
      if (value < 0) {
        return 0;
      }
      if (value > this.width) {
        return this.width;
      }
      return value;
    } else {
      if (value < 0) {
        return 0;
      }
      if (value > this.height) {
        return this.height;
      }
      return value;
    }
  }

  private setupD3(): void {
    this.host = select<HTMLElement, ITemporalEntity>(this.htmlElement);
    this.margin = {top: 20, right: 30, bottom: 20, left: 150};
    this.width = this.htmlElement.offsetWidth - this.margin.left - this.margin.right;
    this.height = this.graphHeight - this.margin.top - this.margin.bottom;

    this.svg = this.host.html('')
      .append('svg')
      .attr('width', this.width + this.margin.left + this.margin.right)
      .attr('height', this.height + this.margin.top + this.margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + this.margin.left + ',' + this.margin.top + ')');

    this.x = scaleTime().range([0, this.width]);
    this.x.domain([this.minTime, this.maxTime]);
    this.svg
      .append('g')
      .attr('class', 'axis x-axis')
      .attr('transform', 'translate(0,' + this.height + ')')
      .call(axisBottom(this.x));

    console.debug('D3 Initialized');
  }
}

import { IInterfacable } from "../../interfacable";
import moment from "moment";

export interface ITrestleEvent {
    individual: string;
    type: string;
    temporal: string;
}

export enum TrestleEventType {
    CREATED,
    DESTROYED,
    BECAME,
    SPLIT,
    MERGED
}

export class TrestleEvent implements IInterfacable<ITrestleEvent> {
    private individual: string;
    private type: string;
    private temporal: moment.Moment;

    constructor(event: ITrestleEvent) {
        this.individual = event.individual;
        this.type = event.type;
        this.temporal = moment(event.temporal, moment.ISO_8601);
    }

    public getIndividual() {
        return this.individual;
    }

    public getType() {
        return this.type;
    }

    public getTemporal() {
        return this.temporal;
    }

    public asInterface() {
        return {
            individual: this.individual,
            type: this.type,
            temporal: this.temporal.toISOString()
        };
    }
}

import { ITrestleTemporal, TrestleTemporal } from "./trestle-temporal";
import { IInterfacable } from "../../interfacable";
import * as moment from "moment";

export interface ITrestleFact {
    identifier: string;
    name: string;
    type: string;
    value: string;
    databaseTemporal: ITrestleTemporal;
    validTemporal: ITrestleTemporal;
}

export class TrestleFact implements IInterfacable<ITrestleFact> {
    private identifier: string;
    private name: string;
    private type: string;
    private value: string;
    private databaseTemporal: TrestleTemporal;
    private validTemporal: TrestleTemporal;

    constructor(fact: ITrestleFact) {
        this.identifier = fact.identifier;
        this.name = fact.name;
        this.type = fact.type;
        this.value = fact.value;
        this.databaseTemporal = new TrestleTemporal(fact.databaseTemporal);
        this.validTemporal = new TrestleTemporal(fact.validTemporal);
    }

    public getID(): string {
        return this.identifier;
    }

    public getName(): string {
        return this.name;
    }

    public getValue(): string {
        return this.value;
    }

    public getType(): string {
        return this.type;
    }

    public getValidTemporal(): TrestleTemporal {
        return this.validTemporal;
    }

    public getDatabaseTemporal(): TrestleTemporal {
        return this.databaseTemporal;
    }

    public isSpatial(): boolean {
        return this.name === "asWKT";
    }

    /**
     * Is this fact active at the specified valid/database intersection
     * @param {moment.Moment} validAt
     * @param {moment.Moment} dbAt
     * @returns {boolean}
     */
    public isActive(validAt: moment.Moment, dbAt?: moment.Moment): boolean {
        if (dbAt) {
            return this.getValidTemporal().isActive(validAt) &&
                this.getDatabaseTemporal().isActive(dbAt);
        } else {
            return this.getValidTemporal().isActive(validAt);
        }
    }

    public asInterface(): ITrestleFact {
        return {
            identifier: this.identifier,
            name: this.name,
            type: this.type,
            value: this.value,
            databaseTemporal: this.databaseTemporal.asInterface(),
            validTemporal: this.validTemporal.asInterface()
        };
    }
}

import { ITrestleTemporal, TrestleTemporal } from "./trestle-temporal";
import { ITrestleFact, TrestleFact } from "./trestle-fact";
import { ITrestleRelation, TrestleRelation } from "./trestle-relation";
import { ITrestleEvent, TrestleEvent } from "./trestle-event";
import { GeometryObject } from "geojson";
import { IInterfacable } from "../../interfacable";
import { parse } from "wellknown";
import SortedArray from 'sorted-array';

export interface ITrestleIndividual {
    individualID: string;
    existsTemporal: ITrestleTemporal;
    facts: ITrestleFact[];
    relations: ITrestleRelation[];
    events: ITrestleEvent[];
}

export class TrestleIndividual implements IInterfacable<ITrestleIndividual> {

    private static suffixRegex = /.*[\/#]([^\/#]*)$/g;
    private static prefixRegex = /.*[\/#]/g;
    private static hostnameRegex = /\w+:\/\/[^\/]*/g;
    private readonly id: string;
    private filteredID?: string;
    private facts: SortedArray<TrestleFact>;
    private spatialFact?: TrestleFact;
    private relations: TrestleRelation[] = [];
    private events: TrestleEvent[] = [];
    private readonly existsTemporal: TrestleTemporal;

    constructor(individual: ITrestleIndividual) {
        this.id = individual.individualID;
        this.facts = new SortedArray<TrestleFact>([], TrestleIndividual.factSort);
        this.existsTemporal = new TrestleTemporal(individual.existsTemporal);
        individual.facts.forEach((fact) => {
            const factClass = new TrestleFact(fact);
            // Set as spatial fact, if that's the case
            if (factClass.isSpatial()) {
                this.spatialFact = factClass;
            }
            this.facts.insert(factClass);
        });
        individual.relations.forEach((relation) => {
            this.relations.push(new TrestleRelation(relation));
        });
        individual.events.forEach((event) => this.events.push(new TrestleEvent(event)));
    }

    /**
     * Get Individual ID
     * @returns {string}
     */
    public getID(): string {
        return this.id;
    }

    /**
     * Get the individual ID, without the URI base or temporal range
     * @returns {string}
     */
    public getFilteredID(): string {
        if (this.filteredID) {
            return this.filteredID;
        }
        this.filteredID = TrestleIndividual.filterID(this.id);
        return this.filteredID;
    }

    /**
     * Gets the URI base of the individual ID
     * @returns {string}
     */
    public getHostname(): string {
        return TrestleIndividual.extractHostname(this.id);
    }

    /**
     * Get the individual ID, without the URI base
     * @returns {string}
     */
    public withoutHostname(): string {
        return TrestleIndividual.withoutHostname(this.id);
    }

    /**
     * Return the filtered individual ID as a hashed numeric value
     * Currently using the SBDM algorithm
     * @returns {number}
     */
    public getIDAsInteger(): number {
        return TrestleIndividual.hashID(this.getFilteredID());
    }

    /**
     * Get the Existence temporal for the individual
     * @returns {TrestleTemporal}
     */
    public getTemporal(): TrestleTemporal {
        return this.existsTemporal;
    }

    /**
     * Get the spatial fact for the individual, parsed as a {GeometryObject}
     * @returns {GeometryObject}
     */
    public getSpatialValue(): GeometryObject {

        if (this.spatialFact) {
            const geojson = parse(this.spatialFact.getValue());
            if (geojson !== null) {
                return geojson;
            }
            console.error("Failed to parse:", this.spatialFact.getValue());
        }
        throw new Error("Individual " + this.getID() + " is not spatial and should be");
    }

    /**
     * Get the sptial fact for the indivdual, as a WKT string
     * @returns {string}
     */
    public getSpatialValueAsWKT(): string {
        if (this.spatialFact) {
            return this.spatialFact.getValue();
        }
        throw new Error("Individual " + this.getID() + " is not spatial and should be");
    }

    /**
     * Get an {Iterable} of {TrestleFact} of all facts for the individual
     * @returns {TrestleFact[]}
     */
    public getFacts(): TrestleFact[] {
        return this.facts.array;
    }

    /**
     * Returns a collection of fact names and associated values
     * @returns {{[p: string]: any}}
     */
    public getFactValues(): { [name: string]: any } {
        const values: { [name: string]: any } = {};
        this.facts.array.forEach((value) => {
            values[value.getName()] = value.getValue();
        });
        return values;
    }

    /**
     * Get all Individual relations
     * @returns {TrestleRelation[]}
     */
    public getRelations(): TrestleRelation[] {
        return this.relations;
    }

    /**
     * Get all individual events
     * @returns {TrestleEvent[]}
     */
    public getEvents(): TrestleEvent[] {
        return this.events;
    }

    /**
     * Gets the start event for the given selection.
     * Returns a MERGED event, if one exists
     * Otherwise, returns CREATED
     * @returns {TrestleEvent}
     */
    public getStartEvent(): TrestleEvent {
        const mergedEvent = this.getEvents()
            .filter((event) => event.getType() === "MERGED");

        if (mergedEvent.length > 0) {
            return mergedEvent[0];
        }
        const createdEvent = this.getEvents()
            .filter((event) => event.getType() === "CREATED");
        if (createdEvent.length > 0) {
            return createdEvent[0];
        }
        throw new Error("Individual: " + this.getID() + " does not have a CREATED/MERGED event");
    }

    /**
     * Returns the end event, if one exists
     * If a SPLIT event exists, returns that
     * Otherwise, returns DESTROYED
     * Returns
     * @returns {TrestleEvent}
     */
    public getEndEvent(): TrestleEvent | null {
        const splitEvent = this.getEvents()
            .filter((event) => event.getType() === "SPLIT");
        if (splitEvent.length > 0) {
            return splitEvent[0];
        }

        const destroyedEvent = this.getEvents()
            .filter((event) => event.getType() === "DESTROYED");
        if (destroyedEvent.length > 0) {
            return destroyedEvent[0];
        }

        return null;
    }

    /**
     * Is this individual a part of a spatial union?
     * @returns {boolean}
     */
    public isUnion(): boolean {
        return this.relations.some((relation) => relation.isUnionType());
    }

    /**
     * Transform the individual back into its Interface type
     * @returns {ITrestleIndividual}
     */
    public asInterface(): ITrestleIndividual {
        const returnValue: ITrestleIndividual = {
            individualID: this.id,
            existsTemporal: this.existsTemporal.asInterface(),
            facts: [],
            relations: [],
            events: []
        };
        this.facts.array.forEach((value) => {
            returnValue.facts.push(value.asInterface());
        });
        this.relations.forEach((value) => {
            returnValue.relations.push(value.asInterface());
        });
        this.events.forEach((event) => returnValue.events.push(event.asInterface()));
        return returnValue;
    }

    /**
     * Remove dates from ID
     * @param {string} id
     * @returns {string}
     */
    public static filterID(id: string): string {
        const suffix = TrestleIndividual.extractSuffix(id);
        const idStrings = suffix.split(":");
        // return idStrings[0] + ":" + idStrings[1];
        return idStrings[0];
    }

    /**
     * Filter ID string to remove hostname (authority)
     * @param {string} id
     * @returns {string}
     */
    public static withoutHostname(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.hostnameRegex.lastIndex = 0;
        return id.replace(TrestleIndividual.hostnameRegex, "");
    }

    /**
     * Get the URI hostname
     * Returns an empty string if nothing matches
     * @param {string} id
     * @returns {string}
     */
    public static extractHostname(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.hostnameRegex.lastIndex = 0;
        const matches = id.match(TrestleIndividual.hostnameRegex);
        if (matches !== null) {
            return matches[0];
        }
        return "";
    }


    /**
     * Extracts the suffix from the individual, or returns an empty string
     * @param {string} id
     * @returns {string}
     */
    public static extractSuffix(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.suffixRegex.lastIndex = 0;
        const matches = TrestleIndividual.suffixRegex.exec(id);
        if (matches !== null) {
            return matches[1];
        }
        return "";
    }

    /**
     * Extracts the prefix from the individual, or returns an empty string
     * @param {string} id
     * @returns {string}
     */
    public static extractPrefix(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.prefixRegex.lastIndex = 0;
        const matches = id.match(TrestleIndividual.prefixRegex);
        if (matches !== null) {
            return matches[0];
        }
        return "";
    }

    /**
     * SDBM algorithm for generating a numeric value of a provided string
     * @param {string} id
     * @returns {number}
     */
    public static hashID(id: string): number {
        let hash = 0;
        for (let i = 0; i < id.length; i++) {
            const char = id.charCodeAt(i);
            // eslint-disable-next-line no-bitwise
            hash = char + (hash << 6) + (hash << 16) - hash;
        }
        return hash;
    }

    /**
     * Sorter for the Fact array which sorts by ID -> validFrom -> dbFrom
     *
     * @param {TrestleFact} a
     * @param {TrestleFact} b
     * @returns {number}
     */
    private static factSort(a: TrestleFact, b: TrestleFact): number {
        const idCompare = a.getID().localeCompare(b.getID());
        // If they're not equal, return
        if (idCompare !== 0) {
            return idCompare;
        }
        //    Next, compare on valid from
        const vEqual = a.getValidTemporal().getFrom().isSame(b.getValidTemporal().getFrom());
        // If they're not equal, is A before?
        if (!vEqual) {
            return a.getValidTemporal().getFrom().isBefore(b.getValidTemporal().getFrom()) ? -1 : 1;
        }
        //    Finally, db time
        const dEqual = a.getDatabaseTemporal().getFrom().isSame(b.getDatabaseTemporal().getFrom());
        if (!dEqual) {
            return a.getDatabaseTemporal().getFrom().isBefore(b.getDatabaseTemporal().getFrom()) ? -1 : 1;
        }
        return 0;
    }
}

