import { IInterfacable } from "../../interfacable";
import moment from "moment";

export interface ITrestleTemporal {
    ID: string;
    From: Date;
    To?: Date | string;
}

export class TrestleTemporal implements IInterfacable<ITrestleTemporal> {
    private id: string;
    private from: moment.Moment;
    private to?: moment.Moment;

    constructor(temporal: ITrestleTemporal) {
        this.id = temporal.ID;
        this.from = moment(temporal.From, moment.ISO_8601);
        // There's a problem with our serialization, which means these things might end up as strings, which is no good.
        if (temporal.To !== null && temporal.To !== "") {
            this.to = moment.utc(temporal.To, moment.ISO_8601);
        }
    }

    public getID(): string {
        return this.id;
    }

    public getFrom(): moment.Moment {
        return this.from;
    }

    public getFromAsDate(): Date {
        return this.from.toDate();
    }

    public getTo(): moment.Moment | undefined {
        return this.to;
    }

    public getToAsDate(): Date | undefined {
        if (this.to) {
            return this.to.toDate();
        }
        return undefined;
    }

    public isContinuing(): boolean {
        return this.to === undefined || !this.to.isValid();
    }

    /**
     * Does the specified temporal fall within the interval of this TemporalObject?
     * @param {moment.Moment} temporal
     * @returns {boolean}
     */
    public isActive(temporal: moment.Moment): boolean {
        if (this.to === undefined) {
            return this.from.isSameOrBefore(temporal);
        } else {
            return this.from.isSameOrBefore(temporal) &&
                this.to.isAfter(temporal);
        }
    }

    public asInterface(): ITrestleTemporal {
        const returnValue: ITrestleTemporal = {
            ID: this.id,
            From: this.from.toDate()
        };
        if (!this.isContinuing() && (this.to !== undefined)) {
            returnValue.To = this.to.toDate();
        }
        return returnValue;
    }
}

import {Injectable} from '@angular/core';
import {Router} from '@angular/router';
import {Privileges, TrestleUser} from './trestle-user';
import {HttpClient} from '@angular/common/http';
import {JwtHelperService} from '@auth0/angular-jwt';
import {environment} from '../../environments/environment';
import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

const _key: string = 'access_token';

export class TrestleToken {

  private readonly exp: number;
  private readonly iat: number;
  private readonly user: ITrestleUser;

  public constructor(token: any) {
    this.exp = token['exp'];
    this.iat = token['iat'];
    this.user = JSON.parse(token['data4j']);
  }

  /**
   * Get token expiration time (in ms from Unix epoch)
   * @returns {number}
   */
  public getExpiration() {
    return this.exp;
  }

  /**
   * Get issuance time (in ms from Unix epoch)
   * @returns {number}
   */
  public getIssue() {
    return this.iat;
  }

  /**
   * Get the user from the token
   * @returns {ITrestleUser}
   */
  public getUser() {
    return this.user;
  }
}

export interface ITrestleUser {
  id?: number;
  firstName: string;
  lastName: string;
  username: string;
  email: string;
  password: string;
  privileges: number;
}

@Injectable()
export class AuthService {

  private jwtHelper: JwtHelperService;
  private readonly baseUrl;

  constructor(private router: Router, private http: HttpClient) {
    this.jwtHelper = new JwtHelperService();
    this.baseUrl = environment.baseUrl;
  }

  /**
   * Attempt to login the given use
   * @param {string} username
   * @param {string} password
   */
  public login(username: string, password: string): Observable<{ user: TrestleUser, token: string }> {
    return this.http.post(this.baseUrl + '/auth/login', {username, password: password}, {
      responseType: 'text'
    })
      .pipe(map(token => {
        const t = this.getToken2(token);
        return {user: new TrestleUser(t.getUser()), token}
      }));
  }

  /**
   * Logout the user
   */
  public logout(): Observable<string> {
    console.debug("Logging out");
    return this.http.post(this.baseUrl + '/auth/logout', null, {
      responseType: 'text'
    });
  }

  /**
   * Is the user currently logged in?
   * @returns {boolean}
   */
  public loggedIn(): boolean {
    const token = localStorage.getItem(_key);
    if (token) {
      console.debug(
        this.jwtHelper.decodeToken(token),
        this.jwtHelper.getTokenExpirationDate(token),
        this.jwtHelper.isTokenExpired(token)
      );
    }
    return !this.jwtHelper.isTokenExpired(token);
  }

  /**
   * Does the user have AT LEAST Admin permissions?
   * @returns {boolean}
   */
  public isAdmin(): boolean {
    if (this.loggedIn()) {
      const token = this.getToken();
      // eslint-disable-next-line no-bitwise
      return (token !== null) && ((token.getUser().privileges & Privileges.ADMIN) > 0);
    }
    return false;
  }

  /**
   * Get the user, if one exists
   * @returns {TrestleUser | null}
   */
  public getUser(): TrestleUser | null {
    if (this.loggedIn()) {
      const token = this.getToken();
      if (token) {
        return new TrestleUser(token.getUser());
      }
    }
    console.error('User is not logged in');
    return null;
  }

  /**
   * Determines if the logged-in user has the necessary roles to perform a certain function
   * @param roles - Array of required Privileges
   * @returns {boolean} - has all the required roles
   */
  public hasRequiredRoles(roles: Privileges[]): boolean {
    const user = this.getUser();
    if (user == null) {
      return false;
    }

    return user.hasRequiredPrivileges(roles);
  }

  public getEncodedToken(): string | null {
    return localStorage.getItem(_key);
  }

  private getToken(): TrestleToken | null {
    const jwtToken = this.getEncodedToken();
    if (jwtToken) {
      return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
    }
    return null;
  }

  private getToken2 = (jwtToken: string): TrestleToken => {
    return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
  };
}

import {Component, OnInit} from '@angular/core';
import {AbstractControl, FormBuilder, FormGroup, ValidationErrors, Validators} from '@angular/forms';
import {ITrestleUser} from '../../user/authentication.service';
import {UserService} from '../../user/users.service';
import {MatDialogRef} from '@angular/material/dialog';
import {Observable, of, timer} from 'rxjs';
import {map, switchMap} from 'rxjs/operators';
import {Privileges} from '../../user/trestle-user';

export enum UserDialogResponseType {
  ADD,
  DELETE
}

export interface IUserDialogResponse {
  type: UserDialogResponseType;
  user: ITrestleUser;
}

@Component({
  selector: 'user-add-dialog',
  templateUrl: './users.dialog.component.html',
  styleUrls: ['./users.dialog.component.scss'],
  providers: [UserService]
})
export class UserDialogComponent implements OnInit {
  public privileges: Map<string, number> = new Map();
  public user: ITrestleUser;
  public updateMode = true;
  public registerForm: FormGroup;
  public maxPasswordLength = 60;

  constructor(public dialogRef: MatDialogRef<UserDialogComponent>,
              private userService: UserService,
              private formBuilder: FormBuilder) {
//    Try to list all the enum keys
    for (const priv in Privileges) {
      if (parseInt(priv, 10) >= 0) {
        console.debug('Privs:', priv, Privileges[priv]);
        this.privileges.set(Privileges[priv], parseInt(priv, 10));
      }
    }
  }

  public ngOnInit(): void {
    // Merge the user object (which might be null) with a blank default
    const mergedUser = {
      ...{
        firstName: '',
        lastName: '',
        username: '',
        password: '',
        email: '',
        privileges: 1
      }, ...this.user
    };

    console.debug('Merged user:', mergedUser);
    //    Create the form
    this.registerForm = this.formBuilder.group({
      firstName: mergedUser.firstName,
      lastName: mergedUser.lastName,
      username: [mergedUser.username, undefined, this.validateUser],
      password: [mergedUser.password, this.validatePasswordLength],
      email: [mergedUser.email, Validators.email]
    });
    if (this.user == undefined || this.user.id === undefined) {
      this.updateMode = false;
      console.debug('Passed null user, creating blank instance');
      this.user = {
        username: '',
        password: '',
        firstName: '',
        lastName: '',
        email: '',
        privileges: 1
      };
    }
  }

  /**
   * Are we currently set to update the user?
   * @returns {boolean}
   */
  public isUpdate(): boolean {
    return this.updateMode;
  }

  /**
   * Save the new/updated user to the database
   */
  public save() {
    if (this.registerForm.valid) {
      // Construct a new user object
      const mergedUser = {
        ...this.user,
        ...this.registerForm.value
      };
      this.userService.modifyUser(mergedUser).subscribe(data => {
        console.debug('Response to add:', data);
        const responseID = parseInt(data, 10);
        if (!this.isUpdate()) {
          mergedUser.id = responseID;
        }
        this.dialogRef.close({
          type: UserDialogResponseType.ADD,
          user: mergedUser
        });
      }, (err: Error) => console.error(err));
    }
  }

  /**
   * Delete the specified user from the database
   */
  public delete() {
    if (this.user.id !== undefined) {
      this.userService
        .deleteUser(this.user.id)
        .subscribe(() => this.dialogRef.close({
            type: UserDialogResponseType.DELETE,
            user: this.user
          }),
          (err: Error) => console.error(err));
    } else {
      console.error('Tried to save a user, but the ID was null');
    }
  }

  /**
   * Dismiss modal, without saving any changes
   */
  public dismiss() {
    this.dialogRef.close(null);
  }

  /**
   * Change permission level of the user to the given values
   * @param {Privileges} level
   */
  public alterPermissionLevel(level: Privileges): void {
    // eslint-disable-next-line no-bitwise
    this.user.privileges = this.user.privileges ^ level;
  }

  /**
   * Does the user currently have the given permission?
   * @param {Privileges} privilage to check
   * @returns {boolean}
   */
  public isSelected(privilage: Privileges): boolean {
    // eslint-disable-next-line no-bitwise
    return (this.user.privileges & privilage) > 0;
  }

  public getEmailErrorMessage(): string {
    if (this.registerForm.controls['email'].hasError('required')) {
      return 'Value is required';
    }
    if (this.registerForm.controls['email'].hasError('email')) {
      return 'Not a valid email address';
    }
    return '';
  }

  public showLengthHint(value?: string): boolean {
    if (value) {
      if (this.isUpdate() && value.length === 60) {
        return false;
      }
      // Warn at 70% length
      if ((value.length / this.maxPasswordLength) > 0.7) {
        return true;
      }
    }

    return false;
  }

  public getPasswordErrorMessage(): string {
    if (this.registerForm.controls['password'].hasError('maxLength')) {
      return 'Password too long';
    }

    if (this.registerForm.controls['password'].hasError('required')) {
      return 'Value is required';
    }

    return '';
  }

  public getUsernameErrorMessage(): string {
    if (this.registerForm.controls['username'].hasError('required')) {
      return 'Value is required';
    }
    if (this.registerForm.controls['username'].hasError('userExists')) {
      return 'Username taken';
    }
    return '';
  }

  public validateUser = (c: AbstractControl): Observable<ValidationErrors | null> => {
    if (this.isUpdate() || c.value === null || c.value === '') {
      console.debug('Returning');
      return of(null);
    }
    return timer(500)
      .pipe(
        switchMap(() => {
          return this.userService.userExists(c.value)
            .pipe(
              map((exists) => {
                console.debug('User exists?', exists);
                return exists ? ({
                  userExists: {
                    value: c.value
                  }
                } as ValidationErrors) : null;
              }));
        }));
  };

  public validatePasswordLength = (c: AbstractControl): null | ValidationErrors => {
    if (c.value === null || c.value === '') {
      return null;
    }

    // If we're update and the password length is max, that's fine
    if (this.isUpdate() && c.value.length === this.maxPasswordLength) {
      return null;
    }

    //    Finally, validate it
    return c.value.length <= this.maxPasswordLength ? null : {
      maxLength: {
        value: true
      }
    };
  };
}

import {Component, OnDestroy, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';
import {FormBuilder, FormGroup, Validators} from '@angular/forms';
import {animate, state, style, transition, trigger} from '@angular/animations';
import {AuthService} from '../../user/authentication.service';
import {selectErrorFromUser, State} from '../../reducers';
import {select, Store} from '@ngrx/store';
import {Subscription} from 'rxjs';
import {login} from '../../actions/auth.actions';
import {HttpErrorResponse} from '@angular/common/http';

interface IUserLogin {
  username: string;
  password: string;
}

@Component({
  selector: 'login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  animations: [
    trigger('errorMessage', [
      state('inactive', style({
        backgroundColor: 'white'
      })),
      state('active', style({
        backgroundColor: 'red'
      })),
      transition('inactive => active', animate('100ms ease-in')),
      transition('active => inactive', animate('100ms ease-out'))
    ])
  ]
})

export class LoginComponent implements OnInit, OnDestroy {
  public loginForm: FormGroup;
  public errorMessage: string;
  public errorState: string;
  private returnUrl: string;
  private errorSubscription: Subscription;

  constructor(private fb: FormBuilder, private authService: AuthService, private route: ActivatedRoute, private router: Router, private store: Store<State>) {
    // Not used
  }

  public ngOnInit(): void {

    this.errorSubscription = this.store
      .pipe(select(selectErrorFromUser))
        .subscribe((result) => {
          if (result) {
            this.errorState = 'active';
            this.errorMessage = LoginComponent.parseErrorMessage(result);
          } else {
            this.errorState = 'inactive';
            this.errorMessage = '';
          }
        });

    this.authService.logout();
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';
    this.loginForm = this.fb.group({
      username: [null, Validators.required],
      password: [null, Validators.required]
    });
    this.errorMessage = '';
    this.errorState = 'inactive';
  }

  public ngOnDestroy(): void {
    this.errorSubscription.unsubscribe();
  }

  /**
   * Attempt to login the given user
   * @param {IUserLogin} user
   */
  public login(user: IUserLogin) {
    this.store.dispatch(login({username: user.username, password: user.password, returnUrl: this.returnUrl}));
  }

  private static parseErrorMessage(error: Error): string {
    if (error instanceof HttpErrorResponse && error.status == 401) {
      return "Incorrect Username or Password";
    }
    return error.message;
  }
}

import { browser, by, element, ElementFinder } from "protractor";

export interface IUserTable {
    username?: string,
    password?: string,
    first_name?: string,
    last_name?: string,
    email?: string;
}

export type UserType = "user" | "admin" | "dba";

export class UserDetailsModal {

    private userNameInput: ElementFinder;
    private passwordInput: ElementFinder;
    private firstNameInput: ElementFinder;
    private lastNameInput: ElementFinder;
    private emailInput: ElementFinder;

    public constructor() {
        this.userNameInput = element(by.css("input[formcontrolname='username']"));
        this.passwordInput = element(by.css("input[formcontrolname='password']"));
        this.firstNameInput = element(by.css("input[formcontrolname='firstName']"));
        this.lastNameInput = element(by.css("input[formcontrolname='lastName']"));
        this.emailInput = element(by.css("input[formcontrolname='email']"));
    }

    /**
     * Create a new user of the given type with the provided data
     * Assumes the modal is already open, but closes it in order to submit the data
     *
     * @param {UserType} userType - user type to add
     * @param {IUserTable} user - User data
     * @returns {Promise<any>} - Returns when the modal closes (waits 500ms)
     */
    public async createUser(userType: UserType, user: IUserTable) {
        // Wait for the modal to load
        await browser.sleep(500);
        await this.userNameInput.sendKeys(user.username || "");
        await this.passwordInput.sendKeys(user.password || "");
        await this.firstNameInput.sendKeys(user.first_name || "");
        await this.lastNameInput.sendKeys(user.last_name || "");
        await this.emailInput.sendKeys(user.email || "");

        //    Now the permissions, select everything lower
        switch (userType) {
            case "dba": {
                await UserDetailsModal.selectUserPermission("dba");
                await UserDetailsModal.selectUserPermission("admin");
                break;
            }
            case "admin": {
                await UserDetailsModal.selectUserPermission("admin");
                break;
            }
            case "user": {
                break;
            }
        }
        return Promise.resolve(undefined);
    }

    /**
     * Update the given properties of the user
     * Assumes the modal is already open, but closes it in order to submit the changes
     *
     * @param {IUserTable} user - User data to update
     * @returns {Promise<any>} - returns when the modal is closed (waits 500ms)
     */
    public async editUser(user: IUserTable) {
        // Wait for the modal to load
        await browser.sleep(500);
        // We have to clear the input fields before
        if (user.username) {
            await this.userNameInput.clear();
            await this.userNameInput.sendKeys(user.username);
        }

        if (user.email) {
            await this.emailInput.clear();
            await this.emailInput.sendKeys(user.email);
        }

        if (user.password) {
            await this.passwordInput.clear();
            await this.passwordInput.sendKeys(user.password);
        }

        if (user.first_name) {
            await this.firstNameInput.clear();
            await this.firstNameInput.sendKeys(user.first_name);
        }

        if (user.last_name) {
            await this.lastNameInput.clear();
            await this.lastNameInput.sendKeys(user.last_name);
        }

        element(by.buttonText("Update User")).click();
        // Sleep for 500 ms to let the modal close
        return browser.sleep(500);
    }

    /**
     * Delete the given user
     * Assumes the modal is already open, but closes it in order to submit the changes
     *
     * @returns {any} - Returns when the modal closes
     */
    public async deleteUser() {
        // Wait for the modal to load
        await browser.sleep(500);
        return element(by.buttonText("Delete User")).click();
    }

    public async submit() {
        await element(by.buttonText("Add User")).click();
        // Sleep for 500 ms to let the modal close
        return browser.sleep(500);
    }

    public async dismiss() {
        await element(by.buttonText("Cancel")).click();
        return browser.sleep(500);
    }

    /**
     * Selects the mat-button-toggle that corresponds to the given permission.
     * Automatically uppercases the input
     *
     * @param {string} permission
     * @returns {Promise<any>}
     */
    private static async selectUserPermission(permission: string) {
        // We need to use contains, because the Text can have trailing whitespace
        const xpathString = "//mat-button-toggle[.//div[contains(text(), '" + permission.toUpperCase() + "')]]";
        return element(by.xpath(xpathString)).click();
    }
}

import {ActionReducerMap, createReducer, createSelector, MetaReducer, on} from '@ngrx/store';
import {TrestleUser} from '../user/trestle-user';
import {login, loginFailure, loginSuccess, logout} from '../actions/auth.actions';
import {storageMetaReducer} from './storage.metareducer';

export interface UserState {
  user: TrestleUser;
  userError: Error;
  userToken: string;
}

const initialUserState: UserState = {
  user: null,
  userError: null,
  userToken: ''
};

export interface State {
  user: UserState;
}

const _authReducer = createReducer(initialUserState, on(login, state => {
    console.log('Trying to login');
    return state;
  }),
  on(loginSuccess, (state, {user, token}) => ({
    userError: null,
    user,
    userToken: token
  })),
  on(loginFailure, (state, {error}) => ({
    user: null,
    userError: error,
    userToken: ''
  })),
  on(logout, (state) => ({
    ...state,
    user: null,
    userToken: ''
  })));


export function authReducer(state: UserState = initialUserState, action): UserState {
  return _authReducer(state, action);
}

export const reducers: ActionReducerMap<State> = {
  user: authReducer
};


export const metaReducers: MetaReducer<State>[] = [storageMetaReducer];


export const selectUserState = (state: State) => state.user;
export const selectUser = (state: UserState) => state.user;


export const selectUserFromUser = createSelector(
  selectUserState,
  selectUser);

export const selectErrorFromUser = createSelector(
  selectUserState,
  (state) => state.userError
);

export const selectTokenFromUser = createSelector(
  selectUserState,
  (state) => state.userToken
);

