import {Injectable} from '@angular/core';
import {Actions, createEffect, ofType} from '@ngrx/effects';
import {AuthService} from '../user/authentication.service';
import {login, loginFailure, loginSuccess, logout} from '../actions/auth.actions';
import {catchError, exhaustMap, map, tap} from 'rxjs/operators';
import {of} from 'rxjs';
import {Router} from '@angular/router';


@Injectable()
export class AuthEffects {

  constructor(private actions$: Actions, private authService: AuthService, private router: Router) {
    // Not used
  }


  login = createEffect(() =>
    this.actions$
      .pipe(ofType(login), exhaustMap(action => {
        return this.authService.login(action.username, action.password)
          .pipe(
            map(({user, token}) => {
              return loginSuccess({user, returnUrl: action.returnUrl, token});
            }),
            catchError(error => {
              return of(loginFailure({error}));
            }));
      }))
  );

  loginSuccess = createEffect(() =>
      this.actions$
        .pipe(ofType(loginSuccess), tap(action => {
          return this.router.navigate([action.returnUrl]);
        })), {
      dispatch: false
    }
  );

  logout = createEffect(() =>
    this.actions$
      .pipe(ofType(logout), exhaustMap(() => {
        return this.authService.logout().pipe(map(() => {
          console.debug("Ready to logout");
          return this.router.navigate(['/']);
        }));
      })), {
    dispatch: false
  });
}

import {Injectable} from '@angular/core';
import {Router} from '@angular/router';
import {Privileges, TrestleUser} from './trestle-user';
import {HttpClient} from '@angular/common/http';
import {JwtHelperService} from '@auth0/angular-jwt';
import {environment} from '../../environments/environment';
import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

const _key: string = 'access_token';

export class TrestleToken {

  private readonly exp: number;
  private readonly iat: number;
  private readonly user: ITrestleUser;

  public constructor(token: any) {
    this.exp = token['exp'];
    this.iat = token['iat'];
    this.user = JSON.parse(token['data4j']);
  }

  /**
   * Get token expiration time (in ms from Unix epoch)
   * @returns {number}
   */
  public getExpiration() {
    return this.exp;
  }

  /**
   * Get issuance time (in ms from Unix epoch)
   * @returns {number}
   */
  public getIssue() {
    return this.iat;
  }

  /**
   * Get the user from the token
   * @returns {ITrestleUser}
   */
  public getUser() {
    return this.user;
  }
}

export interface ITrestleUser {
  id?: number;
  firstName: string;
  lastName: string;
  username: string;
  email: string;
  password: string;
  privileges: number;
}

@Injectable()
export class AuthService {

  private jwtHelper: JwtHelperService;
  private readonly baseUrl;

  constructor(private router: Router, private http: HttpClient) {
    this.jwtHelper = new JwtHelperService();
    this.baseUrl = environment.baseUrl;
  }

  /**
   * Attempt to login the given use
   * @param {string} username
   * @param {string} password
   */
  public login(username: string, password: string): Observable<{ user: TrestleUser, token: string }> {
    return this.http.post(this.baseUrl + '/auth/login', {username, password: password}, {
      responseType: 'text'
    })
      .pipe(map(token => {
        const t = this.getToken2(token);
        return {user: new TrestleUser(t.getUser()), token}
      }));
  }

  /**
   * Logout the user
   */
  public logout(): Observable<string> {
    console.debug("Logging out");
    return this.http.post(this.baseUrl + '/auth/logout', null, {
      responseType: 'text'
    });
  }

  /**
   * Is the user currently logged in?
   * @returns {boolean}
   */
  public loggedIn(): boolean {
    const token = localStorage.getItem(_key);
    if (token) {
      console.debug(
        this.jwtHelper.decodeToken(token),
        this.jwtHelper.getTokenExpirationDate(token),
        this.jwtHelper.isTokenExpired(token)
      );
    }
    return !this.jwtHelper.isTokenExpired(token);
  }

  /**
   * Does the user have AT LEAST Admin permissions?
   * @returns {boolean}
   */
  public isAdmin(): boolean {
    if (this.loggedIn()) {
      const token = this.getToken();
      // eslint-disable-next-line no-bitwise
      return (token !== null) && ((token.getUser().privileges & Privileges.ADMIN) > 0);
    }
    return false;
  }

  /**
   * Get the user, if one exists
   * @returns {TrestleUser | null}
   */
  public getUser(): TrestleUser | null {
    if (this.loggedIn()) {
      const token = this.getToken();
      if (token) {
        return new TrestleUser(token.getUser());
      }
    }
    console.error('User is not logged in');
    return null;
  }

  /**
   * Determines if the logged-in user has the necessary roles to perform a certain function
   * @param roles - Array of required Privileges
   * @returns {boolean} - has all the required roles
   */
  public hasRequiredRoles(roles: Privileges[]): boolean {
    const user = this.getUser();
    if (user == null) {
      return false;
    }

    return user.hasRequiredPrivileges(roles);
  }

  public getEncodedToken(): string | null {
    return localStorage.getItem(_key);
  }

  private getToken(): TrestleToken | null {
    const jwtToken = this.getEncodedToken();
    if (jwtToken) {
      return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
    }
    return null;
  }

  private getToken2 = (jwtToken: string): TrestleToken => {
    return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
  };
}

import {Inject, Injectable} from '@angular/core';
import {CACHE_SERVICE_CONFIG, ICacheServiceConfig} from './cache.service.config';
import { Observable, of, Subject, throwError} from 'rxjs';
import {Cache, KeyType, newCache, RemovalReason} from 'transitory';
import {tap} from 'rxjs/operators';

/**
 * HTTP cache based on:
 * https://hackernoon.com/angular-simple-in-memory-cache-service-on-the-ui-with-rxjs-77f167387e39
 */
@Injectable()
export class CacheService<K extends KeyType, V> {
  private cache: Cache<K, V>;
  private inFlightObservables: Map<K, Subject<V>> = new Map<K, Subject<V>>();

  /**
   * We only ever call this constructor manually, and provide it the cache specific config settings.
   * If it does get called by the Angular DI, it'll get some default config properties
   *
   * @param {ICacheServiceConfig} config
   */
  public constructor(@Inject(CACHE_SERVICE_CONFIG) private config: ICacheServiceConfig) {
    console.debug('Creating with config:', config);
    this.cache = newCache<K, V>()
      .expireAfterRead(this.config.maxAge)
      .maxSize(this.config.maxSize)
      .withRemovalListener(this.evictionHandler)
      .build();
  }

  /**
   * Attempt to get the individual from the Cache,
   * if that fails, call the fallback value and return the result to both the subscriber and the cache
   * @param {K} key to attempt to fetch from the cache
   * @param {Observable<V>} fallback function to call if key is missing
   * @returns {Observable<V> | Subject<V>} of result
   */
  public get(key: K, fallback?: Observable<V>): Observable<V> | Subject<V> {
    const value = this.cache.getIfPresent(key);
    if (value) {
      return of(value);
    }

    if (this.inFlightObservables.has(key)) {
      return (this.inFlightObservables.get(key) as Subject<V>);
    } else if (fallback && fallback instanceof Observable) {
      this.inFlightObservables.set(key, new Subject());
      return fallback.pipe(tap((oValue) => this.set(key, oValue)));
    } else {
      return throwError('Requested key is not available in Cache');
    }
  }

  /**
   * Add key/value pair to cache
   * @param {K} key
   * @param {V} value
   */
  public set(key: K, value: V): void {
    this.cache.set(key, value);
    this.notifyInFlightObservers(key, value);
  }

  private notifyInFlightObservers(key: K, value: V): void {
    if (this.inFlightObservables.has(key)) {
      const inFlight = (this.inFlightObservables.get(key) as Subject<V>);
      const observersCount = inFlight.observers.length;
      if (observersCount) {
        inFlight.next(value);
      }
      inFlight.complete();
      this.inFlightObservables.delete(key);
    }
  }

  private evictionHandler(key: K, _value: V, reason: RemovalReason): void {
    switch (reason) {
      case RemovalReason.EXPIRED: {
        console.log('Key %s expire:', key);
        break;
      }
      case RemovalReason.EXPLICIT: {
        console.log('Key %s was removed', key);
        break;
      }
    }
  }
}

import { Inject, Injectable } from "@angular/core";
import { COLOR_SCHEME, COLOR_SERVICE_CONFIG, IColorServiceConfig } from "./color-service.config";
import { schemeCategory10, schemeSet1, schemeSet2, schemeSet3 } from "d3";


@Injectable()
export class ColorService {

    private availableColors: string[];
    private colorScale: string[];

    public constructor(@Inject(COLOR_SERVICE_CONFIG) private config: IColorServiceConfig) {
        this.availableColors = [];
        this.colorScale = this.initializeColorScale();
    }

    /**
     * Get a color from the color scale, if one is available
     * If not, return white
     * @param {number} layer number to use when getting color
     * @param {string} fallbackColor to use if none are avilable
     * @returns {string}
     */
    public getColor(layer: number, fallbackColor = "white"): string {
        // See if we have a color available
        const aColor = this.availableColors.pop();
        if (aColor === undefined) {
            const color = this.colorScale[layer];
            if (color === null) {
                return fallbackColor;
            }
            return color;
        }
        return aColor;
    }

    /**
     * Return a color so that it's available for the next layer
     * @param {string} color
     */
    public returnColor(color: string): void {
        this.availableColors.push(color);
    }

    /**
     * Reset everything
     */
    public reset(): void {
        this.availableColors = [];
        this.colorScale = this.initializeColorScale();
    }

    private initializeColorScale(): string[] {
        switch (this.config.colorType) {
            case COLOR_SCHEME.CATEGORY_20B: {
                // @ts-ignore
              return schemeSet2;
            }
            case COLOR_SCHEME.CATEGORY_20C: {
                // @ts-ignore
              return schemeSet3;
            }
            case COLOR_SCHEME.CATEGORY_20: {
                // @ts-ignore
              return schemeSet1;
            }
            case COLOR_SCHEME.CATEGORY_10: {
                // @ts-ignore
              return schemeCategory10;
            }
        }
    }
}

import {Inject, Injectable, InjectionToken} from '@angular/core';
import {TrestleIndividual} from '../individual/TrestleIndividual/trestle-individual';
import {HttpClient, HttpParams} from '@angular/common/http';
import {Observable, throwError} from 'rxjs';
import {environment} from '../../../environments/environment';
import {CacheService} from '../cache/cache.service';
import {map, tap} from 'rxjs/operators';

export const DATASET_CACHE = new InjectionToken<CacheService<string, string[]>>('dataset.cache');

@Injectable()
export class DatasetService {

  private readonly baseUrl;

  constructor(private http: HttpClient,
              @Inject(DATASET_CACHE) private cache: CacheService<string, string[]>) {
    this.baseUrl = environment.baseUrl;
  }

  /**
   * Returns the list of currently registered datasets from the database
   * @returns {Observable<string[]>}
   */
  public getAvailableDatasets(): Observable<string[]> {
    // Try from cache first, then hit the API
    return this.cache.get('datasets', this.dsAPICall());
  }

  /**
   * Get all the data properties for the given dataset, returns them as a list with everything but the property name removed.
   *
   * @param {string} dataset - Dataset to query
   * @returns {Observable<string[]>} - Filtered list of registered data properties
   */
  public getDatasetProperties(dataset: string): Observable<string[]> {
    return this.http.get(this.baseUrl + '/datasets/' + dataset)
      .pipe(map(DatasetService.filterPropertyNames));
  }

  /**
   * Get a sampling of unique values for the given dataset property
   * Defaults to 100 values if no limit is provided.
   *
   * @param {string} dataset - Dataset to query
   * @param {string} fact - Property to get values for
   * @param {number} limit - Optional limit of values to return (defaults to 100)
   * @returns {Observable<string[]>} - List of property values
   */
  public getDatasetFactValues(dataset: string, fact: string, limit = 100): Observable<string[]> {

    const datsetURL = this.baseUrl + '/datasets/' + dataset + '/' + fact + '/values';

    const params = new HttpParams();
    params.append('limit', limit.toString());

    return this.http.get<string[]>(datsetURL, {
      params
    });
    // .catch(DatasetService.errorHandler);
  }

  private dsAPICall(): Observable<string[]> {
    return this.http.get<string[]>(this.baseUrl + '/datasets')
      .pipe(tap((res) => console.debug('Available datasets:', res)));

    // .map((res: Response) => res.json())
    // .catch(DatasetService.errorHandler);
  }

  public static errorHandler(error: any): Observable<Error> {
    return throwError(error || 'Server Error');
  }

  public static filterPropertyNames(properties: string[]): string[] {
    return properties
      .map((property) => {
        return TrestleIndividual.extractSuffix(property);
      });
  }
}

