import {Inject, Injectable, InjectionToken} from '@angular/core';
import {ITrestleIndividual, TrestleIndividual} from './TrestleIndividual/trestle-individual';
import {CacheService} from '../cache/cache.service';
import {HttpClient} from '@angular/common/http';
import {Observable} from 'rxjs';
import {environment} from '../../../environments/environment';
import {map, tap} from 'rxjs/operators';

export const INDIVIDUAL_CACHE = new InjectionToken<CacheService<string, TrestleIndividual>>('individual.cache');

@Injectable()
export class IndividualService {

  private readonly baseURL;

  constructor(private trestleHttp: HttpClient,
              @Inject(INDIVIDUAL_CACHE) private individualCache: CacheService<string, TrestleIndividual>) {
    this.baseURL = environment.baseUrl;
  }

  /**
   * Search for an individual in the database
   * @param {string} name of the individual to search for (partial value)
   * @param {string} dataset to restrict queries to
   * @param {number} limit number of return values
   * @returns {Observable<string[]>}
   */
  public searchForIndividual(name: string, dataset = '', limit = 10): Observable<string[]> {
    return this.trestleHttp.get<string[]>(this.baseURL + '/visualize/search', {
      params: {
        name: name,
        dataset: dataset,
        limit: limit.toString()
      }
    })
      .pipe(tap(res => console.debug('Search response:', res)));
    // .catch((error: Error) => Observable.throw(error || "Server Error"));
  }

  /**
   * Return a {TrestleIndividual} from the API
   * Uses the cache if possible
   * @param {string} name - Individual IRI string
   * @returns {Observable<TrestleIndividual>}
   */
  public getTrestleIndividual(name: string): Observable<TrestleIndividual> {
    return this.individualCache.get(name, this.getIndividualAPI(name));
  }

  private getIndividualAPI(name: string): Observable<TrestleIndividual> {
    return this.trestleHttp.get<ITrestleIndividual>(this.baseURL + '/individual/retrieve', {
      params: {
        name
      }
    })
      .pipe(map(response => {
        console.debug('Has response, building object', response);
        return new TrestleIndividual(response);
      }));
    // .catch((error: Error) => Observable.throw(error || "Server Error"));
  }
}

import {ApplicationRef, ComponentFactoryResolver, Injectable, Injector, ViewContainerRef} from "@angular/core";
import {ComponentPortal, DomPortalOutlet} from "@angular/cdk/portal";
import {LoadingSpinnerComponent} from "./loading-spinner.component";

@Injectable()
export class LoadingSpinnerService {

    private loadingSpinnerPortal: ComponentPortal<LoadingSpinnerComponent>;
    private bodyPortalHost: DomPortalOutlet;
    private domElement: HTMLElement;

    public constructor(private componentFactoryResolver: ComponentFactoryResolver,
                       private appRef: ApplicationRef,
                       private injector: Injector) {

        this.loadingSpinnerPortal = new ComponentPortal(LoadingSpinnerComponent);
    }

    public setViewContainerRef(ref: ViewContainerRef | HTMLElement): void {
        // const factory = this.componentFactoryResolver
        //     .resolveComponentFactory(LoadingSpinnerComponent);

        // const component = factory.create(ref.parentInjector);

        if (ref instanceof ViewContainerRef) {
            this.domElement = ref.element.nativeElement;
        } else {
            this.domElement = ref;
        }

        console.debug("Setting view ref to:", this.domElement);

        this.bodyPortalHost = new DomPortalOutlet(
            this.domElement,
            this.componentFactoryResolver,
            this.appRef,
            this.injector
        );
    }

    public reveal(): void {
        if (this.bodyPortalHost == null) {
            throw new Error("Host is empty, cannot add!");
        }
        console.debug("Adding spinner");
        this.bodyPortalHost.attach(this.loadingSpinnerPortal);
    }

    public hide(): void {
        if (this.bodyPortalHost == null) {
            throw new Error("Host is empty, cannot remove!");
        }
        console.debug("Removing spinner");
        this.bodyPortalHost.detach();
    }
}

import {Injectable} from '@angular/core';
import {LngLatBounds} from 'mapbox-gl';
import {FeatureCollection, GeometryObject, MultiPolygon, Polygon} from 'geojson';
import {Moment} from 'moment';
import {fromEvent, Observable, Subscriber, throwError} from 'rxjs';
import {filter, flatMap, map} from 'rxjs/operators';
import {HttpClient} from '@angular/common/http';
import {environment} from '../../../environments/environment';
import {ITrestleIndividual, TrestleIndividual} from '../../shared/individual/TrestleIndividual/trestle-individual';

export type wktType = 'POINT' |
  'MULTIPOINT' |
  'LINESTRING' |
  'MULTILINESTRING' |
  'POLYGON' |
  'MULTIPOLYGON';

export type wktValue = LngLatBounds | GeometryObject;

export interface IContributionReport {
  object: any;
  area: number;
  contributingParts: IContributionPart[];
}

export interface IComparisonReport {
  union: IContributionReport | null;
  reports: ISpatialComparisonReport[];
}

export interface IContributionPart {
  object: any;
  contribution: number;
}

export interface ISpatialComparisonReport {
  objectAID: string;
  objectBID: string;
  relations: string[];
  equality?: number;
  spatialOverlap?: string;
  spatialOverlapPercentage?: number;
}

interface IIntersectionBody {
  dataset: string;
  geojson: Polygon | MultiPolygon;
  buffer: number;
  validAt?: string;
  databaseAt?: string;
}

interface ICompareBody {
  compare: string;
  compareAgainst: string[];
}

export interface IMapWorkerRequest {
  id: number;
  response: object[];
}

export interface IMapWorkerResponse {
  id: number;
  geom: FeatureCollection<GeometryObject>;
}


@Injectable()
export class MapService {
  private worker: Worker;
  private workerStream: Observable<IMapWorkerResponse>;
  private readonly baseURL;

  constructor(private http: HttpClient) {
    //    Create the worker and register a stream for the results
    this.worker = new Worker("/projector");
    this.workerStream = fromEvent(this.worker, 'message')
      .pipe(map((m: MessageEvent) => (m.data as IMapWorkerResponse)));
    this.baseURL = environment.baseUrl;
  }

  /**
   * Before a spatio-temporal interesction for the given WKT bounding box, returning a GeoJSON Feature Collection
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  public stIntersect(dataset: string,
                     wkt: wktValue,
                     validTime: Moment,
                     dbTime?: Moment,
                     buffer: number = 0): Observable<FeatureCollection<GeometryObject>> {
    console.debug('Intersecting at:', wkt, validTime.toISOString());

    if (wkt === null || wkt === undefined) {
      return throwError('Intersection boundary cannot be empty');
    }

    const postBody: IIntersectionBody = {
      dataset,
      validAt: validTime.toISOString(),
      databaseAt: new Date().toISOString(),
      geojson: MapService.normalizeToGeoJSON(wkt),
      buffer
    };
    console.debug('Post body', postBody);
    return this.http.post(this.baseURL + '/visualize/intersect', postBody)
      .pipe(flatMap(this.parseToGeoJSONWorker))
  }

  /**
   * Performa a spatio-temporal intersection for the given WKT boundary, returning the results as a list of {TrestleIndividual}
   * @param {string} dataset to use
   * @param {wktValue} wkt boundary
   * @param {moment.Moment} validTime of intersection
   * @param {moment.Moment} dbTime of intersection
   * @param {number} buffer (in meters) around boundary
   * @returns {Observable<TrestleIndividual[]>}
   */
  public stIntersectIndividual(dataset: string,
                               wkt: wktValue,
                               validTime?: Moment,
                               dbTime?: Moment,
                               buffer: number = 0): Observable<TrestleIndividual[]> {
    const postBody: IIntersectionBody = {
      dataset,
      buffer,
      geojson: MapService.normalizeToGeoJSON(wkt)
    };

    if (validTime) {
      postBody.validAt = validTime.toISOString();
    }

    if (dbTime) {
      postBody.databaseAt = dbTime.toISOString();
    }

    console.debug('Intersecting individuals with', postBody);

    return this.http.post(this.baseURL + '/individual/intersect-individuals', postBody)
      .pipe(map(MapService.parseResponseToIndividuals));
  }

  /**
   * Perform a spatio-temporal comparison between the input object and the given set of comparison objects
   * @param {ICompareBody} request
   * @returns {Observable<IComparisonReport>}
   */
  public compareIndividuals(request: ICompareBody): Observable<IComparisonReport> {
    return this.http.post<IComparisonReport>(this.baseURL+ '/visualize/compare', request);
  }

  /**
   * Parses an input set of generic objects, by sending them to a web worker to do the interesting stuff
   * @param {object[]} objects
   * @returns {Observable<FeatureCollection<GeometryObject>>}
   */
  private parseToGeoJSONWorker = (objects: object[]): Observable<FeatureCollection<GeometryObject>> => {
    console.debug('Sending to worker');
    const id = new Date().getTime();
    const workerRequest: IMapWorkerRequest = {
      id,
      response: objects
    };

    //    Dispatch the event
    this.worker.postMessage(workerRequest);

    //    Subscribe to the event stream
    return new Observable((observer: Subscriber<FeatureCollection<GeometryObject>>) => {
      this.workerStream
        .pipe(filter(m => m.id === id))
        .subscribe((msg) => {
          console.debug('Has from worker:', msg);
          observer.next(msg.geom);
          observer.complete();
        });
    });
  };

  public static normalizeToGeoJSON(geom: wktValue): Polygon | MultiPolygon {
    if (MapService.isGeometryObject(geom)) {
      if (geom.type === 'Polygon') {
        return (geom as Polygon);
      } else if (geom.type === 'MultiPolygon') {
        return (geom as MultiPolygon);
      } else {
        console.error('Not correct geom', geom);
        throw new Error('Not correct geometry');
      }
    }
    return {
      type: 'Polygon',
      // need to return and array of bounds as an array of SW -> NW -> NE -> SE -> SW
      coordinates: [[geom.getSouthWest().toArray(),
        geom.getNorthWest().toArray(),
        geom.getNorthEast().toArray(),
        geom.getSouthEast().toArray(),
        geom.getSouthWest().toArray()]]
      // crs: {type: "name", properties: {name: "EPSG:4326"}}
    };
  }

  private static parseResponseToIndividuals(res: ITrestleIndividual[]): TrestleIndividual[] {
    console.debug('Intersected result from server:', res);
    return res
      .map((individual: ITrestleIndividual) => new TrestleIndividual(individual));
  }

  private static isGeometryObject(x: any): x is GeometryObject {
    return (x as GeometryObject).type !== undefined;
  }
}

import {Injectable} from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import {environment} from '../../../environments/environment';
import {Observable, throwError} from 'rxjs';
import {catchError, map, tap} from 'rxjs/operators';

export interface ITrestleMetricsHeader {
  upTime: number;
  startTime: number;
  meters: Map<string, string>;
}

export interface IMetricsData {
  metric: string;
  values: IMetricsValue[];
}

export interface IMetricsValue {
  timestamp: Date;
  value: number;
}

@Injectable()
export class MetricsService {

  private readonly baseUrl: string;

  constructor(private authHttp: HttpClient) {
    this.baseUrl = environment.baseUrl;
  }

  /**
   * Get initial data about available metrics
   * @returns {Observable<ITrestleMetricsHeader>}
   */
  public getMetrics(): Observable<ITrestleMetricsHeader> {
    return this.authHttp.get<ITrestleMetricsHeader>(this.baseUrl + '/metrics')
      .pipe(catchError((error: Error) => throwError(error || 'Server Error')));
  }

  /**
   * Get values for a specified metric ID that falls within the given range
   * @param {string} metricID to fetch
   * @param {number} start of temporal period for metrics data
   * @param {number} end of temporal period for metrics data
   * @returns {Observable<IMetricsData>}
   */
  public getMetricValues(metricID: string, start: number, end: number): Observable<IMetricsData> {
    console.debug('Retrieving values for metric: ' + metricID + ' from: ' + start + ' to: ' + end);
    const params = new HttpHeaders();
    params.append('start', start.toString());
    params.append('end', end.toString());
    return this.authHttp.get(this.baseUrl + '/metrics/metric/' + metricID, {
      params: {
        start: start.toString(),
        end: end.toString()
      }
    })
      .pipe(
        tap(json => console.debug('Metric values:', json)),
        map(json => {
          const metricValues: IMetricsValue[] = [];
          Object.keys(json).forEach((key) => {
            const longKey = parseInt(key, 10);
            if (longKey !== 0) {
              metricValues.push({
                timestamp: new Date(longKey),
                value: json[longKey]
              });
            }
          });
          return {
            metric: metricID,
            values: metricValues.sort((a, b) => {
              if (a.timestamp === b.timestamp) {
                return 0;
              }
              if (a.timestamp < b.timestamp) {
                return -1;
              }
              return 1;
            })
          };
        }));
  }

  /**
   * Export the given metric values as a CSV file
   * @param {string[] | null} metrics to export
   * @param {number} start of temporal period
   * @param {number} end of temporal period
   * @returns {Observable<Blob>}
   */
  public exportMetricValues(metrics: null | string[], start: number, end?: number): Observable<Blob> {
    return this.authHttp.post(this.baseUrl + '/metrics/export', {
        metrics,
        start,
        end
      },
      {
        responseType: 'blob'
      });
  }
}

