import {browser, by, element, ElementFinder, Locator} from 'protractor';
import {promise} from 'selenium-webdriver';

export class LoginPageObject {

  private header: ElementFinder;
  private usernameField: ElementFinder;
  private passwordField: ElementFinder;

  constructor() {
    this.header = element(by.css('mat-card-header'));
    this.usernameField = element(by.css('input[formcontrolname="username"]'));
    this.passwordField = element(by.css('input[formcontrolname=\'password\']'));
  }

  public async getTitle() {
    return this.header.getText();
  }

  /**
   * Fill in user login details, and optionally submit them
   * @param {string} username - Username to fill in
   * @param {string} password - Password to fill in
   * @param {boolean} login - Should we login the user?
   * @returns {Promise<void>}
   */
  public async loginUser(username: string, password: string, login?: boolean): Promise<void> {
    // Try to click the login button, if it doesn't exist, click logout
    const loginButton = element(by.css('[routerLink=\'/login\']'));
    const isPresent = await loginButton.isPresent();
    if (isPresent) {
      await loginButton.click();
    } else {
      // Logout and log in again
      await element(by.id('logout')).click();
      await browser.sleep(1000);
      await loginButton.click();
    }
    await this.usernameField.clear();
    await this.usernameField.sendKeys(username);
    await this.passwordField.clear();
    await this.passwordField.sendKeys(password);

    if (login) {
      element(by.buttonText('Submit')).click();
    }

    return browser.sleep(1000);
  }

  /**
   * Determines if the form matches the given valid state
   *
   * @param {boolean} valid - Should the form be value?
   * @returns {Promise<boolean>} - The form matches the given valid state
   */
  public async formValidState(valid: boolean): Promise<boolean> {
    const formSelector = by.css('form');
    if (valid) {
      return this.elementHasClass(formSelector, 'ng-valid');
    }
    return this.elementHasClass(formSelector, 'ng-invalid');
  }

  /**
   * Returns the text for the given element
   * @param {Locator} selector - Selector to use
   * @returns {Promise<string>} - Element text
   */
  public async getElementText(selector: Locator): promise.Promise<string> {
    return element(selector).getText();
  }

  /**
   * * Determines if the element for the given selector contains the specified class
   * @param {Locator} selector - selector to use
   * @param {string} clazz - String class name
   * @returns {Promise<boolean>} - The element contains the specified class
   */
  private async elementHasClass(selector: Locator, clazz: string): Promise<boolean> {
    return element(selector).getAttribute('class')
      .then((classes) => {
        return classes.split(' ').indexOf(clazz) !== -1;
      });
  }
}

import { IInterfacable } from "../../interfacable";
import moment from "moment";

export interface ITrestleEvent {
    individual: string;
    type: string;
    temporal: string;
}

export enum TrestleEventType {
    CREATED,
    DESTROYED,
    BECAME,
    SPLIT,
    MERGED
}

export class TrestleEvent implements IInterfacable<ITrestleEvent> {
    private individual: string;
    private type: string;
    private temporal: moment.Moment;

    constructor(event: ITrestleEvent) {
        this.individual = event.individual;
        this.type = event.type;
        this.temporal = moment(event.temporal, moment.ISO_8601);
    }

    public getIndividual() {
        return this.individual;
    }

    public getType() {
        return this.type;
    }

    public getTemporal() {
        return this.temporal;
    }

    public asInterface() {
        return {
            individual: this.individual,
            type: this.type,
            temporal: this.temporal.toISOString()
        };
    }
}

import { ITrestleTemporal, TrestleTemporal } from "./trestle-temporal";
import { IInterfacable } from "../../interfacable";
import * as moment from "moment";

export interface ITrestleFact {
    identifier: string;
    name: string;
    type: string;
    value: string;
    databaseTemporal: ITrestleTemporal;
    validTemporal: ITrestleTemporal;
}

export class TrestleFact implements IInterfacable<ITrestleFact> {
    private identifier: string;
    private name: string;
    private type: string;
    private value: string;
    private databaseTemporal: TrestleTemporal;
    private validTemporal: TrestleTemporal;

    constructor(fact: ITrestleFact) {
        this.identifier = fact.identifier;
        this.name = fact.name;
        this.type = fact.type;
        this.value = fact.value;
        this.databaseTemporal = new TrestleTemporal(fact.databaseTemporal);
        this.validTemporal = new TrestleTemporal(fact.validTemporal);
    }

    public getID(): string {
        return this.identifier;
    }

    public getName(): string {
        return this.name;
    }

    public getValue(): string {
        return this.value;
    }

    public getType(): string {
        return this.type;
    }

    public getValidTemporal(): TrestleTemporal {
        return this.validTemporal;
    }

    public getDatabaseTemporal(): TrestleTemporal {
        return this.databaseTemporal;
    }

    public isSpatial(): boolean {
        return this.name === "asWKT";
    }

    /**
     * Is this fact active at the specified valid/database intersection
     * @param {moment.Moment} validAt
     * @param {moment.Moment} dbAt
     * @returns {boolean}
     */
    public isActive(validAt: moment.Moment, dbAt?: moment.Moment): boolean {
        if (dbAt) {
            return this.getValidTemporal().isActive(validAt) &&
                this.getDatabaseTemporal().isActive(dbAt);
        } else {
            return this.getValidTemporal().isActive(validAt);
        }
    }

    public asInterface(): ITrestleFact {
        return {
            identifier: this.identifier,
            name: this.name,
            type: this.type,
            value: this.value,
            databaseTemporal: this.databaseTemporal.asInterface(),
            validTemporal: this.validTemporal.asInterface()
        };
    }
}

import { ITrestleTemporal, TrestleTemporal } from "./trestle-temporal";
import { ITrestleFact, TrestleFact } from "./trestle-fact";
import { ITrestleRelation, TrestleRelation } from "./trestle-relation";
import { ITrestleEvent, TrestleEvent } from "./trestle-event";
import { GeometryObject } from "geojson";
import { IInterfacable } from "../../interfacable";
import { parse } from "wellknown";
import SortedArray from 'sorted-array';

export interface ITrestleIndividual {
    individualID: string;
    existsTemporal: ITrestleTemporal;
    facts: ITrestleFact[];
    relations: ITrestleRelation[];
    events: ITrestleEvent[];
}

export class TrestleIndividual implements IInterfacable<ITrestleIndividual> {

    private static suffixRegex = /.*[\/#]([^\/#]*)$/g;
    private static prefixRegex = /.*[\/#]/g;
    private static hostnameRegex = /\w+:\/\/[^\/]*/g;
    private readonly id: string;
    private filteredID?: string;
    private facts: SortedArray<TrestleFact>;
    private spatialFact?: TrestleFact;
    private relations: TrestleRelation[] = [];
    private events: TrestleEvent[] = [];
    private readonly existsTemporal: TrestleTemporal;

    constructor(individual: ITrestleIndividual) {
        this.id = individual.individualID;
        this.facts = new SortedArray<TrestleFact>([], TrestleIndividual.factSort);
        this.existsTemporal = new TrestleTemporal(individual.existsTemporal);
        individual.facts.forEach((fact) => {
            const factClass = new TrestleFact(fact);
            // Set as spatial fact, if that's the case
            if (factClass.isSpatial()) {
                this.spatialFact = factClass;
            }
            this.facts.insert(factClass);
        });
        individual.relations.forEach((relation) => {
            this.relations.push(new TrestleRelation(relation));
        });
        individual.events.forEach((event) => this.events.push(new TrestleEvent(event)));
    }

    /**
     * Get Individual ID
     * @returns {string}
     */
    public getID(): string {
        return this.id;
    }

    /**
     * Get the individual ID, without the URI base or temporal range
     * @returns {string}
     */
    public getFilteredID(): string {
        if (this.filteredID) {
            return this.filteredID;
        }
        this.filteredID = TrestleIndividual.filterID(this.id);
        return this.filteredID;
    }

    /**
     * Gets the URI base of the individual ID
     * @returns {string}
     */
    public getHostname(): string {
        return TrestleIndividual.extractHostname(this.id);
    }

    /**
     * Get the individual ID, without the URI base
     * @returns {string}
     */
    public withoutHostname(): string {
        return TrestleIndividual.withoutHostname(this.id);
    }

    /**
     * Return the filtered individual ID as a hashed numeric value
     * Currently using the SBDM algorithm
     * @returns {number}
     */
    public getIDAsInteger(): number {
        return TrestleIndividual.hashID(this.getFilteredID());
    }

    /**
     * Get the Existence temporal for the individual
     * @returns {TrestleTemporal}
     */
    public getTemporal(): TrestleTemporal {
        return this.existsTemporal;
    }

    /**
     * Get the spatial fact for the individual, parsed as a {GeometryObject}
     * @returns {GeometryObject}
     */
    public getSpatialValue(): GeometryObject {

        if (this.spatialFact) {
            const geojson = parse(this.spatialFact.getValue());
            if (geojson !== null) {
                return geojson;
            }
            console.error("Failed to parse:", this.spatialFact.getValue());
        }
        throw new Error("Individual " + this.getID() + " is not spatial and should be");
    }

    /**
     * Get the sptial fact for the indivdual, as a WKT string
     * @returns {string}
     */
    public getSpatialValueAsWKT(): string {
        if (this.spatialFact) {
            return this.spatialFact.getValue();
        }
        throw new Error("Individual " + this.getID() + " is not spatial and should be");
    }

    /**
     * Get an {Iterable} of {TrestleFact} of all facts for the individual
     * @returns {TrestleFact[]}
     */
    public getFacts(): TrestleFact[] {
        return this.facts.array;
    }

    /**
     * Returns a collection of fact names and associated values
     * @returns {{[p: string]: any}}
     */
    public getFactValues(): { [name: string]: any } {
        const values: { [name: string]: any } = {};
        this.facts.array.forEach((value) => {
            values[value.getName()] = value.getValue();
        });
        return values;
    }

    /**
     * Get all Individual relations
     * @returns {TrestleRelation[]}
     */
    public getRelations(): TrestleRelation[] {
        return this.relations;
    }

    /**
     * Get all individual events
     * @returns {TrestleEvent[]}
     */
    public getEvents(): TrestleEvent[] {
        return this.events;
    }

    /**
     * Gets the start event for the given selection.
     * Returns a MERGED event, if one exists
     * Otherwise, returns CREATED
     * @returns {TrestleEvent}
     */
    public getStartEvent(): TrestleEvent {
        const mergedEvent = this.getEvents()
            .filter((event) => event.getType() === "MERGED");

        if (mergedEvent.length > 0) {
            return mergedEvent[0];
        }
        const createdEvent = this.getEvents()
            .filter((event) => event.getType() === "CREATED");
        if (createdEvent.length > 0) {
            return createdEvent[0];
        }
        throw new Error("Individual: " + this.getID() + " does not have a CREATED/MERGED event");
    }

    /**
     * Returns the end event, if one exists
     * If a SPLIT event exists, returns that
     * Otherwise, returns DESTROYED
     * Returns
     * @returns {TrestleEvent}
     */
    public getEndEvent(): TrestleEvent | null {
        const splitEvent = this.getEvents()
            .filter((event) => event.getType() === "SPLIT");
        if (splitEvent.length > 0) {
            return splitEvent[0];
        }

        const destroyedEvent = this.getEvents()
            .filter((event) => event.getType() === "DESTROYED");
        if (destroyedEvent.length > 0) {
            return destroyedEvent[0];
        }

        return null;
    }

    /**
     * Is this individual a part of a spatial union?
     * @returns {boolean}
     */
    public isUnion(): boolean {
        return this.relations.some((relation) => relation.isUnionType());
    }

    /**
     * Transform the individual back into its Interface type
     * @returns {ITrestleIndividual}
     */
    public asInterface(): ITrestleIndividual {
        const returnValue: ITrestleIndividual = {
            individualID: this.id,
            existsTemporal: this.existsTemporal.asInterface(),
            facts: [],
            relations: [],
            events: []
        };
        this.facts.array.forEach((value) => {
            returnValue.facts.push(value.asInterface());
        });
        this.relations.forEach((value) => {
            returnValue.relations.push(value.asInterface());
        });
        this.events.forEach((event) => returnValue.events.push(event.asInterface()));
        return returnValue;
    }

    /**
     * Remove dates from ID
     * @param {string} id
     * @returns {string}
     */
    public static filterID(id: string): string {
        const suffix = TrestleIndividual.extractSuffix(id);
        const idStrings = suffix.split(":");
        // return idStrings[0] + ":" + idStrings[1];
        return idStrings[0];
    }

    /**
     * Filter ID string to remove hostname (authority)
     * @param {string} id
     * @returns {string}
     */
    public static withoutHostname(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.hostnameRegex.lastIndex = 0;
        return id.replace(TrestleIndividual.hostnameRegex, "");
    }

    /**
     * Get the URI hostname
     * Returns an empty string if nothing matches
     * @param {string} id
     * @returns {string}
     */
    public static extractHostname(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.hostnameRegex.lastIndex = 0;
        const matches = id.match(TrestleIndividual.hostnameRegex);
        if (matches !== null) {
            return matches[0];
        }
        return "";
    }


    /**
     * Extracts the suffix from the individual, or returns an empty string
     * @param {string} id
     * @returns {string}
     */
    public static extractSuffix(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.suffixRegex.lastIndex = 0;
        const matches = TrestleIndividual.suffixRegex.exec(id);
        if (matches !== null) {
            return matches[1];
        }
        return "";
    }

    /**
     * Extracts the prefix from the individual, or returns an empty string
     * @param {string} id
     * @returns {string}
     */
    public static extractPrefix(id: string): string {
        // Manually reset regex match, because Javascript
        TrestleIndividual.prefixRegex.lastIndex = 0;
        const matches = id.match(TrestleIndividual.prefixRegex);
        if (matches !== null) {
            return matches[0];
        }
        return "";
    }

    /**
     * SDBM algorithm for generating a numeric value of a provided string
     * @param {string} id
     * @returns {number}
     */
    public static hashID(id: string): number {
        let hash = 0;
        for (let i = 0; i < id.length; i++) {
            const char = id.charCodeAt(i);
            // eslint-disable-next-line no-bitwise
            hash = char + (hash << 6) + (hash << 16) - hash;
        }
        return hash;
    }

    /**
     * Sorter for the Fact array which sorts by ID -> validFrom -> dbFrom
     *
     * @param {TrestleFact} a
     * @param {TrestleFact} b
     * @returns {number}
     */
    private static factSort(a: TrestleFact, b: TrestleFact): number {
        const idCompare = a.getID().localeCompare(b.getID());
        // If they're not equal, return
        if (idCompare !== 0) {
            return idCompare;
        }
        //    Next, compare on valid from
        const vEqual = a.getValidTemporal().getFrom().isSame(b.getValidTemporal().getFrom());
        // If they're not equal, is A before?
        if (!vEqual) {
            return a.getValidTemporal().getFrom().isBefore(b.getValidTemporal().getFrom()) ? -1 : 1;
        }
        //    Finally, db time
        const dEqual = a.getDatabaseTemporal().getFrom().isSame(b.getDatabaseTemporal().getFrom());
        if (!dEqual) {
            return a.getDatabaseTemporal().getFrom().isBefore(b.getDatabaseTemporal().getFrom()) ? -1 : 1;
        }
        return 0;
    }
}

import { IInterfacable } from "../../interfacable";

export interface ITrestleRelation {
    subject: string;
    object: string;
    relation: string;
}

export type TrestleRelationType =
    "CONTAINS"
    | "COVERS"
    | "DISJOINT"
    | "EQUALS"
    | "INSIDE"
    | "MEETS"
    | "SPATIAL_OVERLAPS"
    | "AFTER"
    | "BEFORE"
    | "BEGINS"
    | "DURING"
    | "ENDS"
    | "TEMPORAL_OVERLAPS"
    | "SPLIT_INTO"
    | "SPLIT_FROM"
    | "MERGED_INTO"
    | "MERGED_FROM"
    | "COMPONENT_WITH";

// export enum TrestleRelationType {
//     // Spatial
//     CONTAINS,
//     COVERS,
//     DISJOINT,
//     EQUALS,
//     INSIDE,
//     MEETS,
//     SPATIAL_OVERLAPS,
//     // Temporal
//     AFTER,
//     BEFORE,
//     BEGINS,
//     DURING,
//     ENDS,
//     TEMPORAL_OVERLAPS,
//     SPLIT_INTO,
//     SPLIT_FROM,
//     MERGED_INTO,
//     MERGED_FROM
// }

export class TrestleRelation implements IInterfacable<ITrestleRelation> {
    private subject: string;
    private object: string;
    private type: TrestleRelationType;

    constructor(relation: ITrestleRelation) {
        this.subject = relation.subject;
        this.object = relation.object;
        this.type = (relation.relation as TrestleRelationType);
    }

    public getSubject(): string {
        return this.subject;
    }

    public getObject(): string {
        return this.object;
    }

    public getType(): TrestleRelationType {
        return this.type;
    }

    /**
     * Is this relation part of a SpatialUnion?
     * @returns {boolean}
     */
    public isUnionType(): boolean {
        return (this.type === "SPLIT_INTO") ||
            (this.type === "SPLIT_FROM") ||
            (this.type === "MERGED_INTO") ||
            (this.type === "MERGED_FROM") ||
            (this.type === "COMPONENT_WITH");
    }

    public asInterface(): ITrestleRelation {
        return {
            subject: this.getSubject(),
            object: this.getObject(),
            relation: this.getType()
        };
    }
}

import { IInterfacable } from "../../interfacable";
import moment from "moment";

export interface ITrestleTemporal {
    ID: string;
    From: Date;
    To?: Date | string;
}

export class TrestleTemporal implements IInterfacable<ITrestleTemporal> {
    private id: string;
    private from: moment.Moment;
    private to?: moment.Moment;

    constructor(temporal: ITrestleTemporal) {
        this.id = temporal.ID;
        this.from = moment(temporal.From, moment.ISO_8601);
        // There's a problem with our serialization, which means these things might end up as strings, which is no good.
        if (temporal.To !== null && temporal.To !== "") {
            this.to = moment.utc(temporal.To, moment.ISO_8601);
        }
    }

    public getID(): string {
        return this.id;
    }

    public getFrom(): moment.Moment {
        return this.from;
    }

    public getFromAsDate(): Date {
        return this.from.toDate();
    }

    public getTo(): moment.Moment | undefined {
        return this.to;
    }

    public getToAsDate(): Date | undefined {
        if (this.to) {
            return this.to.toDate();
        }
        return undefined;
    }

    public isContinuing(): boolean {
        return this.to === undefined || !this.to.isValid();
    }

    /**
     * Does the specified temporal fall within the interval of this TemporalObject?
     * @param {moment.Moment} temporal
     * @returns {boolean}
     */
    public isActive(temporal: moment.Moment): boolean {
        if (this.to === undefined) {
            return this.from.isSameOrBefore(temporal);
        } else {
            return this.from.isSameOrBefore(temporal) &&
                this.to.isAfter(temporal);
        }
    }

    public asInterface(): ITrestleTemporal {
        const returnValue: ITrestleTemporal = {
            ID: this.id,
            From: this.from.toDate()
        };
        if (!this.isContinuing() && (this.to !== undefined)) {
            returnValue.To = this.to.toDate();
        }
        return returnValue;
    }
}

import {Injectable} from '@angular/core';
import {Router} from '@angular/router';
import {Privileges, TrestleUser} from './trestle-user';
import {HttpClient} from '@angular/common/http';
import {JwtHelperService} from '@auth0/angular-jwt';
import {environment} from '../../environments/environment';
import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

const _key: string = 'access_token';

export class TrestleToken {

  private readonly exp: number;
  private readonly iat: number;
  private readonly user: ITrestleUser;

  public constructor(token: any) {
    this.exp = token['exp'];
    this.iat = token['iat'];
    this.user = JSON.parse(token['data4j']);
  }

  /**
   * Get token expiration time (in ms from Unix epoch)
   * @returns {number}
   */
  public getExpiration() {
    return this.exp;
  }

  /**
   * Get issuance time (in ms from Unix epoch)
   * @returns {number}
   */
  public getIssue() {
    return this.iat;
  }

  /**
   * Get the user from the token
   * @returns {ITrestleUser}
   */
  public getUser() {
    return this.user;
  }
}

export interface ITrestleUser {
  id?: number;
  firstName: string;
  lastName: string;
  username: string;
  email: string;
  password: string;
  privileges: number;
}

@Injectable()
export class AuthService {

  private jwtHelper: JwtHelperService;
  private readonly baseUrl;

  constructor(private router: Router, private http: HttpClient) {
    this.jwtHelper = new JwtHelperService();
    this.baseUrl = environment.baseUrl;
  }

  /**
   * Attempt to login the given use
   * @param {string} username
   * @param {string} password
   */
  public login(username: string, password: string): Observable<{ user: TrestleUser, token: string }> {
    return this.http.post(this.baseUrl + '/auth/login', {username, password: password}, {
      responseType: 'text'
    })
      .pipe(map(token => {
        const t = this.getToken2(token);
        return {user: new TrestleUser(t.getUser()), token}
      }));
  }

  /**
   * Logout the user
   */
  public logout(): Observable<string> {
    console.debug("Logging out");
    return this.http.post(this.baseUrl + '/auth/logout', null, {
      responseType: 'text'
    });
  }

  /**
   * Is the user currently logged in?
   * @returns {boolean}
   */
  public loggedIn(): boolean {
    const token = localStorage.getItem(_key);
    if (token) {
      console.debug(
        this.jwtHelper.decodeToken(token),
        this.jwtHelper.getTokenExpirationDate(token),
        this.jwtHelper.isTokenExpired(token)
      );
    }
    return !this.jwtHelper.isTokenExpired(token);
  }

  /**
   * Does the user have AT LEAST Admin permissions?
   * @returns {boolean}
   */
  public isAdmin(): boolean {
    if (this.loggedIn()) {
      const token = this.getToken();
      // eslint-disable-next-line no-bitwise
      return (token !== null) && ((token.getUser().privileges & Privileges.ADMIN) > 0);
    }
    return false;
  }

  /**
   * Get the user, if one exists
   * @returns {TrestleUser | null}
   */
  public getUser(): TrestleUser | null {
    if (this.loggedIn()) {
      const token = this.getToken();
      if (token) {
        return new TrestleUser(token.getUser());
      }
    }
    console.error('User is not logged in');
    return null;
  }

  /**
   * Determines if the logged-in user has the necessary roles to perform a certain function
   * @param roles - Array of required Privileges
   * @returns {boolean} - has all the required roles
   */
  public hasRequiredRoles(roles: Privileges[]): boolean {
    const user = this.getUser();
    if (user == null) {
      return false;
    }

    return user.hasRequiredPrivileges(roles);
  }

  public getEncodedToken(): string | null {
    return localStorage.getItem(_key);
  }

  private getToken(): TrestleToken | null {
    const jwtToken = this.getEncodedToken();
    if (jwtToken) {
      return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
    }
    return null;
  }

  private getToken2 = (jwtToken: string): TrestleToken => {
    return new TrestleToken(this.jwtHelper.decodeToken(jwtToken));
  };
}

import {ITrestleUser} from './authentication.service';

export enum Privileges {
  USER = 1,
  ADMIN = 2,
  DBA = 4
}

export class TrestleUser {
    private readonly _id: number | undefined;
    private readonly _firstName: string;
    private readonly _lastName: string;
    private _username: string;
    private readonly _email: string;
    private readonly _password: string;
    private readonly _privileges: number;

    public constructor(userInterface: ITrestleUser) {
        this._id = userInterface.id;
        this._email = userInterface.email;
        this._firstName = userInterface.firstName;
        this._lastName = userInterface.lastName;
        this._email = userInterface.email;
        this._password = userInterface.password;
        this._privileges = userInterface.privileges;
    }


    get id(): number | undefined {
        return this._id;
    }

    get firstName(): string {
        return this._firstName;
    }

    get lastName(): string {
        return this._lastName;
    }

    get username(): string {
        return this._username;
    }

    get email(): string {
        return this._email;
    }

    get password(): string {
        return this._password;
    }

    get privileges(): number {
        return this._privileges;
    }

    /**
     * Does the user have admin privileges?
     * This will return true for any permission level equal to or higher than admin (e.g. dba)
     * @returns {boolean} - Has admin or higher permissions?
     */
    public isAdmin(): boolean {
        // eslint-disable-next-line no-bitwise
        return (this._privileges & Privileges.ADMIN) > 0;
    }

    public hasRequiredPrivileges(roles: Privileges[]): boolean {
        // eslint-disable-next-line no-bitwise
        return (this._privileges & TrestleUser.buildRoleValue(roles)) > 0;
    }

    public serialize(): ITrestleUser {
      return {
        email: this._email,
        firstName: this._firstName,
        id: this._id,
        lastName: this._lastName,
        password: this._password,
        privileges: this._privileges,
        username: this._username
      }
    }

    /**
     * Convert an array of privileges into a single value
     * @param {Privileges[]} roles
     * @returns {number}
     */
    private static buildRoleValue(roles: Privileges[]): number {
        let roleValue = 0;
        roles.forEach((role) => {
            // eslint-disable-next-line no-bitwise
            roleValue = roleValue | role;
        });
        return roleValue;
    }
}

import { browser, by, element, ElementFinder } from "protractor";

export interface IUserTable {
    username?: string,
    password?: string,
    first_name?: string,
    last_name?: string,
    email?: string;
}

export type UserType = "user" | "admin" | "dba";

export class UserDetailsModal {

    private userNameInput: ElementFinder;
    private passwordInput: ElementFinder;
    private firstNameInput: ElementFinder;
    private lastNameInput: ElementFinder;
    private emailInput: ElementFinder;

    public constructor() {
        this.userNameInput = element(by.css("input[formcontrolname='username']"));
        this.passwordInput = element(by.css("input[formcontrolname='password']"));
        this.firstNameInput = element(by.css("input[formcontrolname='firstName']"));
        this.lastNameInput = element(by.css("input[formcontrolname='lastName']"));
        this.emailInput = element(by.css("input[formcontrolname='email']"));
    }

    /**
     * Create a new user of the given type with the provided data
     * Assumes the modal is already open, but closes it in order to submit the data
     *
     * @param {UserType} userType - user type to add
     * @param {IUserTable} user - User data
     * @returns {Promise<any>} - Returns when the modal closes (waits 500ms)
     */
    public async createUser(userType: UserType, user: IUserTable) {
        // Wait for the modal to load
        await browser.sleep(500);
        await this.userNameInput.sendKeys(user.username || "");
        await this.passwordInput.sendKeys(user.password || "");
        await this.firstNameInput.sendKeys(user.first_name || "");
        await this.lastNameInput.sendKeys(user.last_name || "");
        await this.emailInput.sendKeys(user.email || "");

        //    Now the permissions, select everything lower
        switch (userType) {
            case "dba": {
                await UserDetailsModal.selectUserPermission("dba");
                await UserDetailsModal.selectUserPermission("admin");
                break;
            }
            case "admin": {
                await UserDetailsModal.selectUserPermission("admin");
                break;
            }
            case "user": {
                break;
            }
        }
        return Promise.resolve(undefined);
    }

    /**
     * Update the given properties of the user
     * Assumes the modal is already open, but closes it in order to submit the changes
     *
     * @param {IUserTable} user - User data to update
     * @returns {Promise<any>} - returns when the modal is closed (waits 500ms)
     */
    public async editUser(user: IUserTable) {
        // Wait for the modal to load
        await browser.sleep(500);
        // We have to clear the input fields before
        if (user.username) {
            await this.userNameInput.clear();
            await this.userNameInput.sendKeys(user.username);
        }

        if (user.email) {
            await this.emailInput.clear();
            await this.emailInput.sendKeys(user.email);
        }

        if (user.password) {
            await this.passwordInput.clear();
            await this.passwordInput.sendKeys(user.password);
        }

        if (user.first_name) {
            await this.firstNameInput.clear();
            await this.firstNameInput.sendKeys(user.first_name);
        }

        if (user.last_name) {
            await this.lastNameInput.clear();
            await this.lastNameInput.sendKeys(user.last_name);
        }

        element(by.buttonText("Update User")).click();
        // Sleep for 500 ms to let the modal close
        return browser.sleep(500);
    }

    /**
     * Delete the given user
     * Assumes the modal is already open, but closes it in order to submit the changes
     *
     * @returns {any} - Returns when the modal closes
     */
    public async deleteUser() {
        // Wait for the modal to load
        await browser.sleep(500);
        return element(by.buttonText("Delete User")).click();
    }

    public async submit() {
        await element(by.buttonText("Add User")).click();
        // Sleep for 500 ms to let the modal close
        return browser.sleep(500);
    }

    public async dismiss() {
        await element(by.buttonText("Cancel")).click();
        return browser.sleep(500);
    }

    /**
     * Selects the mat-button-toggle that corresponds to the given permission.
     * Automatically uppercases the input
     *
     * @param {string} permission
     * @returns {Promise<any>}
     */
    private static async selectUserPermission(permission: string) {
        // We need to use contains, because the Text can have trailing whitespace
        const xpathString = "//mat-button-toggle[.//div[contains(text(), '" + permission.toUpperCase() + "')]]";
        return element(by.xpath(xpathString)).click();
    }
}

import {browser, by, element} from 'protractor';
import {IUserTable, UserDetailsModal, UserType} from './user.details.modal';

export class UsersPage {

  private userModal = new UserDetailsModal();

  public async fillUserForm(userType: UserType, user: IUserTable) {
    //    Click the button
    await element(by.id('add-user')).click();
    return this.userModal.createUser(userType, user);
  }

  public async editUser(username: string, userData: IUserTable) {
    await UsersPage.selectUserRow(username);
    return this.userModal.editUser(userData);
  }

  public async countUsers(): Promise<number> {
    await browser.sleep(1000);
    return element(by.id('users-table'))
      .all(by.css('tbody tr')).count();
  }

  public async deleteUser(username: string) {
    await UsersPage.selectUserRow(username);
    return this.userModal.deleteUser();
  }

  public async submitModal() {
    return this.userModal.submit();
  }

  public dismissModal() {
    return this.userModal.dismiss();
  }

  public async getFieldMessage(field: string) {
    await browser.sleep(500);
    const xpathString = '//form//mat-form-field[.//input[@formcontrolname=\'' + field + '\']]//mat-error';
    const matField = element(by.xpath(xpathString));
    return matField.getText();
  }

  private static async selectUserRow(username: string) {
    const xpathString = '//table[@id=\'users-table\']/tbody/tr[td//text()[contains(.,\''
      + username
      + '\')]]';
    const rowElement = element(by.xpath(xpathString));
    // We need to use the JS click method, otherwise Firefox complains it can't scroll to the table row
    await browser.executeScript('arguments[0].click()', rowElement);
    // Wait 500ms for the modal to open
    return browser.sleep(500);
  }
}

